style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("lifehacker.com") {

#header_container, #sidebar, #sitemeter, #ad-728, #ask_ads_part_1, #ask_ads_part_2 {
  display: none !important;
  }

#logobar {
  margin-top: 12px !important;
  }

#content_container {
  background: transparent !important;
  }

#ghostTags span {
  visibility: visible !important;
  }

#content, #main {
  width: 999px !important;
  }

.post .excerpt, .post .entry, element.style, h2.tagged-top, #comment_list, #ghostTags {
  width: 779px !important;
  }

.post .metadata, .quicklink .metadata, #postMeta {
  width: 200px !important;
  }

.page_navigation {
  float: right !important;
  }

#footer {
  margin-left: 0 !important;
  }

#interruptor {
  border-bottom: 2px solid #CCC !important;
  width: 779px !important;
  margin-top: 10px !important;
  }

}
