style.css:
@-moz-document domain("se7ensins.com") {
img[src*="logo_bg"]{
    width:0;
    height:100px;
    padding-right:375px;
    background:transparent url(http://i.imgur.com/qkIDtp4.png) top left no-repeat;
}
#userBox {
    background: rgba(26,26,26,0.7);
}
#logoBlock { background: url("http://i.imgur.com/otAm8l6.png") repeat scroll 0% 0% rgb(26, 26, 26) !important;}
.sidebar .section .secondaryContent h3 {
    font-weight: bold;
    background: url("http://i.imgur.com/otAm8l6.png") repeat-x scroll left top rgb(37, 37, 37);
}
.sidebar .section .secondaryContent h3, .sidebar .section .secondaryContent h3 a{color: white;
}
.navTabs .navTab.selected .tabLinks a {
font-size: 11px;
color: #D5D5D5;
padding: 1px 10px;
display: block;
line-height: 34px;
}
html {
overflow-y: scroll!important;
background-image: url(http://i.imgur.com/JPrP3Yw.jpg);
background-repeat: no-repeat;
background-position: top left;
background-attachment: fixed;
}
#copyright {
color: #FFFFFF;
float: left;
}
}
