style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://feedly.com), url-prefix(https://feedly.com) {
    body,
    span,
    .title,
    .entryBody,
    .entryBody h2,
    .entryBody h3,
    #feedlyTabs .label {
       font-family: "iranian sans", ubuntu !important;
    }
    
    .entryBody div {
       text-align: justify !important;
    }

    .condensed .entryholder .u100entry {
       margin-right: 167px;
       margin-left: auto;
    }
}
