style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {
    #blueBar {
        /* I like a green top bar */
        background-image: none !important;
        background-color: #474 !important;
    }
    div#rightCol {
        display: none !important;
    }
    div#globalContainer {
        width: auto !important;
    }
    .navHeader a, .sortLink {
        /* This is the text colour of the big bold headers (Groups, Apps, Sort, etc) */
        color: #666 !important;
    }
    ._4-u2 {
        /* This is the content (posts) background colour */
        background-color: #eee !important;
    }
    ._5vb_, ._5vb_ #contentCol {
        /* This is the general background colour */
        background-color: #ccc !important;
    }
    div#content {
        float: right !important;
    }
    div#contentArea {
        width: 600px  !important;
    }
    #contentCol {
        padding-right: 50px !important;
    }
   .hasSmurfbar #headNav #pageNav, .slim #blueBar {
        margin: 0 !important;
        height: auto !important;
    }    
    ._5bfg #headNav #navSearch {
        padding-top: 0 !important;
        height: auto !important;
    }
    .hasSmurfbar #blueBarHolder, .hasSmurfbar #blueBarHolder.slim, .hasSmurfbar #blueBarHolder #pageHead, .hasSmurfbar #blueBarHolder #headNav, .hasSmurfbar #blueBarHolder #blueBar {
       height: 25px !important;
    }
    #pageLogo {
        margin-top: 0 !important;
    }
    html #pageLogo._5bfh a {
        height: 24px !important;
        background-position: -38px -106px !important;
        width: 32px !important;
    }
}
