style.css:
@-moz-document domain("raw.senmanga.com"), domain("www.senmanga.com") {
/* Author @Demo_d */

/* 屏蔽头部联系我们 */
#header #site-stat {display: none !important;}
.container #row iframe{display: none !important;}
#header #menu {position: relative;}
/* 屏蔽头部登陆 */
.user-button{display: none !important;}
/* 屏蔽头部斜幅 */
#histatsC {display: none !important;}

/* 屏蔽首页订阅到邮箱及分享 */
#contentleft .post .addthis_toolbox.addthis_default_style, #contentleft .post #rss, #contentleft .post #rss #_atssh,   #contentleft .post a[href="javascript:login('show');"] { display: none !important;}
#contentleft .post, #l_sidebar-2, #r_sidebar {border:none !important; }
section#content > #post:nth-of-type(1),section#content > #post:nth-of-type(2){ display: none !important;}
#content #contentleft #login-box + .post +.post, .fb-like-box.fb_iframe_widget { display: none !important;}
#login-box { display: none !important; }
#rss { display: none !important; }

/* 屏蔽右边栏 Follow Us */
#contentleft + #Box350 > h2:first-of-type, #Box350 iframe,#Box350 .fb-like-box.fb_iframe_widget { display: none !important; }
#social { display: none !important; }
/* 屏蔽右侧分享到 */
#share_addthis {display: none !important;}
/* 调整底部面包屑宽度 */
#omv tr + tr +tr +tr div { width: auto !important; }  
/*{display: none !important;} 
/* 屏蔽页脚 */
#footerbg, .footer-1, .footer-2, .footer-3 {display: none !important;}
/* 屏蔽页脚 Tip */
.note {display: none !important;}
/* 屏蔽漫画页页脚 */
.clear + #content #Box350, .clear + #content #contentleft {display: none !important;}
/* 隐藏漫画页页脚 Tag */
body { color: #fff; } body > div, body table { color: #000 !important; }
[id~="ad"],iframe{display:none !important;}
/* 针对小屏幕 */
@media screen and (max-width: 980px){
/* 隐藏 logo */  
#header .header-left a > img[src="/Manga/logo2.png"]{ display: none !important; }
#header .logo, #header .top-set a { display: none !important; }
#header .top-set div.right { width: 320px; float: left; }
#header { width:  auto !important; }  
#header .top-set { width: auto !important; }
/* 调整菜单宽度和高度，调整菜单背景图 */  
#header .header-right {width: 100%;}
#header .nav-set, #header .letter-set { width: 95% !important; }
#header .letter-set { height: 60px !important; }
#header { margin: 10px !important;}  
#header .nav-set {
 background: url("http://raw.senmanga.com/Manga/nav-bg.gif") -240px 0 no-repeat rgba(0, 0, 0, 0) !important; }
#header .nav-set .menu { padding-left: 0 !important;}
.navbar-collapse{padding:0;}
/* 调整右边栏位置 */
#content #Box350 {display: none !important;}
aside#sidebar,.row-fluid .col-sm-4.col-md-4{ float: none !important; width: auto; }
/* 调整左边栏位置 */
section#content,.row-fluid .col-sm-8.col-md-8, #contentleft{ float: none !important; width: auto;}
#content #contentleft, #content #Box350 { width: 92% !important;float:none !important; margin: 10px !important; }
#header + #content { width: auto !important;margin: 60px auto 0 !important; } 
.row #content{width: 100% !important;}
#contentleft .post, #contentleft #comments {width: auto !important; float: none;}
#header #menu {
position: relative;
}
.navbar-form .form-group{float:left; margin-right:5px;}
#adv-search {display:none;}
}
}

@-moz-document regexp("http://raw.senmanga.com/((?!'Manga'|updates|Privacy).*)*") {
/* 对 首页、Manga、updates、Privacy 页不生效.漫画页生效  */
/* 针对小屏幕 */
[id~="ad"],iframe{display:none !important;}
@media screen and (max-width: 980px){
/* 屏蔽页头 */
#header {display: none !important;}
/* 屏蔽顶部面包屑 */
#omv .location:first-of-type { display: none !important;}
#omv .ads > div:first-of-type { width:0 !important; }
#omv .ads div { width: 97% !important; }
#omv .ads { padding: 0 !important;}
#omv .location + .ads { height:0 !important;}  
#omv .ads .location .inner{width: auto !important;}
/* 屏蔽顶部章节选择 */
#omv .clear + .pager { display: none !important;}
/* 调整底部章节选择到适合的宽度 */
#omv .pager span select { width: 20% !important;}
#omv .pager span span select { width: 5% !important;}
/* 调整漫画主体到适合的宽度 */
#omv tbody tr > td > a > img { width: 95% !important;  /* height: 75%; */}
.clear + #content { margin-top: 0 !important;}  
}
}

@-moz-document regexp("http://www.senmanga.com/((?!releases|directory).*)*") {
/* 对 首页、releases、directory 页不生效.漫画页生效  */
/* 调整顶部高度 */
body[data-twttr-rendered="true"] #content .location + .clear > div:first-of-type {height: 0px !important;;padding-bottom:10px !important;}
/* 隐藏漫画页页脚 Tag */
body[data-twttr-rendered="true"] .wpm_pag {margin: 20px auto 0;}
body[data-twttr-rendered="true"] .clear > .wpm_pag,.wpm_pag > div > a { color: #fff; }
body[data-twttr-rendered="true"] .wpm_pag div[style="float:left;"], body[data-twttr-rendered="true"] .wpm_pag div[style="clear: right;"]{display: none !important;}
body[data-twttr-rendered="true"] .wpm_pag > div:nth-of-type(4){display: none !important;}
[id~="ad"],iframe{display:none !important;}
/* 针对小屏幕 */
@media screen and (max-width: 980px){
/* 屏蔽页头 */
body[data-twttr-rendered="true"] #header {display: none !important;}
body[data-twttr-rendered="true"] #content {margin-top: 20px !important;}
/* 屏蔽顶部面包屑 */
body[data-twttr-rendered="true"] #content .location + .clear > div:first-of-type {display: none !important;}
/* 屏蔽漫画页大标题 */
.wpm_pag h1 {display: none !important;}
/* 屏蔽顶部章节选择 */
.wpm_pag .manga-navi:first-of-type {display: none !important;}
/* 调整底部章节选择到适合的宽度 */
.wpm_pag .pager span select { width: 20% !important;}
/* 调整漫画主体到适合的宽度 */
.wpm_pag tbody tr > td > a > img { width: 95% !important;/* height: 75%; */}
}
}
