style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

menu,
menuitem,
menupopup,
splitmenu {
    background-color: #fff !important;
    -moz-appearance: none !important;
    font: 14px arial !important;
}

menu, 
menuitem, 
splitmenu {
    height: 25px !important;
}

/*For Firefox 32's combined (back, forward, reload, bookmark) menuitems*/
#contentAreaContextMenu > menugroup {
    height: 36px !important;
}

#context-sep-navigation, #page-menu-separator {
    display: none !important;
}

/* Small Borders */
menupopup {
    border: 1px solid #AAA;
}
menuseparator {
    -moz-appearance: none !important;
    height: 1px !important;
    margin: 0px !important;
    border: none !important;
    border-top: 1px solid #DDD !important;
    padding: 0px !important;
}

menu[_moz-menuactive="true"],
menuitem[_moz-menuactive="true"],
menupopup[_moz-menuactive="true"],
splitmenu[_moz-menuactive="true"] {
    background-color: #4281F4 !important;
    color: #FFF !important;
    -moz-appearance: none !important;
}

/* Make text visible for checkbox menuitems until Bug 605985 is resolved*/
menuitem[type="checkbox"] {
    color: #000 !important;
}




/*Classic Firefox menu*/
#appmenuSecondaryPane {
    background-color: #FAFAFA !important;
}
#appmenuSecondaryPane>menu,
#appmenuSecondaryPane>hbox>*,
#appmenuSecondaryPane>menuitem,
#appmenuSecondaryPane>menupopup,
#appmenuSecondaryPane>splitmenu {
    background-color: #FAFAFA !important;
    -moz-appearance: none !important;
    font: 14px arial !important;
}
#appmenuSecondaryPane>menu[_moz-menuactive="true"],
#appmenuSecondaryPane>hbox>*[_moz-menuactive="true"],
#appmenuSecondaryPane>menuitem[_moz-menuactive="true"],
#appmenuSecondaryPane>menupopup[_moz-menuactive="true"],
#appmenuSecondaryPane>splitmenu[_moz-menuactive="true"] {
    background-color: #4281F4 !important;
    color: #FFF !important;
}
