style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("greasyfork.org") {
  .discussion-question {
    list-style-image: url(data:image/png;base64,);
  }

  .discussion-ok {
    list-style-image: url(data:image/png;base64,);
  }

  .discussion-good {
    list-style-image: url(data:image/gif;base64,);
  }

  .discussion-bad {
    list-style-image: url(data:image/gif;base64,);
  }

  .Vanilla img[src^="/images/circle"] {
    width: 0 !important;    /*  We're using important flag here to */
    height: 0 !important;   /*  override inline style in img element */
    background-size: cover;
  }

  .Vanilla img[src="/images/circle-green.png"],
  .Vanilla img[src="/images/circle-red.png"] {
    padding: 5.5px 6px;
  }

  .Vanilla img[src="/images/circle-yellow.png"],
  .Vanilla img[src="/images/circle-blue.png"] {
    padding: 8px;
  }

  .Vanilla img[src="/images/circle-blue.png"] {
    background-image: url(data:image/png;base64,);
  }

  .Vanilla img[src="/images/circle-yellow.png"] {
    background-image: url(data:image/png;base64,);
  }

  .Vanilla img[src="/images/circle-green.png"] {
    background-image: url(data:image/gif;base64,);
  }

  .Vanilla img[src="/images/circle-red.png"] {
    background-image: url(data:image/gif;base64,);
  }
}
