style.css:
@-moz-document domain(rebelion.org) {
#CuerpoNoticia { padding-left: 10px; padding-right: 10px; text-align: left; width: 625px; margin: 30px auto; font-family: Arial; }
#TextoNoticia{ font-size: 16px !important; line-height: 1.5; color: #4E4E4E; font-family: Georgia; }
.pretitulo { font-size: 14px; }
.titulo { font-size: 30px; }
.cabecera { color: #111; padding: 10px; }
.cabecera table { background: none !important; }
.ruta_blanco { color: #ccc; }
.ruta_blanco a:link, .ruta_blanco a:visited { border-bottom: 1px solid #DDD; color: #F15123; }
.cabecera:before { content: "Rebelion.org"; font-size: 14px; text-align: left; font-family: Arial; text-transform: uppercase; float: left; font-weight: bold; }
.cabecera table:first-child { display: none; }
}
