style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("spreeder.com")
{
    
    body, div#container, div#header, div#main, .bgwhte, .advanced, #nav
    {  background-color: black;  }
   
    .playbar, .settingsMenu
    {  background: none;
       background-color: #333333;
       color: #C2C2C2;
    }
   
    .clock  {  color: #333333;  }
   
    .introduction  {
       background-color: black;
       color: grey;
    }
   
   .start  {  background-color: #424242;  }
   
   h1, h2, h3, h4, h5, h6, p  {  color: #424242;  }
   
   .newPrompt  {  background-color: #2f2f2f;  }
   .newPrompt textarea  {  background-color: black;  color: darkgrey;  }
   
   #sidebarblog, #sidebar  {  background-color: #2f2f2f;  }
}
