style.css:
@-moz-document url-prefix("https://www.tumblr.com") {
/* --- v. 2.1.3 --- */

/* --- Header --- */

.l-header-container--refresh {
    bottom: 0px !important;
    top: auto !important;
    background: #BDBDBD !important;
    box-shadow: inset 0px 8px 2px -6px #fff !important;
}

.l-header-container--refresh .l-header {
    max-width: 100% !important;
    height: 30px !important;
}

.compose-button {
    height: 30px !important;
    margin-top: 0px !important;
    fill: #BDBDBD !important;
    background: #444 !important;
}

.l-header-container--refresh .l-header .logo.logo-redesign {
    margin-left: -18px !important;
}

.svg-logo {
    height: 25px !important;
    padding-top: 4px !important;
    padding-left: 25px !important;
}

.tab-bar-container {
    float: left !important;
    margin-left: -10px !important;
}

.tab_bar {
    padding-top: 1px !important;
    height: 22px !important;
    margin-top: 5px !important;
  	border-left: 1px solid #7c7c7c !important;
  	border-right: 1px solid #f1f1f1 !important;
  	box-shadow: 1px 0px 0px #7c7c7c, -1px 0px 0px #f1f1f1 !important;
}

.tab {
    margin-top: -5px !important;
}

.tab .tab_anchor {
    opacity: 1 !important;
    -webkit-filter: drop-shadow(1px 1px 0px #000) !important;
    filter: drop-shadow(1px 1px 0px #000) !important;
}

.tab-bar-container .tab_notice {
    background: #f2ead5 !important;
    border: 1px solid #444 !important;
    padding: 1px 3px !important;
    top: 4px !important;
}

.tab_notice .tab_notice_value { color: #111 !important; }

.tab_nav_account {
    float: left !important;
}

.tab_nav_account .tab_anchor { color: #B2F769  !important; }

.tab_home .tab_anchor { color: #FD6E6E  !important; }

.tab_discover .tab_anchor { color: #76EFFD  !important; }

.tab_inbox .tab_anchor { color: #F5DEA6 !important; }

.tab_activity .tab_anchor { color: #F5EE65 !important; }

.ui_search {
    float: right !important;
    width: 215px !important;
    margin-right: 32px !important;
}

.ui_search .search_query {
    height: 22px !important;
    color: #444 !important;
    margin-top: 5px !important;
    border-radius: 0px !important;
    padding-bottom: 4px !important;
    box-shadow: inset 1px 1px 1px #848484, inset -1px -1px 1px #fff !important;
}

.ui_search .search_form_field:before {
    font-size: 24px !important;
    left: 7px !important;
    top: 0px !important;
    color: #444 !important;
}

.elevator-wrapper {
    z-index: 3000 !important;
    bottom: 4px !important;
    right: 95px !important;
}

.elevator-wrapper.visible .elevator { color: #444 !important; }



/* --- Drop-downs --- */

.search_popover {
    top: auto !important;
    left: auto !important;
    bottom: 30px !important;
    right: 97px !important;
    box-shadow: none !important;
    width: 233px !important;
}

.popover_gradient>.popover_inner, .popover.search_popover>.popover_inner, .popover--account-popover .popover .popover_menu_item_blog.popover_menu_item_blog {
    border-radius: 0px !important;
    background: #BDBDBD !important;
}

.popover_header, .blog-activity .ui_notes .blog-activity-date .date_header, .blog-activity .blog-activity-more, .popover--activity-popover .popover_menu_sublist {
    background: rgba(0,0,0,0.212) !important;
    color: #444 !important;
}


.popover_menu>.popover_inner .popover_item_suffix a { color: #444 !important; }


.popover--account-popover {
    left: 6px !important;
    position: fixed !important;
    /*margin-top: -2px !important;*/
    overflow: hidden !important;
    padding-left: 28px !important;
    background: #BDBDBD !important;
    border: 2px solid #CED0CF !important;
    border-bottom-color: #6E6D6B !important;
    border-right-color: #6E6D6B !important;
}

.popover--account-popover:before {
    content: 'Tumblr 98' !important;
    color: #fff !important;
    display: block !important;
    position: absolute !important;
    bottom: 390px !important;
    left: -391px !important;
    background: #ccc !important;
    background: linear-gradient(to right, #00007B 0%, #0000FF 9%, #00007B 18%, #00007B 100%) !important;
    transform: rotate(-90deg) !important;
    width: 300% !important;
    height: 25px !important;
    padding-left: 6px !important;
    padding-top: 4px !important;
    font-weight: 600 !important;
}


.popover--activity-popover .popover {
    /*top: -1px !important;*/
    border: 2px solid #CED0CF !important;
    border-bottom-color: #6E6D6B !important;
    border-right-color: #6E6D6B !important;
    box-shadow: none !important;
    background: #BDBDBD !important;
}

.ui_notes .ui_note.is_friend {
    background: #eee !important;
    border-top-color: #777 !important;
    border-bottom-color: #fff !important;
}



/* --- body --- */

.l-container--two-column-dashboard {
    padding-top: 50px !important;
}

.post-forms-glass, .glass.blue {
    background: rgba(0,0,0,0.679) !important;
}

#dashboard_iframe {
    background: transparent !important;
  }

.identity .right_column:after, .identity .controls_section.user_list li .follow_list_item_blog:before, .radar .radar_footer .radar_avatar:before {
    background: transparent !important;
    border-color: transparent !important;
}

.identity .controls_section.follow_list, #tumblr_radar {
	background: #BDBDBD !important;
    padding: 4px !important;
    margin-top: 8px !important;
	box-shadow: inset 8px 8px 2px -6px #fff, inset -8px -8px 2px -6px #333 !important;
}

#tumblr_radar {
    padding-top: 31px !important;
    margin-left: 9px !important;
}

.radar .radar_footer .follow {
    top: 44px !important;
}

.controls_section_radar {
    margin-bottom: -45px !important;
    margin-left: 9px !important;
    position: relative !important;
    z-index: 2 !important;
    padding: 4px !important;
}

.section_header {
    color: #fff !important;
    border: none !important;
    background: linear-gradient(to right, #00007B, #0483CF) !important;
    margin-bottom: 4px !important;
}

.identity .controls_section.user_list li .follow_list_item_blog .tumblelog_title, .identity .controls_section.user_list li .follow_list_item_blog .tumblelog_name, .radar .radar_tumblelog_label .name, .radar .radar_tumblelog_label .subheading {
    color: #444 !important;
}

.identity .controls_section li:not(.section_header):not(.selected_blog):hover {
    background: rgba(0,0,0,0.161) !important;
}


.pinned_sidebar_footer {
    bottom: 24px !important;
}


/* --- posts --- */

.post.post_full {
    width: 556px !important;
    padding: 8px !important;
    border-radius: 0px !important;
    background: #BDBDBD !important;
    box-shadow: inset 8px 8px 2px -6px #fff, inset -8px -8px 2px -6px #333 !important;
}
    

.post.post_full .post_header {
    background: linear-gradient(to right, #00007B, #0483CF) !important;
    margin-left: -4px !important;
    margin-right: -3px !important;
    height: 24px !important;
    padding-top: 1px !important;
}

.post .post_header .post_info_link, .post_header .post_info {
    color: #fff !important;
}

.post .post_info .reblog_icon {
    -webkit-filter: invert(1) !important;
    filter: invert(1) !important;
    opacity: 1 !important;
}

.post_content_inner {
    background: #BDBDBD !important;
}

.post_body blockquote {
    border-left: 1px solid #797979 !important;
    box-shadow: -1px 0px 0px #f1f1f1 !important;
}

.post .post_content img {
    border: 2px solid #797979 !important;
    border-right-color: #fff !important;
    border-bottom-color: #fff !important;
}

.post_full .post_footer {
    border: 1px solid #797979 !important;
    border-right-color: #fff !important;
    border-bottom-color: #fff !important;
    border-radius: 0px !important;
    height: auto !important;
    width: auto !important;
    margin: -4px !important;
    margin-right: -3px !important;
    margin-top: 4px !important;
    clear: both !important;
    line-height: 30px !important;
}

.post .post-source-footer, .post_full .post_tags {
    background: transparent !important;
    border: 1px solid #797979 !important;
    border-right-color: #fff !important;
    border-bottom-color: #fff !important;
    color: #777 !important;
    padding: 2px 4px !important;
    margin-left: -4px !important;
    margin-bottom: 4px !important;
}

.post_full .post_tags {
    margin-right: -3px !important;
}

.post_info_link, .post_full .post_tags .post_tag {
    color: #666 !important;
}

.post_controls .post_controls_inner {
    height: auto !important;
}

.post_full .post_notes {
    color: #777 ! important;
}

.post_controls .post_control {
    margin: 3px 0px 3px 12px !important;
}


.post_full .post_permalink {
    width: 18px !important;
    height: 18px !important;
    border: none !important;
    border-radius: 0px !important;
    box-shadow: inset 7px 7px 2px -6px #fff, inset -7px -7px 2px -6px #333 !important;
    top: 8px !important;
    right: 8px !important;
}

.post .explore-trending-badge-footer {
    left: 2px !important;
    top: 4px !important;
}

.post_full .post_tags:after {
    background: linear-gradient(to right, rgba(189,189,189,0), rgba(189,189,189,1)) !important;
}

.post.has_source .post_tags.draggable:before {
    background: linear-gradient(to left, rgba(189,189,189,0), rgba(189,189,189,1)) !important;
}

.post-typography-update .post .post-content .note_wrapper .note_item, .post-typography-update .post .post_body .note_wrapper .note_item {
    background: #f1f1f1 !important;
    border: 2px solid #797979 !important;
    border-right-color: #fff !important;
    border-bottom-color: #fff !important;
    border-radius: 0px !important;
}

.post_full.is_note .nipple, .post_full.is_note .nipple::after, .post-composer_note-post .nipple, .post-composer_note-post .nipple::after { display: none !important; }



/* --- MISC --- */

.yamplus-unit-container {
    margin-left: 16px !important;
}

.yam-plus-ad-container {
    background: transparent !important;
}

#toaster {
  bottom: 31px !important;
  z-index: 3001 !important;
    background: transparent !important;
}

.recommended-unit-container.blog-card-compact {
    margin-left: 3px !important;
    margin-right: -13px !important;
}

.post_avatar {
background: transparent !important;
}


/* --- option --- */

body:not(.mceContentBody), .l-content, .left_column, .right_column {
  background: /*[[bgc]]*/ !important;
  background-attachment: fixed !important;
}

/*[[ssize]]*/
}
