style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ifconfig.me") {

body {
    background: #EEE;
}

a,
#header,
td#table_ads,
div#ads,
div#plungins {
    display: none !important;
}

#ip_address {
    font-size: 1.3em;
}

div#middle {
    height: 5px;
    padding-top: 0px;
}

table#info_table {
    table-layout: auto !important;
    width: 100%;
}

table#info_table tr {
    height: 50px;
    border-top: 1px dotted #CCC;
}

table#info_table td {
    border: 0;
    font-family: monospace;
}

#cli_wrap {
    display: none;
}

#footer {
    display: none;
}

#container {
    margin-top: 10px;
	margin: 0 auto;
	padding: 1em;
	width: 100%;
	max-width: 57em;
	max-width: 57rem;
	border: #EEE 1px solid !important;
	background: #FFF;
	box-shadow: 0 0 30px rgba(0,0,0,0.4);
	color: #333;
	word-wrap: break-word;
}


h1, h2, h3, h4, h5, p {
    font-family: "Ubuntu", "Helvetica Neue", "Lucida Grande", Helvetica, Arial, sans-serif;
}

h1 {
	font-size: 3.157em;
	font-size: 3.157rem;
}
h2 {
	font-size: 2.369em;
	font-size: 2.369rem;
	padding-top: 10px;
	padding-bottom: 15px;
}

}
