style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("zhihu.com") {

    a { outline: none!important; }
    
    .zu-top {
        background: rgba(250,250,250,0.9)!important;
        border: none!important;
        box-shadow: none!important;
    }
    .zu-top a,
    .zu-top button {
        color: #777!important;
    }
    #zh-top-link-logo {
        width: 54px!important;
        background-image: url("http://z1.zhimg.com/images/6e75b796.sprites.png");
        background-size: 240px 400px;
        background-position: 16px 10px!important;
    }
    #zh-top-inner { width: calc(100% - 60px); }
    .zu-top-search,
    .zu-top-nav {
        opacity: 0;
        transition: .2s;
    }
    .zu-top:hover .zu-top-search,
    .zu-top:hover .zu-top-nav {
        opacity: 1;
    }    
    .zu-top-search-input,
    .zu-top-add-question {
        background: #FFF!important;
        border-radius: 0!important;
        border: none!important;
        box-shadow: none!important;
    }
    .icon-magnify-q { vertical-align: -2px!important }
    .zu-top-nav {
        float: right;
        right: 150px;
    }
    .zu-top-nav-userinfo.selected, html.no-touch .top-nav-profile:hover .zu-top-nav-userinfo,
    .zu-top-nav-userinfo:hover {
        box-shadow: none!important;
        background: #EEE!important;
    }
    .zu-top-nav-li.current {
        background: #EEE!important;
        box-shadow: none!important;
    }
    .top-nav-profile li a {
        background: #EEE!important;
        border: none!important;
        box-shadow: none!important;
    }
    
    .zu-main-content-inner { margin-right: 360px; }
    
    a.zu-main-feed-fresh-button {
        position: absolute;
        left: -100px;
        top: 10px;
        border: none!important;
        border-radius: 0!important;
        text-shadow: none!important;
    }
    
    .zu-main-feed-con { border: none!important; }
    
    .zh-general-list .feed-item {
        border: none!important;
        margin-bottom: 25px;
    }
    .feed-item .avatar {
        position: absolute;
        left: -50px;
    }
    .feed-item .avatar img {
        border-radius: 19px;
    }
    .zh-general-list .feed-main {
        margin-top: 11px;
        margin-left: 0;
    }
    .feed-main .time { display: none; }
    .zh-general-list h2 a {
        color: #000!important;
    }
    .zh-general-list .entry-body {
        margin-left: 48px;
    }
    .zm-votebar button {
        background: #EEE!important;
    }
    .zm-votebar button:hover,
    .zm-votebar button.pressed {
        background: #777!important;
    }

}
