style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("phphelp.com") {

html, head, body, .frame, div, .titlebg, .info, .icon, .stats,
.lastpost, .upperframe, .upperframe span, .subject, .windowbg,
.locked_sticky, .lockedbg
{
background:#000 !important;
}
.bbc_code, code
{
background:#111 !important;
border:1px #FFF solid !important;
}
.megamenu_container, .megamenu_purple, .cat_bar, .catbg,
.megamenu_container a:hover, .megamenu_purple a:hover,
.children, .description_board, .description,
th, .botslice, .topslice span, .topslice, .botslice span
{
background:#9400d3 !important;
}
.bbc_standard_quote
{
color:#FFF !important;
background:#9400d3 !important;
border:1px #FFF solid !important;
}
a, p, li, i, b, td, h1, h2, h3, h4, h5, span, dt, strong, .stats,
.lastpost, .inner, .smalltext, .signature, .post, .pagelinks
{
color:#FFF !important;
}
}
