style.css:
@-moz-document domain("twitch.tv") {
/* 
 * Name:    Twitch TV – Circular Round Profile Pictures
 * Title:   Does what it says
 * Author:  Crystallis
 * Version: 0.3
 *
 */

.channel-link .profile-photo, .profile-link .profile-photo {
border: 4px solid #fff;
background: #000;
box-shadow: 0 0 0 1px rgba(0,0,0,0.25);
-moz-box-shadow: 0 0 0 1px rgba(0,0,0,0.25);
-webkit-box-shadow: 0 0 0 1px rgba(0,0,0,0.25);
display: block;
position: relative;
border-radius: 85px 85px 85px 85px ! important;
}
.channel-link .profile-photo div, .profile-link .profile-photo div {
position: absolute;
top: 0;
left: 0;
right: 0;
bottom: 0;
text-align: center;
padding: 20px 0;
background: rgba(0,0,0,0.75);
color: #fff;
display: none;
line-height: 20px;
font-size: 14px;
border-radius: 64px 64px 64px 64px ! important;
}
#channel .player-column #broadcast-meta .profile-link .profile-photo img {
width: 60px;
height: 60px;
border-radius: 64px 64px 64px 64px ! important;
}
.channel-link .profile-photo img, .profile-link .profile-photo img {
width: 80px;
height: 80px;
border-radius: 64px 64px 64px 64px ! important;
}
div.preview .from_to_user .prof {
float: left;
margin-right: 10px;
padding: 2px;
border: 1px solid #ccc;
background: #fff;
box-shadow: 0 1px 0 #fff;
-moz-box-shadow: 0 1px 0 #fff;
-webkit-box-shadow: 0 1px 0 #fff;
border-radius: 15px 15px 15px 15px ! important;
}
.p30 {
width: 30px;
height: 30px;
border-radius: 20px 20px 20px 20px ! important;
}

}
