    background-color: var(--bkgd1) !important;
}
#overpanel, #sidepanelContainer, sidepanelpaymentOptions {
    background-color: var(--bkgd1) !important;
}
/* for google protection from sellers --*/
 #gts-g-main {
    background-color: var(--bkgd1) !important;
}
/*--- For MyFitnessPal.com Popups ---*/
