@-moz-document domain("getonce.com") {
@font-face{font-family:Ubuntu;font-style:normal;font-weight:400;src:url(http://themes.googleusercontent.com/static/fonts/ubuntu/v4/2Q-AW1e_taO6pHwMXcXW5w.ttf) format(truetype);}
::-webkit-scrollbar{width:10px;height:10px;}
::-webkit-scrollbar-button{width:0;height:0;}
::-webkit-scrollbar-thumb{background:#e1e1e1;border:0 none #fff;border-radius:50px;}
::-webkit-scrollbar-track{background:#666;border:0 none #fff;border-radius:50px;}
::-webkit-scrollbar-track:hover{background:#666;}
::-webkit-scrollbar-corner{background:transparent;}
*{font-family:Ubuntu, sans-serif;}
.Navbar .nav__item__link:hover svg{fill:#49c1ab;}
.match{background:#000;color:#fff;}
.chat__content .sidebar.active{box-shadow:none;border-radius:0;}
.chat__content__block{background:#111;box-shadow:none;border-radius:0;}
.chat__content .header{background:#222;border-bottom:0;}
.chat__content .header .chat-action{border-left:0;}
.chat__block .reply-box{border-top:0;background:#222;}
.chat__content .disabled:before{background:rgba(34,34,34,0.5);}
.chat__block .reply-box__action__icons{border:0;}
.chat__block .reply-box__action__input{background:#000;border-radius:0;}
.chat__block .reply-box__action__input::placeholder{color:#999;}
.match-card{box-shadow:none;background:#222;border-radius:0;}
.dropdown.open .dropdown__menu{background:#333;box-shadow:none;}
.Navbar .nav__setting__profile.dropdown .dropdown__menu>a{color:#fff;border-bottom:1px solid #000;}
.oc-modal{background:#000;transition:9999s;}
.modal-complex{background:#222;color:#fff;box-shadow:none;}
.modal-complex>h2{background:#111;color:#fff;border-bottom:0;}
.oc-modal .crowns__plans__plan{border-top:1px solid #000;}
.crowns-payment__header{border-bottom:0;color:#fff;background:#333;}
.profile__header__location__choice>a:hover{color:#fff;background:#444;}
.profile__content__infos .info__value{text-align:left;}
.modal-container .modal-content .modal-box .once-modal>h2{background:#444;color:#49c1ab;border-bottom:0;}
.application-picture-uploader .picture-uploader-content .dragndrop{background:#111;border:5px dashed #349886;}
.modal-tuto .tuto-help .content .links a{background:#111;border:1px solid #000;color:#fff;}
.modal-tuto .tuto-help .content .links a:visited{background:#111;border:1px solid #000;}
::-webkit-scrollbar-thumb:hover,.slick-dots li button{background:#fff;}
::-webkit-scrollbar-thumb:active,.o-nav__item,.Navbar{background:#111;}
::-webkit-scrollbar-track:active,.responsive-footer,.oc-modal .crowns__plans__plan:hover,.oc-modal .crowns__plans__plan:nth-child(3),.modal-vip-plans .slick__item{background:#333;}
