.match-card__info:after,.profile__content__infos .info:after,.once-select__multi .item__label:after,.modal-complex .complex-description-editor textarea{background-color:#000;}
.Navbar .nav__setting__profile.dropdown .dropdown__menu>a:hover,.report-button__reasons>li a:hover,.once-select__dropdown__menu .item [type=checkbox]+.item__label:hover,.once-select__dropdown__menu .item [type=checkbox]:checked+.item__label,.modal-container .modal-content .modal-box .once-modal{background:#333;color:#fff;}
.modal-complex .complex-settings .subtitle,.oc-modal .crowns__plans__plan:last-child,.report-button__reasons>li{border-bottom:1px solid #000;}
.input.standard input,.picture-previewer .gallery-profile .profile-previewer ul li{background-color:#000;color:#fff;}
.oc-modal .crowns__plans__plan .plan-details h3,.modal-complex .complex-profile-editor{color:#349886;}
.application-picture-uploader .upload-mode.active,.modal-vip-plans .slick__item.slick-current .slick__item__content:before{background:#444;}
.modal-tuto,.pick__header,.pick__choices__choice .user,.modal-tuto-premium .container,.another__choice,.stalk-info__author{box-shadow:none;background:#222;color:#fff;}
.modal-tuto .tuto-help .content .links a:hover,.modal-tuto .tuto-help .content .links a:active{background:#333;border:1px solid #000;}
.connections .connections-chat__block__title{background:#111;}
.connections .connections-chat__block .connection:not(:last-child){border-bottom:#000;}
.connections .connections-chat__block .connection{background:#333;}
.connections .connections-chat__block .connection .connection-info .match-name{color:#49c1ab;}
.connections .connections-chat__block .connection .connection-info .msg, .connections .connections-chat__block .connection .connection-info__last-msg{color:#fff;}
.connections .connections-chat__block .connection--unread, .connections .connections-chat__block .connection:focus, .connections .connections-chat__block .connection:hover{background:#444;}
.chat-message .msg-body{box-shadow:none;background:#222;color:#fff;}
.chat-message--sender .msg-body{background-color:#000;}
.oc-modal .small-another-now{color:#fff;}

.text-divider{color:#fff;}
.block-button__label:focus, .block-button__label:hover,.block-button__content>a:focus, .block-button__content>a:hover{background:#444;}
a{color:#349886;}
a:active, a:focus, a:hover{color:#49c1ab;}
.s-icon--color--primary{color:#3085D6;}
.modal-complex .complex-settings hr{background:#000;}
.button--vip,.button--vip:visited,.button--vip:focus,.button--vip:hover{color:#644429;font-weight:bold;}
.button--warning,.button--warning:visited,.button--warning:focus,.button--warning:hover{color:#644429;font-weight:bold;}
.simple-notification.error .msg div{background-color:#CA3838;color:#fff;border-color:#CA3838;border-radius:0px;font-weight:bold;text-transform:uppercase;line-height:20px;}
.button--white{background:#000;}
.button--white:hover,.button--white:visited,.button--white:active,.button--white:focus{background:#111;}

.chat-requests__button{background:#111;}
.chat-requests__preview h1, .chat-requests__preview h2, .chat-requests__preview h3{color:#fff;}

.button--success,.button--success:visited,.button--success:focus,.button--success:hover{color:#30583A;font-weight:bold;}
.premium-buttons button{color:#386F46;}
