@-moz-document domain("librebook.me") {
.pull-right {
    float: left !important;
}

.p-control {
    box-shadow: none;
    background-color: inherit;
}

.b-chapter p {
    color: black;
}

.pageBlock {
    border-radius: 0;
    border: 0;
    box-shadow: none;
}

#mangaBox, .pageBlock {
    max-width: 65% !important;
    min-width: 50% !important;
    border-color: rgba(0, 0, 0, 0);
}

#mangaBox {
    letter-spacing: 0; padding-top: 0;
    font-size: 20px !important;
    font-family: Open Sans,Tahoma,sans-serif !important;
}

.book-header, .nav-main-menu .pull-left, .reader-arrow {
    display: none;
}

.navbar {
    background: #333;
}

.pageBlock > .row .long {
    background: rgba(246, 237, 212, 0.05);
    border-color: rgba(0, 0, 0, 0.3);
}

.form-control {
    border: 1px solid rgba(0, 0, 0, 0.3);
}

body, .second-nav {
    background-image: none;
    background: #f8f1d9 url("data:image/png;base64,");
}

.pageBlock, #mangaBox, .forumButton {
    background: #f6edd4 url("data:image/png;base64,");
}
}

