@-moz-document url-prefix("https://gamepress.gg/grandorder/"), url-prefix("https://www.gamepress.gg/grandorder/") {
html {
    filter: invert(1) hue-rotate(180deg);
    background: black;
}
html img,
html iframe,
.FGOENEMYTABLEenemystat,
.embedqty-quest-drop-item-img {
    filter: invert(1) hue-rotate(180deg);
}

.gamepress-top-branding,
.gamepress-top-branding .gamepress-sub-menu,
.gamepress-sub-menu ul li a {
    background: #fff !important;
    color: #333 !important;
    border-color: #fff !important;
}
header .gamepress-top-container {
    background: #eee !important;
    color: #333 !important;
}
header .gamepress-top-container * {
    color: #333 !important;
}
@media screen and (min-width: 1076px) {
    #side-menu-button {
        background: #eee !important;
        filter: invert(1) hue-rotate(180deg);
        border-color: #ccc !important;
    }
}
@media screen and (max-width: 1075px) {
    #side-menu-button {
        z-index: +999;
    }
}
#sidenav .sidemenu-icon {
    filter: invert(1) hue-rotate(180deg);
}
#sidenav .sidemenu-icon img {
    filter: none;
}
#overlay {
    background-color: rgba(255, 255, 255, 0.8);
}

.menu-container {
    background: #050505 !important;
    filter: invert(1) hue-rotate(180deg);
    border-color: #333 !important;
}
.menu-container * {
    border-color: #333 !important;
}
.menu-container,
.menu-container a {
    color: #eee !important;
}

@media screen and (max-width: 1075px) {
    .menu-container .menu--top-menu.outer-wrapper {
        background: #111 !important;
    }
    .menu-container a {
        background-color: #000 !important;
        color: #eee !important;
        box-shadow: 0 0 4px #666;
    }
}

section#content.column-2-setup {
    background: #e5e5e5 !important;
}
.top-content-brand,
h2,
.field--name-field-secondary-title {
    filter: invert(1) hue-rotate(180deg);
}

.accordion-custom {
    filter: invert(1) hue-rotate(180deg);
    border-color: #000 !important;
}
.featured-content-title,
.content-2-group-item {
    color: #000 !important;
    background: #eee !important;
}
.paragraph--type--featured-content img {
    background: transparent !important;
}
.featured-button:hover,
.view-footer a:hover,
.tab-link.current {
    filter: invert(1) hue-rotate(180deg);
}
.pages-ranking-number {
    filter: invert(1) hue-rotate(180deg);
    z-index: +1;
}
table td,
table td span {
    background: transparent !important;
    color: #333 !important;
}

footer {
    filter: invert(1) hue-rotate(180deg);
}
footer img {
    filter: none;
}

.commento-body p,
.commento-root textarea {
    font-family: monospace;
    white-space: pre-wrap;
    font-size: .7em;
}
