@-moz-document domain("www.sparknotes.com")
{
  html
  {
    color: white;
    background: #121212;
    font-family: 'Lexend Deca', sans-serif;
  }

  ::selection
  {
    background: #99AAB5;
    font-family: 'Lexend Deca', sans-serif;
  }

  .interior-sticky-nav__navigation, .interior-sticky-nav__main
  {
    background: #23272A;
    font-family: 'Lexend Deca', sans-serif;
  }

  .interior-sticky-nav
  {
    border-bottom: 1px solid #7289DA;
    font-family: 'Lexend Deca', sans-serif;
  }

  .interior-sticky-nav__navigation__list--short__link--active
  {
    color: white !important;
  }

  .interior-sticky-nav__navigation__list--short__link
  {
    color: #99AAB5;
  }

  .interior-sticky-nav
  {
    background: #212121;
    font-family: 'Lexend Deca', sans-serif;
  }

  .title-nav
  {
    background: #212121;
    font-family: 'Lexend Deca', sans-serif;
  }

  .title-nav__item>a
  {
    color: #99AAB5;
    transition-duration: 0.2s;
    font-family: 'Lexend Deca', sans-serif;
  }

  .subnav__bold a
  {
    color: white;
    font-family: 'Lexend Deca', sans-serif;
  }

  .subnav__light
  {
    color: white;
    font-family: 'Lexend Deca', sans-serif;
  }

  .interior__page__title
  {
    color: white;
    font-family: 'Lexend Deca', sans-serif;
  }

  p
  {
    font-family: 'Lexend Deca', sans-serif;
    color: #fafafa;
  }

  h3
  {
    font-family: 'Lexend Deca', sans-serif;
    color: white;
  }

  .quotation
  {
    font-family: 'Lexend Deca', sans-serif;
    color: #fafafa;
  }

  .most-viewed-pages__heading
  {
    font-family: 'Lexend Deca', sans-serif;
    color: white;
  }

  .most-viewed-blogs__heading
  {
    color: white;
  }

  .most-viewed-blogs__link__name
  {
    color: #99AAB5;
  }

  .interior-sticky-nav__title__text
  {
    color: white;
  }

  .global-header
  {
    background: #23272A;
  }

  .nav-link
  {
    color: white !important;
  }

  #search-overlay
  {
    background: #23272A;
  }

  .search-overlay__search-bar
  {
    background: #2C2F33;
    border-bottom: 0px solid #7289DA !important;
    border-left: 25px solid #2C2F33 !important;
    color: white !important;
  }

  #overlay-search
  {
    background: #2C2F33;
    border-bottom: 2px solid #7289DA !important;
  }

  .search-overlay__wrapper
  {
    background: #2C2F33 !important;
  }

  #search-suggestions li a
  {
    color: #99AAB5;
  }

  #search-suggestions::hover li a
  {
    background: #7289DA;
    color: #99AAB5;
  }

  .sub-list, .list-grid
  {
    background: #2C2F33 !important;
  }

  blockquote
  {
    color: black;
  }

  .footer
  {
    background: #23272A;
  }

  .footer p
  {
    color: white !important;
  }

  .footer a
  {
    color: white !important;
  }

  .footer h2
  {
    color: white !important;
  }

  b, .container--faq, .container--faq h3
  {
    color: white;
    font-family: 'Lexend Deca', sans-serif;
  }

  .quick-quiz-question, #result-zone
  {
    background: #23272A !important;
    font-family: 'Lexend Deca', sans-serif;
  }

  .noFear__cell--modern
  {
    padding-left: 12px;
    color: white;
    font-family: 'Lexend Deca', sans-serif;
  }

/* Quotes */
  .mainTextContent__quote
  {
    background-color: #212121;
    color: #ffffffde;
  }

  .mainTextContent__quote__line
  {
    color: #ffffff;
  }

/* search icon */
  .img[type="image/svg+xml"]
  {
    color: #ffffffde;
  }

/* Shakey shakey */
  .summary_sentence
  {
    color: #ffffff;
  }

  .texts-landing-page__toc__section__link__description
  {
    color: #ffffffde;
  }

  .texts-landing-page__toc__section__title
  {
    color: #ffffff;
  }
  
  .most-viewed-pages__link:hover {
    background-color: #242424;
  }
/* END - DO NOT TOUCHY */
}

@-moz-document url-prefix("https://sklep.skript.pl/") {
body {
    background-color: #222;
    background: url("https://code.skript.pl/api/background/night");
    color: #fff;
}
.bg-light {
    background-color: #212121!important;
}
.card {
    background-color: #212121!important;
    color: #fff
}
.navbar-light .navbar-nav .nav-link{
    color: #fff !important;
}
.navbar-brand {
	color: #fff !important;
}
a {
    color: #3cd2ff;
}    
a:hover {
    color: #17bbec;
}    
.table td, .table th {
   border-top: 1px solid #252525
}

.text-muted {
    color: #a9e7ff !important;
}    
.modal-content {
    background-color: #212121
}
.modal-footer {
 	border-top: 1px solid #737373;   
}
.alert-danger {
    color: #ffffff;
    background-color: #821923;
    border-color: #000000;
}
.input-group-text {
    color: #ffffff;;
    background-color: #3a3a3a;
    border: 1px solid #404040;
}
.form-control {
    color: #fff;
    background-color: #3a3a3a!important;
    border: 1px solid #404040;
}
.form-control:focus {
    color: #fff;
    background-color: #3a3a3a!important;
    border-color: #404040;
}
table.items tr:not(:first-child):hover, a.card-footer:hover {
    background: #171717;
}
}

@-moz-document regexp("https://www.twitch.tv/.*") {
.chat-list{
    position: absolute !important;
    top: 50px !important;
/*     left: -465% !important; */
    width: 100% !important;
}

.chat-list > div:nth-child(1) > div:nth-child(2){
    height: 640px !important;
}

.chat-input{
    height: 50% !important;
    position: absolute !important;
    top: 5px !important;
}

.tw-animated-number{
    text-shadow: 2px 2px #000 !important;  
}




.player-controls__right-control-group{
    position: absolute !important;
    left: 50% !important;
}

.chat-line__message, .chat-line__moderation, .chat-line__status{ font-size: 18px}
}

@-moz-document regexp("https://www.twitch.tv/.*") {
.right-column--theatre {
    background-color: transparent !important;
}

.channel-root {
    background-color: rgba(210, 0, 0, 0) !important;
}

.video-player__container--theatre-whispers {
    bottom: 0.25rem;
}

.chat-line__message{
    opacity: 90% !important;
    background-color: rgba(255, 255, 255, 0) !important;
    text-shadow: 2px 2px #000;
}

.tw-root--theme-dark .chat-room {
     background-color: rgba(255, 255, 255, 0) !important;
}

.tw-c-background-base {
    background-color: rgba(255, 255, 255, 0) !important;
}

.tw-root--theme-dark, :root {
--border-radius-large: unset !important;
--border-radius-medium: unset !important;
--border-radius-none: unset !important;
--border-radius-rounded: unset !important;
--border-radius-small: unset !important;
--border-width-default: unset !important;
--border-width-input: unset !important;
--border-width-input-overlay-unfocused: unset !important;
--border-width-marked: unset !important;
--border-width-spinner: unset !important;
--border-width-tag: unset !important;
}

.persistent-player {
top: 0px !important;
left: -.25vw !important;
position: fixed !important;
width: 100% !important;
max-height: 100% !important;
height: 100% !important;;
transform: scale(1.015) !important;;
}


.stream-chat-header{
    display: none !important;
}

.channel-root + .persistent-player {
    width: 200%;
}

.right-column__toggle-visibility{
    display: none !important;
}

.channel-leaderboard{
    display: none !important;
}

.community-highlight-stack__scroll-area--disable{
    display: none !important;
}

.tw-mg-l-05{
    display: none !important;
}


.theatre-social-panel{
    position: absolute !important;
    left: 50% !important;
}


.community-points-summary{
    position: absolute !important;
    top: 51vw !important;
    left: 200px !important;
}


.tw-align-content-center > div:nth-child(2){
    position: absolute !important;
    top: 193.5% !important;
    right: 18vw !important;
}


.chat-settings__content{
   background-color:#26262a!important;
}

.tw-block{
    background-color: transparent !important;
}

}

@-moz-document url-prefix("https://www.bingosync.com/room/") {
.square {
    color: #fff;
    text-shadow: 1px 1px 2px rgba(0,0,0,0.7);
    padding: 0 5px;
    cursor: pointer;
    width: 100px;
    height: 100px;
    text-align: center;
    border: 1px #424242 solid;
    background: #181818;

    /* vertical-align changed to top so background colors wont be pushed halfway down */
    vertical-align: top;

    /* position needs to be relative for overflow hidden to work */
    position: relative;
    overflow: hidden;
}
}

