@-moz-document domain("web.telegram.org") {
/*      Author @Sventra                  */
/*      Based on @RisingFire redesign    */
body {
    background: #1B2735;
    font-family: 'Roboto', sans-serif;
}
.tg_head_split {
    max-width: none;
    height: 100px;
    background: #456A94;
}
.tg_head_logo_wrap {
    width: 20%;
    margin-left: 11.4%;
}
.tg_head_main_wrap {
    width: 53.6%;
}
/* BOTH LEFT SIDE BAR AND CHAT HISTORY */
.im_page_wrap {
    background: none;
    border: none;
    margin-top: -50px;
    margin-bottom: 50px;
    -webkit-box-shadow: none;
    max-width: 1500px;
}
/* LEFT SIDE BAR COLUMN*/
.im_dialogs_col_wrap {
    border-radius: 26px;
    width: 26%;
    background: #212E3E;
    border: 2px solid #1B2735;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 15px 0 rgba(0, 0, 0, 0.19);
    margin-bottom: 20px;
    margin-left: 10px;
}
.im_dialogs_col {
    border-radius: 26px;
}
div.im_dialogs_wrap.nano.has-scrollbar.active-scrollbar {
    height: 100.8%;
    border-radius: 24px;
}

input[type=search] {
    border-radius: 16px;
    background: #1B2735;
    border: 2px solid #1B2735
}
i.icon-search-clear {
    color: #fff;
}
.im_dialog_peer {
    color: #fff;
}

/* Pinned message bar */
.im_history_pinned_wrap {
    border: none;
    background: #456A94;
    padding-bottom: 14px;
    max-height: 50px;
}
.im_message_reply_thumbed .im_message_reply_border, .im_message_reply_thumbed .im_message_reply_wrap {
    height: 32px;
}
.im_message_body {
    background: #283A50;
