.pagination-button > svg > g > g, .video-slider-controls button > svg > g > g, .logo-anchor path:nth-child(2), .logo path:nth-child(2), .icon-button[data-track-id="burger-menu"]:hover > svg > path, .icon-button[data-track-id="burger-menu"]:focus > svg > path {
