data.css:
@-moz-document domain("studio.youtube.com") {
/* Dark Scrollbar*/

::-webkit-scrollbar,
 ::-webkit-scrollbar-corner,
 ::-webkit-scrollbar,
::-webkit-scrollbar-corner {
    background: #222 !important;
  }
::-webkit-scrollbar-button,
 ::-webkit-scrollbar-button {
    display: none !important;
  }
::-webkit-scrollbar-thumb,
 ::-webkit-scrollbar-thumb {
    background: #444 !important;
  }
::-webkit-scrollbar-thumb:not(:active):hover,
 ::-webkit-scrollbar-thumb:not(:active):hover {
    background: #383838 !important;
  }
::-webkit-scrollbar-thumb:active,
::-webkit-scrollbar-thumb:hover:active,
 ::-webkit-scrollbar-thumb:active,
 ::-webkit-scrollbar-thumb:hover:active {
    background: #5c5c5c !important;
  }
  
/* vars */

:root {
		--theme-color: #F00;
		--primary-color: var(--theme-color) !important;
		--ytve-border-color-focused: var(--theme-color) !important;
		--ytve-timeline-marker-color-active: var(--theme-color) !important;
		--ytcp-focused-color: var(--theme-color) !important;
		--theme-darken-color: #a00;
		--theme-add-color: #c00;
		--theme-darken2-color: #800;
		--theme-darken3-color: #300;
		--theme-lighten-color: #f77;
		--theme-lighten2-color: #fbb;
		--shadow: 0px 0px 7px 4px #000;
		--bg-color: #111;
		--ytcp-background-10: #111 !important;
		--bg2-color: #222;
		--bg3-color: #333;
		--item-color: #444;
		--item-hover-color: #666;
		--item-active-color: #777;
		--text-color: #fff;
		--text-darken-color: #ddd;
		--text-darken2-color: #aaa;
        --ytve-secondary-color: #aaa !important;
        --ytcp-focused-background-color: #333 !important;
    --paper-button-flat-keyboard-focus_-_background-color: #f77 !important;
    --ytcp-call-to-action: var(--theme-color) !important;
    --ytcp-text-primary: #fff !important;
	--ytve-background-color: #333 !important;
    --ytcp-highlighted-background-color: #666 !important;
    --paper-tabs-selection-bar-color: var(--theme-color) !important;
    --ytcp-entity-card-title-color: #fff;
    --yt-spec-text-primary: #fff !important;
    --paper-progress-active-color: #f00 !important;
    --paper-tab-ink: var(--theme-color) !important;
    --ytcp-link-color: var(--theme-color) !important;
    --paper-progress-container-color: #f44 !important;
    --yt-spec-call-to-action: var(--theme-color) !important;
    --ytcp-black-disabled: #ccc !important;
    --ytcp-background-0: #111 !important;
    --ytcp-call-to-action-ripple: #d3060622 !important;
}


.ytcp-header {
    background: var(--theme-color);
}

.st0.style-scope.ytcp-home-button, iron-icon[icon="icons:search"]{
    fill: var(--text-color);
}

.st1.style-scope.ytcp-home-button {
    fill: var(--text-color) !important;
}

.st2.style-scope.ytcp-home-button {
    fill: var(--theme-color);
}

.ytcp-icon-button {
    fill: var(--text-color);
}

#query-input {
    background: rgba(255,255,255,0.2);  
    color: var(--text-color);
}

#query-input::placeholder {
    color: var(--text-color);
    opacity: 1;
}

.search-icon.ytcp-omnisearch {
    color: var(--text-color) !important;
}

ytcp-navigation-drawer, #left-nav{
    background: var(--bg2-color);
    color: var(--text-color);
}

.nav-item-text{
    color: var(--text-color);
}

iron-icon.ytcp-navigation-drawer {
    --iron-icon-width: 24px;
    --iron-icon-height: 24px;
    color: var(--text-color);
}

paper-icon-item.iron-selected.ytcp-navigation-drawer, paper-icon-item.iron-selected.ytcp-navigation-drawer iron-icon.ytcp-navigation-drawer {
    color: var(--theme-color);    
}

paper-icon-item.iron-selected.ytcp-navigation-drawer > .nav-item-text {
    color: var(--theme-color);   
}

.menu-item-link.ytcp-navigation-drawer > paper-icon-item:hover > #contentIcon > iron-icon.ytcp-navigation-drawer {
    color: var(--theme-color);
}

.menu-item-link.ytcp-navigation-drawer > paper-icon-item:hover > div {
    color: var(--theme-color);
}

ytcp-navigation-drawer[keyboard-focus] .menu-item-link.ytcp-navigation-drawer:focus paper-icon-item.ytcp-navigation-drawer, ytcp-navigation-drawer[keyboard-focus] paper-icon-item.ytcp-navigation-drawer:focus, paper-icon-item.ytcp-navigation-drawer:hover, paper-icon-item[submenu-open].ytcp-navigation-drawer, paper-icon-item.iron-selected.ytcp-navigation-drawer {
    background: var(--bg2-color);
}

ytcp-navigation-drawer[enable-matterhorn-entity-label] .entity-type.ytcp-navigation-drawer {
    color:var(--text-color);
}

ytcp-navigation-drawer[enable-matterhorn-entity-label] .entity-name.ytcp-navigation-drawer {
    color:var(--text-color);
}

main {
    background: var(--bg-color) !important;
}

.page-title {
    color: var(--text-color);
}

/* Cards */

.default-card.ytcd-card{
    background: var(--bg-color);
}

.page-title-container.ytcp-app{
    background-color: var(--bg2-color);
}

.ytcd-item > div > .item-title {
    color: var(--text-color);
}

.ytcd-item > div > div > div {
    color: var(--text-color) !important;
}

.ytcd-item > div > hr {
        border-bottom: 1px solid rgba(255,255,255,0.5) !important;
}

#metrics-table > div > div {
    color: var(--text-color) !important;
}

.ytcd-item > div > .section-title > span {
    color: var(--text-color) !important;
}

.ytcd-item > div > .top-videos > table > tbody > tr > td {
    color: var(--text-color) !important;
}

.default-card.ytcd-card > .card-title {
    color: var(--text-color);
}

.default-card.ytcd-card > ytcd-item > ytcd-ki-item > div > ul > li > a > span {
    color: var(--text-color);
}



.card-title {
    color: var(--text-color) !important;
}

.ytcd-item > .header > #title {
    color: var(--text-color) !important; 
}

.ytcd-item > .content > span {
    color: var(--text-color) !important; 
}

ytcd-subscriber-highlights-item > .title {
    color: var(--text-color) !important; 
}

ytcd-subscriber-highlights-item > .subtitle {
    color: var(--text-color) !important; 
}

ytcd-subscriber-highlights-item > ul > li > ytcp-ve > a > div > span {
    color: var(--text-color) !important; 
}

ytcd-video-snapshot-item > div > .title{
    color: var(--text-color) !important; 
}

ytcd-video-snapshot-item > div > hr {
    border-bottom: 1px solid rgba(255,255,255,0.5) !important;
}

#video-snapshot.yta-video-snapshot {
    background: none;
}

#video-snapshot.yta-video-snapshot > ytcp-ve > div {
    color: var(--text-color) !important; 
    
}

#video-snapshot.yta-video-snapshot > ytcp-ve > #metrics-table > div > #table-ranking-container > div > span {
    color: var(--text-color) !important; 
    
}

#video-snapshot.yta-video-snapshot > ytcp-ve > #metrics-table > div > #table-ranking-container > div > iron-icon {
    fill: var(--text-color) !important; 
    
}

/* End Cards */

#menu-header.ytcp-missing-features-menu {
   color: var(--text-color) !important;  
}

paper-icon-item.ytcp-missing-features-menu {
    color: var(--text-color) !important; 
}

paper-icon-item.ytcp-missing-features-menu:hover {
    color: black !important; 
}

#missing-features-item:hover > #contentIcon > iron-icon {
    color: var(--theme-color) !important; 
}

#missing-features-item:hover > div {
    color: var(--theme-color) !important; 
}

#missing-features-item:hover > iron-icon {
    color: var(--theme-color) !important; 
}

#missing-features-item > #contentIcon > iron-icon {
    color: var(--text-color) !important; 
}

#missing-features-item > iron-icon {
    color: var(--text-color) !important; 
}

#menu-items.ytcp-missing-features-menu > li > ytcp-ve > a > paper-icon-item > #contentIcon > iron-icon {
    color: var(--text-color) !important;    
}

#menu-items.ytcp-missing-features-menu > li > ytcp-ve > a > paper-icon-item:hover > #contentIcon > iron-icon {
    color: var(--theme-color) !important;  
}

#bottom-section > ul > li > ytcp-send-feedback-button > ytcp-ve > paper-icon-item:hover > #contentIcon > iron-icon {
    color: var(--theme-color) !important;
}

#bottom-section > ul > li > ytcp-send-feedback-button > ytcp-ve > paper-icon-item:hover > div {
    color: var(--theme-color) !important;
}

#bottom-section > ul > li > ytcp-ve > paper-icon-item:hover > #contentIcon > iron-icon {
    color: var(--theme-color) !important;
}

#bottom-section > ul > li > ytcp-ve > paper-icon-item:hover > div {
    color: var(--theme-color) !important;
}

#bottom-section > ul > li > ytcp-ve > paper-icon-item:hover > iron-icon {
    color: var(--theme-color) !important;
}


/* ytcp-button {
    color: var(--theme-color) !important;
} */

ytcp-button[raised] {
    min-width: 88px;
    color: var(--text-color) !important;
    background-color: var(--theme-color);
}

#header.ytcp-video-section-content {
    background: var(--bg2-color) !important;
}

paper-tabs.ytcp-primary-action-bar {
    --paper-tabs-selection-bar_-_background-color: var(--theme-color);
    --paper-tab-content_-_color: var(--theme-color);
    --paper-tab-content-unselected_-_color: var(--text-color) !important;
}

ytcp-filter-bar {
  background: var(--bg3-color) !important;  
}

iron-icon.ytcp-filter-bar {
    --iron-icon-width: 24px;
    --iron-icon-height: 24px;
    color: var(--text-color);
}

ytcp-chip-bar > div > input::placeholder {
    color: var(--text-color) !important;
}

ytcp-chip-bar > div > input {
    color: var(--text-color) !important;
}

ytcp-chip > ytcp-icon-button > .ytcp-icon-button {
    fill: var(--theme-color) !important;
}

ytcp-chip {
    background: var(--item-color) !important;
}

ytcp-chip > .text {
    color: var(--text-color) !important;
}

#checkbox.checked.ytcp-paper-checkbox {
    background-color: var(--ytcp-paper-checkbox-checked-color, var(--theme-color));
    border-color: var(--ytcp-paper-checkbox-checked-color, var(--theme-color));
}

#checkbox.ytcp-paper-checkbox {
    position: relative;
    box-sizing: border-box;
    height: 100%;
    border: solid 2px;
    border-color: var(--ytcp-paper-checkbox-unchecked-color, var(--text-color));
    border-radius: 2px;
    pointer-events: none;
    -webkit-transition: background-color 140ms, border-color 140ms;
    transition: background-color 140ms, border-color 140ms;
}

ytcp-table-header {
    background: var(--bg3-color) !important;
}

.floating-column.ytcp-table-header, ytcp-table-header {
    background-color: var(--bg3-color) !important;
}

.cell-header.ytcp-table-header {
    color: var(--text-color) !important;
}

#row-container.row-selected.ytcp-video-row, #row-container.row-selected.ytcp-video-row .floating-column.ytcp-video-row, ytmus-library-row{
    background-color: var(--item-color) !important;
}

ytmus-library-row div{
    color: var(--text-color);
}

#row-container.ytcp-video-row, #row-container.ytcp-video-row .floating-column.ytcp-video-row{
    background-color: var(--bg3-color);
}

#row-container.ytcp-video-row.row-highlighted, #row-container.ytcp-video-row.row-highlighted .floating-column.ytcp-video-row {
    background-color: var(--item-color);
}

#row-container.ytcp-video-row, #row-container.ytcp-video-row .floating-column.ytcp-video-row > .cell-body > ytcp-video-list-cell-video > div > h3 {
    color: var(--text-color) !important;
}

#video-title.ytcp-video-list-cell-video {
    color: var(--text-color) !important;
}

.description.placeholder.ytcp-video-list-cell-video {
    color: var(--text-color) !important;
}

.description.ytcp-video-list-cell-video {
 color: var(--text-color) !important;   
}

#row-container.ytcp-video-row, #row-container.ytcp-video-row .floating-column.ytcp-video-row > .cell-body > ytcp-video-list-cell-video > div > h3 > a:href {
    color: var(--text-color) !important;
}

.first-row.ytcp-video-list-cell-visibility .privacy-label.ytcp-video-list-cell-visibility {
    color: var(--text-color) !important;
}

.visibility-invisible.ytcp-video-list-cell-visibility .privacy-icon.ytcp-video-list-cell-visibility {
    color: var(--text-color) !important;
}

.tablecell-comments.cell-body.ytcp-video-row a.ytcp-video-row, .tablecell-comments.cell-body.ytcp-video-row span.ytcp-video-row {
    color: var(--text-color) !important;
}

.percent-label.ytcp-video-list-cell-likes {
    color: var(--text-color) !important;
}

.likes-label.ytcp-video-list-cell-likes {
    color: var(--text-color) !important;    
}

.progress-fill.ytcp-video-list-cell-likes {
    background: var(--text-color) !important;
    height: var(--progress-height);
    width: 0;
    border-radius: var(--ytcp-video-list-cell-likes-radius);
}

.tablecell-date.cell-body.ytcp-video-row {
     color: var(--text-color) !important; 
}

.tablecell-date.cell-body.ytcp-video-row .cell-description.ytcp-video-row {
    color: var(--text-color) !important; 
}

ytcp-primary-action-bar {
   background-color: var(--bg-color) !important; 
}

.page-description.ytcp-table-footer {
    margin: 0 24px;
    color: var(--text-color) !important; 
}

.borderless.ytcp-dropdown-trigger .right-container.ytcp-dropdown-trigger iron-icon.ytcp-dropdown-trigger {
    color: var(--text-color) !important; 
}

ytcp-text-dropdown-trigger[compact] .dropdown-trigger-text.ytcp-text-dropdown-trigger {
    color: var(--text-color) !important;
}

ytcp-table-footer {
    color: var(--text-color) !important;
}


.contents.ytcp-video-metadata-editor, .footer-container.ytcr-history-table{
    background-color: var(--bg-color); 
}
ytcp-navigation-drawer:not([theme="DARK"]) .back-button-text.ytcp-navigation-drawer {
    color: var(--text-color) !important; 
}

paper-icon-item.ytcp-navigation-drawer:hover > .nav-item-text {
    color: var(--theme-color) !important;
}

.text-container.ytcp-form-textarea {
 border: 1px solid var(--text-color);
}

#section-label.ytcp-form-textarea {
   color: var(--text-color) !important;  
}

.text-container.ytcp-form-textarea:hover {
 border: 1px solid var(--theme-color);
}

#section-label.ytcp-form-textarea > div > ytcp-ve > iron_icon {
   fill: var(--text-color) !important;  
}

iron-icon.ytcp-form-textarea {
    --iron-icon-width: 24px;
    --iron-icon-height: 24px;
    color: var(--text-color) !important;  
}

#section-label.ytcp-form-textarea, textarea.ytcp-form-textarea, .char-counter.ytcp-form-textarea {
    padding-left: 12px;
    padding-right: 12px;
    color: var(--text-color) !important; 
}

.borderless.container.ytcp-dropdown-trigger{
padding-left: var(--ytcp-dropdown-trigger-left-padding, 12px);
}
#outer.ytcp-form-input-container > div {
    color: var(--text-color);
}

#outer.ytcp-form-input-container {
    background-color: var(--bg2-color);
    border: none;
}

#outer.ytcp-form-input-container:hover, ytcp-form-input-container.focused #outer.ytcp-form-input-container, ytcp-form-input-container.focused #outer.ytcp-form-input-container:hover {
    background-color: var(--bg3-color);
    border: none;
}

ytcp-form-input-container.focused #label.ytcp-form-input-container{
    color: var(--theme-color);
}

.sublabel.ytcp-thumbnails-compact-editor {
     color: var(--text-color) !important;
}

a.ytcp-thumbnails-compact-editor, a.ytcp-thumbnails-compact-editor > iron-icon[icon$="open-in-new"].ytcp-thumbnails-compact-editor {
    color: var(--theme-color);
}

#tags-description[slot="description"].ytcp-video-metadata-basics {
    color: var(--text-color);
}

.container.ytcp-video-metadata-info {
    background-color: var(--item-color);
}

paper-icon-item.ytcp-navigation-drawer:after {
    background: var(--theme-color);
}

a.ytcp-video-metadata-info, a.ytcp-video-metadata-info > iron-icon[icon$="open-in-new"].ytcp-video-metadata-info {
    color: var(--theme-color);
}

.label.ytcp-video-metadata-info {
    color: var(--text-color) !important;
}

.value.ytcp-video-metadata-info {
    color: var(--text-color) !important;
}

#label.ytcp-video-metadata-visibility {
    color: var(--text-color) !important;
}

#first-row.ytcp-video-metadata-visibility {
    color: var(--text-color) !important;
}

.label-text.ytcp-dropdown-trigger {
    color: var(--text-color) !important;
}

.dropdown-trigger-text.ytcp-text-dropdown-trigger {
    color: var(--text-color) !important;
}

ytcp-dropdown-trigger[keyboard-focus] .right-container.ytcp-dropdown-trigger iron-icon.ytcp-dropdown-trigger, .container.ytcp-dropdown-trigger:hover .right-container.ytcp-dropdown-trigger iron-icon.ytcp-dropdown-trigger {
    color: var(--text-color) !important;
}

.right-container.ytcp-dropdown-trigger iron-icon.ytcp-dropdown-trigger {
    color: var(--text-color) !important;
}

.subhead.ytcp-video-metadata-advanced {
    color: var(--text-color) !important;
}

iron-icon.ytcp-dropdown-trigger {
    --iron-icon-width: 24px;
    --iron-icon-height: 24px;
    color: var(--text-color) !important;
}

input.ytcp-form-autocomplete {
    color: var(--text-color) !important;
}

.subtext.ytcp-video-metadata-advanced {
    color: var(--text-color) !important;
}

.subtext.ytcp-video-metadata-advanced > a {
    color: var(--theme-color) !important;
}

.subhead-compact.ytcp-video-metadata-advanced {
    color: var(--text-color) !important;
}

.label.ytcp-checkbox, ytcp-paper-checkbox.ytcp-checkbox > * {
    color: var(--text-color) !important;
}

#message-text.ytcp-comments-section {
    color: var(--text-color) !important;
}

.optin-header.ytgn-video-translations-section {
    color: var(--text-color) !important;
}

#row-container.ytgn-video-translation-row, #row-container.ytgn-video-translation-row .floating-column.ytgn-video-translation-row {
    background-color: var(--item-color);
}

#row-container.ytgn-video-translation-row.row-highlighted, #row-container.ytgn-video-translation-row.row-highlighted .floating-column.ytgn-video-translation-row {
    background-color: var(--theme-color);
}

.cell-body.ytgn-video-translation-row .language-text.ytgn-video-translation-row {
    color: var(--text-color) !important;
}

.language-text.ytgn-video-translation-row, .last-modified-text.ytgn-video-translation-row {
    color: var(--text-color) !important;
}

.translation-status.ytgn-video-translation-cell-captions {
    color: var(--text-color) !important;
}

.translation-source.ytgn-video-translation-cell-captions {
    color: var(--text-color) !important;
}

#sort-menu-container.ytcp-comments-section {
    background-color: var(--bg-color);
}

#metadata.ytcp-comment {
    color: var(--text-color) !important;
}

#name.ytcp-comment, #reply-endpoint.ytcp-comment {
    color: var(--theme-color) !important;
    font-family: var(--header-font);
    font-weight: 700;
}

#content-text.ytcp-comment {
     color: var(--text-color) !important;
}

ytcp-comment-button[is-paper-button] yt-formatted-string.ytcp-comment-button {
    color: var(--text-color) !important;
}

ytcp-comment-toggle-button #button.ytcp-comment-toggle-button, #button.ytcp-comment-button{
    color: var(--text-color) !important;
}


ytcp-comment-button[is-icon-button]:not([disabled]):hover yt-icon.ytcp-comment-button{
    color: var(--theme-color);
}


ytcp-comment-button[disabled][is-icon-button] yt-icon.ytcp-comment-button{
    color: var(--bg3-color) !important;
}


ytcp-author-comment-badge.creator {
    padding-right: 6px;
    padding-left: 6px;
    --ytcp-author-comment-badge-background-color: var(--theme-color) !important;
    --ytcp-author-comment-badge-hover-background-color: var(--theme-color) !important;
    --ytcp-author-comment-badge-name-color: var(--text-color) !important;
    --ytcp-author-comment-badge-hover-text-color: var(--text-color) !important;
}

ytcp-comment[highlighted] {
    background-color: var(--bg2-color);
}

#vote-count.ytcp-comment-action-buttons {
    color: var(--text-color) !important;
}

ytcp-comment-toggle-button.style-text[is-icon-button] yt-icon.ytcp-comment-toggle-button {
    color: var(--text-color) !important;
}

#unhearted.ytcp-comment-creator-heart {
    color: var(--text-color) !important;
}

#button.ytcp-menu-renderer yt-icon.ytcp-menu-renderer {
    color: var(--text-color) !important;
}

#sort-filter.ytcp-sort-filter-sub-menu-renderer {
    border: 1px solid var(--text-color);
}

#sort-filter.ytcp-sort-filter-sub-menu-renderer :hover #sort-filter.ytcp-sort-filter-sub-menu-renderer {
    border: 1px solid var(--theme-color);
}

#background.yta-overview-app {
   background-color: var(--bg3-color); 
}

paper-card {
   background-color: var(--item-color);  
}

paper-listbox {
   background-color: var(--item-color);
}

paper-item.iron-selected.yta-key-metric-card {
   background-color: hsl(0, 0%, 35%);   
}

#metric-label.yta-key-metric-block {
    color: var(--text-color) !important;
}

.layout.horizontal.yta-key-metric-block > div {
    color: var(--text-color) !important;
}

.comparison-label-content.yta-key-metric-card {
    color: var(--text-color) !important;
}

#chart-container.yta-line-chart-base {
    fill: var(--text-color) !important;
}

.container.ytcp-badge {
    color: var(--text-color);
