.content > ul > li:nth-of-type(1)  {grid-area: 1/1/2/2}
.content > ul > li:nth-of-type(10) {grid-area: 4/2/5/4}
.content > ul > li:nth-of-type(11) {grid-area: 5/1/6/3}
.content > ul > li:nth-of-type(12) {grid-area: 5/3/6/4}
.content > ul > li:nth-of-type(13) {grid-area: 6/1/7/2}
.content > ul > li:nth-of-type(14) {grid-area: 6/2/7/3}
.content > ul > li:nth-of-type(15) {grid-area: 6/3/7/4}
.content > ul > li:nth-of-type(16) {grid-area: 7/1/8/2}
.content > ul > li:nth-of-type(17) {grid-area: 7/2/8/3}
.content > ul > li:nth-of-type(18) {grid-area: 7/3/8/4}
.content > ul > li:nth-of-type(19) {grid-area: 8/1/9/3}
.content > ul > li:nth-of-type(2)  {grid-area: 1/2/3/3}
.content > ul > li:nth-of-type(20) {grid-area: 8/3/10/4}
.content > ul > li:nth-of-type(21) {grid-area: 9/1/12/2}
.content > ul > li:nth-of-type(22) {grid-area: 9/2/12/3}
.content > ul > li:nth-of-type(23) {grid-area: 10/3/11/4}
.content > ul > li:nth-of-type(24) {grid-area: 11/3/12/4}
.content > ul > li:nth-of-type(25) {grid-area: 12/1/13/2}
.content > ul > li:nth-of-type(26) {grid-area: 12/2/13/3}
.content > ul > li:nth-of-type(27) {grid-area: 12/3/13/4}
.content > ul > li:nth-of-type(3)  {grid-area: 1/3/2/4}
.content > ul > li:nth-of-type(4)  {grid-area: 2/1/3/2}
.content > ul > li:nth-of-type(5)  {grid-area: 2/3/3/4}
.content > ul > li:nth-of-type(6)  {grid-area: 3/1/4/2}
.content > ul > li:nth-of-type(7)  {grid-area: 3/2/4/3}
.content > ul > li:nth-of-type(8)  {grid-area: 3/3/4/4}
.content > ul > li:nth-of-type(9)  {grid-area: 4/1/5/2}
}

@-moz-document url("https://optifine.net/downloads") {
/** CSS Start **/


.downloadLine .downloadLineFile {background:rgb(50,100,150)}
.downloadLine {border:1px solid rgb(50,100,150)}
.downloadLineFirst .downloadLineFileFirst {background:rgb(50,150,100)}
.downloadLineFirst {border:1px solid rgb(50,150,100)}


.content > .downloads {display: block}
.content > .downloads > tbody {display: block}
.content > .downloads > tbody > tr {display: block}
.content > .downloads > tbody > tr > td {display:flex;flex-direction:column;flex-wrap: nowrap;}
.content #preview > .downloadTable {width: 100%;}
.content #preview > .downloadTable > tbody {display: flex;flex-direction: row;flex-wrap: wrap;width: 100%;}
.content #preview > .downloadTable > tbody > .downloadLine {display: grid;max-width: 30%;flex: 1 1 auto;background: rgb(40,44,46);border: 2px solid rgb(50,100,150);margin: 10px;}
.content #preview > .downloadTable > tbody > .downloadLine > .downloadLineChangelog {grid-column: 3/4;grid-row: 2/3;margin: 10px;height: 30px;width: 90px;}
.content #preview > .downloadTable > tbody > .downloadLine > .downloadLineDate {grid-column: 1/4;grid-row: 3/4;width: inherit;height: 30px;font-size: 16px;text-align: center;line-height: 30px;}
.content #preview > .downloadTable > tbody > .downloadLine > .downloadLineDownload {grid-column: 1/2;grid-row: 2/3;height: 30px;width: 90px;margin: 10px;}
.content #preview > .downloadTable > tbody > .downloadLine > .downloadLineFile {grid-column: 1/4;grid-row: 1/2;text-align: center;font-size: 18px;width: 100%;height: 30px;line-height: 30px;background: rgb(50,100,150);color: white;}
.content #preview > .downloadTable > tbody > .downloadLine > .downloadLineMirror {grid-column: 2/3;grid-row: 2/3;margin: 10px;width: 90px;height: 30px;}
.content #preview > .downloadTable > tbody > tr > .downloadLineChangelog > a {background: rgb(100,50,150);}
.content #preview > .downloadTable > tbody > tr > .downloadLineMirror > a {background: rgb(50,100,150);text-indent: -10px;}
.content #preview > .downloadTable > tbody > tr > td > a {display: block;height: 30px;width: 90px;text-align: center;line-height: 30px;color: white;border-radius: 4px;box-shadow: 0px 0px 4px rgb(20,24,26);background: rgb(50,150,100);}
.content #preview > br {display:none}

.content > .downloads > tbody > tr > td > a:nth-of-type(1) {color: rgb(220,220,220);height: 30px;text-align: center;line-height: 30px;text-decoration: underline rgb(200,200,200);font-size: 24px;}
.content > .downloads > tbody > tr > td > a:nth-of-type(1):hover {color: rgb(50,100,200);text-decoration-color: rgb(50,100,200)}
.content > .downloads > tbody > tr > td > h2 {color: rgb(220,220,220);height: 30px;text-align: center;line-height: 30px;margin-top: 10px;text-decoration: underline rgb(200,200,200);}
.content > .downloads > tbody > tr > td > h3 {color: rgb(220,220,220);height: 30px;text-align: center;line-height: 30px;text-decoration: underline rgb(200,200,200);}
.content > .downloads > tbody > tr > td > span:last-of-type {text-align: center;color: rgb(220,220,220);}

.content .downloadTable {width: 100%;}
.content .downloadTable > tbody {display: flex;flex-direction: row;flex-wrap: wrap;width: 100%;}
.content .downloadTable > tbody > tr {display: grid;max-width: 30%;flex: 1 1 auto;background: rgb(40,44,46);margin: 10px;}
.content .downloadTable > tbody > tr > .downloadLineChangelog {grid-column: 3/4;grid-row: 2/3;margin: 10px;height: 30px;width: 90px;}
.content .downloadTable > tbody > tr > .downloadLineChangelog > a {background: rgb(100,50,150);}
.content .downloadTable > tbody > tr > .downloadLineDate {grid-column: 1/4;grid-row: 3/4;width: inherit;height: 30px;font-size: 16px;text-align: center;line-height: 30px;}
.content .downloadTable > tbody > tr > .downloadLineDownload,
.content .downloadTable > tbody > tr > .downloadLineDownloadFirst {grid-column: 1/2;grid-row: 2/3;height:30px;width:90px;margin:10px;}
.content .downloadTable > tbody > tr > .downloadLineFile,
.content .downloadTable > tbody > tr > .downloadLineFileFirst {grid-column: 1/4;grid-row: 1/2;text-align: center;font-size:18px;width:100%;height: 30px;line-height: 30px;color: white;font-weight: 400;}
.content .downloadTable > tbody > tr > .downloadLineMirror {grid-column: 2/3;grid-row: 2/3;margin: 10px;width: 90px;height: 30px;}
.content .downloadTable > tbody > tr > .downloadLineMirror > a {background: rgb(50,100,150);text-indent: -10px;}
.content .downloadTable > tbody > tr > td > a {display: block;height: 30px;width: 90px;text-align: center;line-height: 30px;color: white;border-radius: 4px;box-shadow: 0px 0px 4px rgb(20,24,26);background: rgb(50,150,100);text-transform: capitalize;}
}

@-moz-document url("https://optifine.net/donate") {
/** CSS Start **/


.content > p {text-align: center;}


.content > p {font-size: 16px;width: 90%;margin: 0 auto;padding: 10px;border-bottom: 2px solid rgb(50,100,150);}
.content > form {margin: 10px 80px;display: flex;flex-wrap: wrap;flex-direction: row;text-align: center;line-height: 30px;}
.content > form > label {height: 30px;width: max-content;line-height: 30px;}
.content > form > input {background: rgb(20,24,26);border-color: rgb(50,54,56);flex: 1 1 auto;color: white;padding: 0 10px;margin: 0 10px;}
.content > form > br {display: none;}
.content > form > #capeDesign {width: 100%;display:   flex !important;flex-direction: row;flex-wrap: wrap;margin-top: 20px;align-items: center;font-size: 24px;color: rgb(220,220,220);padding-bottom: 80px;}
.content > form > #capeDesign > select {height: 32px;width: 120px !important;margin: 0 10px;text-align: center;background: rgb(20,24,26);color: white;border: none;}
.content > form > #capeDesign > #capePreview {display: block !important;margin: 0 !important;padding: 20px 10px;background: rgb(20,24,26);border-radius: 4px;}
.content > form > #capeDesign > #capeCustom {padding: 10px;background: rgb(20,24,26);}
.content > form > #capeDesign > #capeCustom > tbody {display: grid;}

.content > form > #capeDesign > #capeCustom > tbody > tr {display: grid;}
.content > form > #capeDesign > #capeCustom > tbody > tr > td {font-size: 18px;text-align: center;margin: 2px 4px 0px 4px;}

.content > form > #capeDesign > #capeCustom > tbody > tr:nth-of-type(1) {grid-column: 1/2;grid-row: 1/2;}
.content > form > #capeDesign > #capeCustom > tbody > tr:nth-of-type(1) > td:nth-of-type(1) {grid-column: 1/2;grid-row: 1/2;}
.content > form > #capeDesign > #capeCustom > tbody > tr:nth-of-type(1) > td:nth-of-type(2) {grid-column: 1/2;grid-row: 2/3;}
.content > form > #capeDesign > #capeCustom > tbody > tr:nth-of-type(1) > td:nth-of-type(3) {grid-column: 2/3;grid-row: 1/2;}
.content > form > #capeDesign > #capeCustom > tbody > tr:nth-of-type(1) > td:nth-of-type(4) {grid-column: 2/3;grid-row: 2/3;}

.content > form > #capeDesign > #capeCustom > tbody > tr:nth-of-type(2) {grid-column: 2/3;grid-row: 1/2;}
.content > form > #capeDesign > #capeCustom > tbody > tr:nth-of-type(2) > td:nth-of-type(1) {grid-column: 1/2;grid-row: 1/2;}
.content > form > #capeDesign > #capeCustom > tbody > tr:nth-of-type(2) > td:nth-of-type(2) {grid-column: 1/2;grid-row: 2/3;}
.content > form > #capeDesign > #capeCustom > tbody > tr:nth-of-type(2) > td:nth-of-type(3) {grid-column: 2/3;grid-row: 1/2;}
.content > form > #capeDesign > #capeCustom > tbody > tr:nth-of-type(2) > td:nth-of-type(4) {grid-column: 2/3;grid-row: 2/3;}

.content > form > #capeDesign > #capeCustom > tbody > tr > td > .color {border: none;padding: 4px 10px;text-align: center;}

.content > form > #capeDesign > #capeBanner {flex: 1 1 auto;}
.content > form > #capeDesign > #capeBanner > tbody {display: grid;}
.content > form > #capeDesign > #capeBanner > tbody > tr{display: grid;grid-template-columns: 120px 120px auto;}
.content > form > #capeDesign > #capeBanner > tbody > tr:nth-of-type(1) > td {color: white;text-align: center;font-size: 18px;}
.content > form > #capeDesign > #capeBanner > tbody > tr:nth-of-type(1) {grid-column: 1/2;grid-row: 1/2;}
.content > form > #capeDesign > #capeBanner > tbody > tr:nth-of-type(1) > td:nth-of-type(1) {grid-column: 2/3;grid-row: 1/3;display: flex;margin: 0 10px;}
.content > form > #capeDesign > #capeBanner > tbody > tr:nth-of-type(1) > td:nth-of-type(1) > a {color: white;background: rgb(50,100,200);padding: 4px 20px;border-radius: 4px;align-self: center;}
