  min-height: 12px !important;
  max-height: 12px !important;
}
}

@-moz-document url-prefix("https://play.typeracer.com/"), url-prefix("https://data.typeracer.com/") {
.nonHideableWords,
.inputPanel span {

    letter-spacing: 0.02px;
    font-size: 22px
}




h1,
h2,
h3 .gameView .gameStatusLabel,
.textInfoView .textInfoHeader,
.textInfoView .textInfoOwnStats,
.textInfoView .textInfoTitle a,
.textInfoView .textInfoOwnStatsNumber,
.tblOwnStatsNumber .datetime,
.tblOwnStatsNumber,
.textInfoView,
.tblOwnStats,
.gwt-InlineLabel,
.gwt-Anchor,
.rankPanelWpm.rankPanelWpm-self,
.themeContent,
.main,
.mainviewportholder,
.mainViewport,
.highScoresView,
.gwt-TabBar,
.gwt-TabPanel,
.navigation {
    background-color: black !important;
    color: #ff00aa !important;
}


.chrome_l,
.chrome_m,
.chrome_r,
.chrome_t,
.chrome_tr,
.chrome_tl,
.chrome_b,
.chrome_bl,
.chrome_br {
    background: none
}

td.title {
    background-color: black !important;
    padding: 25px
}

.main {
    background-color: black !important;
    background: none;
}

#tstats {
    background-color: black !important;
    color: #ff00aa !important;
}

td {
    background-color: black !important;
}

.footer {
    background: none;
    background-color: black !important;
    color: #ff00aa !important;
}

.footer a {
    color: #ff00aa !important;
}


.navigation a {
    color: #ff00aa
}


.title a {
    color: #ff00aa !important;
}


#tstats .userNameLabel {
    color: #ff00aa !important;
}



#tstats .datarow td {
    color: #eaff00 !important;
}


.DialogBox,
.dialogContent {
    background-color: black !important;
    color: #ff00aa !important;
}

.popupContent {

    overflow: auto;
}

.PlayerInfoPopup .dialogContent {



    overflow: auto;
}

.PlayerInfoPopup .lblAccountType a {
    color: yellow !important
}


.PlayerInfoPopup .pitLink {
    color: yellow !important;
}
.gwt-HTML {
    color: yellow !important;
}


.gwt-Anchor {
    color: yellow !important;
}

.gwt-TabBar,
.gwt-TabBarItem {
    color: yellow !important;
}

.hyuSQleP {
    color: black !important;
}

.gameView .inputPanel .txtInput-error {
    color: black !important;
    background-color: #d08383 !important
}
.gameView .inputPanel .txtInput {
    color: #ff00aa
}

.mainMenuItem a {
    color: #ff00aa !important;
}

.gwt-MenuBar {
    background-color: black !important;
}

.gwt-DisclosurePanel .header {
    color: #ff00aa
}

.tblOwnStatsNumber {
    color: yellow !important;
}

.gameView .raceAgainLink {
    color: #ff00aa !important;
}

.textInfoView .textInfoContributor a {
    color: #ff00aa !important;
}

.ScoreMenuButton a {
    color: #ff00aa !important;
}

p {

    display: none
}

table.scoresTable td,
table.scoresTable a {
    color: #ff00aa !important;
}

table.scoresTable td:first-child,
table.scoresTable th:first-child,
table.scoresTable th {
    background-color: black !important;
}

.fullTextStr {
    background-color: black !important;
}
}
