    font-weight: 600;
    border-bottom: 2px solid #7289da; 
}
.profile_menu ul li.rightside img {
    height: 34px; 
}

.profile_ranking {
    font-size: 12px;
    margin-top: 20px;
    opacity: 0.5;
}

.profile_username h1 {
    color: white;
    font-family: Rochester;
    size: 50px;
    text-shadow: 0px 0px 8px black;
