}

::-webkit-scrollbar {
    height: 15px;
    width: 15px;
    background-color: #24252a; 
}

::-webkit-scrollbar-thumb {
    background: #151719;
    border: 4px solid #36393f;
    border-left: none; 
    border-right: none; 
    border-radius: 10px; 
}
::-webkit-scrollbar-thumb:hover {
    background: #202225; 
}

a.msg_delete {
    color: #1c3738;
