@font-face {font-family: 'Lora'; font-style: normal; font-weight: 700; font-display: swap; src: url(https://fonts.gstatic.com/s/lora/v15/0QIvMX1D_JOuMwr7Iw.woff2) format('woff2'); unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2191, U+2193, U+2212, U+2215, U+FEFF, U+FFFD;}

:root {
--theme_default_bg: hsl(30,20%,97%);
--theme_default_scrollbg: hsl(30,20%,97%,.2);
--theme_default_scroll: hsl(30,20%,70%);
--theme_default_scrollhover: hsl(30,20%,65%);
--theme_default_scrollactive: hsl(30,20%,60%);
--theme_default_postbg: hsl(30,20%,95%);
--theme_default_postbg2: hsl(30,20%,90%);
--theme_default_postbghighlight: hsl(30,20%,88%);
--theme_default_mod: hsl(210,30%,30%);
--theme_default_spectext: hsl(300,40%,25%);
--theme_default_postnum: hsl(145,25%,40%);
--theme_default_postnum2: hsl(145,25%,50%);
--theme_default_border: hsl(30,20%,83%);
--theme_default_altborder: hsl(30,20%,73%);
--theme_default_text: hsl(30,5%,20%);
--theme_default_alttext: hsl(30,5%,40%);
--theme_default_alttext2: hsl(30,5%,60%);
--theme_default_contrasttext: hsl(30,5%,100%);
--theme_default_red: hsl(0,60%,60%);
--theme_default_sage: hsl(0,60%,70%);
--theme_default_ophui: hsl(145,25%,40%);
--theme_default_link: hsl(30,30%,50%);
--theme_default_hover: hsl(30,30%,40%);
--theme_default_title: hsl(210,30%,30%);
--theme_default_drag:hsl(30,5%,70%);
--theme_default_spoiler: hsl(30,5%,85%);
--theme_default_icon: hsl(30,5%,60%);
--theme_default_btntext: var(--theme_default_text);
--theme_default_altbtntext: hsl(30,5%,100%);
--theme_default_btnbg: hsl(30,20%,92%);
--theme_default_btnborder: hsl(30,20%,87%);
--theme_default_altbtnborder: hsl(30,20%,77%);
--theme_default_altbtnbg: hsl(30,20%,92%);
--theme_default_btnbghover: hsl(30,20%,85%);
--theme_default_altbtnbghover: hsl(30,20%,90%);
--theme_default_inputtext: var(--theme_default_text);
--theme_default_checkbox: 1px 2px;
--theme_nmswitch: hsl(30,5%,60%);
--theme_mpborder: hsl(30,5%,40%);
--theme_default_shadow: hsl(30,5%,2%,.05);
--theme_default_shadow2: hsl(30,5%,2%,.1);
--theme_default_shadow3: hsl(30,5%,2%,.15);
--theme_default_shadow4: hsl(30,5%,2%,.25);
}
html[data-theme=nightmode] {
--theme_default_bg: hsl(30,5%,15%);
--theme_default_scrollbg: hsl(30,5%,15%,.2);
--theme_default_postbg2: hsl(30,5%,25%);
--theme_default_scroll: hsl(30,5%,45%);
--theme_default_scrollhover: hsl(30,5%,50%);
--theme_default_scrollactive: hsl(30,5%,55%);
--theme_default_postbg: hsl(30,5%,20%);
--theme_default_postbghighlight: hsl(30,5%,23%);
--theme_default_mod: hsl(210,30%,80%);
--theme_default_spectext: hsl(300,20%,60%);
--theme_default_postnum: hsl(145,25%,60%);
--theme_default_postnum2: hsl(145,25%,70%);
--theme_default_border: hsl(30,5%,25%);
--theme_default_altborder: hsl(30,5%,30%);
--theme_default_text: hsl(30,5%,80%);
--theme_default_alttext: hsl(30,5%,65%);
--theme_default_alttext2: hsl(30,5%,50%);
--theme_default_contrasttext: hsl(30,5%,100%);
--theme_default_red: hsl(0,40%,55%);
--theme_default_sage: hsl(0,40%,65%);
--theme_default_ophui: hsl(145,25%,60%);
--theme_default_link: hsl(30,30%,60%);
--theme_default_hover: hsl(30,30%,70%);
--theme_default_title: hsl(210,30%,80%);
--theme_default_drag: hsl(30,5%,50%);
--theme_default_spoiler: hsl(30,5%,30%);
--theme_default_icon: hsl(30,5%,50%);
--theme_default_btntext: var(--theme_default_text);
--theme_default_altbtntext: hsl(30,5%,15%);
--theme_default_btnbg: hsl(30,5%,17%);
--theme_default_btnborder: hsl(30,5%,22%);
--theme_default_altbtnborder: hsl(30,5%,27%);
--theme_default_altbtnbg: hsl(30,5%,17%);
--theme_default_btnbghover: hsl(30,5%,25%);
--theme_default_altbtnbghover: hsl(30,5%,20%);
--theme_default_inputtext: var(--theme_default_text);
--theme_default_checkbox: 1px -18px;
--theme_nmswitch: hsl(30,30%,60%);
--theme_mpborder: hsl(30,5%,50%);
--theme_default_shadow: hsl(30,5%,2%,.15);
--theme_default_shadow2: hsl(30,5%,2%,.2);
--theme_default_shadow3: hsl(30,5%,2%,.25);
--theme_default_shadow4: hsl(30,5%,2%,.35);
}

::-webkit-scrollbar {background: var(--theme_default_scrollbg); height: 14px; width: 14px;}
::-webkit-scrollbar-track {background-clip: padding-box; border: solid transparent; border-width: 0;}
::-webkit-scrollbar-button {width: 0px; height: 0px;}
::-webkit-scrollbar-thumb {background-color: var(--theme_default_scroll); background-clip: padding-box; border: 2px solid transparent; border-radius: 4px;}
::-webkit-scrollbar-thumb:hover {background-color: var(--theme_default_scrollhover);}
::-webkit-scrollbar-thumb:active {background-color: var(--theme_default_scrollactive);}
::-webkit-scrollbar-corner {background: var(--theme_default_scrollbg);}

div[style="width: 100%; height: 100%; position: fixed; top: 0px; left: 0px; z-index: 2000000000; background-color: rgb(255, 255, 255); opacity: 0.5;"] {background-color: var(--theme_default_bg) !important;}

html, body {background: var(--theme_default_bg); color: var(--theme_default_text); font-family: 'Lora', 'Georgia', serif !important; line-height: 1.35;}
body {font-size: 1em;}

.cntnt {max-width: 1024px; margin: 0 auto; padding: 0 35px 0 15px;}
.catalog, .body-stickers {max-width: 1024px; margin: 0 auto; padding: 0 10px;}

*::selection {background: var(--theme_default_link); color: var(--theme_default_contrasttext);}
*::-moz-selection {background: var(--theme_default_link); color: var(--theme_default_contrasttext);}
*::-webkit-selection {background: var(--theme_default_link); color: var(--theme_default_contrasttext);}

.rmenu, #plashque, #mobile-plashque, .logo img, .logo br, .mmm {display: none;}

a {color: var(--theme_default_link); text-decoration: none; transition: color .1s; outline: none;}
a:hover {color: var(--theme_default_hover);}
a:link {text-decoration: none;}
a[href*="youtube.com"] {border-bottom: none;}
a[href$="sage"], a[href$="Sage"], a[href$="SAGE"] {color: var(--theme_default_sage);}
a[savefrom_lm_is_link="1"] {background: none !important;}
a[savefrom_lm_is_link="1"]::before {content: '\f019'; font: 16px/1 FontAwesome;}

/* ===== Main Page ===== */
.st0, .st1, use[fill="url(#gradientaaCw2bI38)"] {fill: var(--theme_default_text);}
.welcomefriend {color: var(--theme_default_text);}

.wraper.mainpage {margin: 0 10px;}
.mcontent, .board-data {max-width: 1000px; width: auto;}
.hdescription, .hdescription-stats {font-family: inherit;}
.postscontainer {border-bottom-width: 2px; border-radius: 4px;}
font[color="red"], font[color="#ff0000"], span[style="font-size:10px; color:red;"], span[style="color: #ff0000;"], .closed-board, .archive-thread, .thanks-abu {color: var(--theme_default_red) !important;}
.hnews .news-posts-count {color: var(--theme_default_alttext2);}

/* Stats */
.ui-widget, .ui-widget input, .ui-widget select, .ui-widget textarea, .ui-widget button {font-family: inherit;}
.ui-state-default a, .ui-state-default a:link, .ui-state-default a:visited, a.ui-button, a:link.ui-button, a:visited.ui-button, .ui-button {color: var(--theme_default_alttext);}
.ui-state-hover a, .ui-state-hover a:hover, .ui-state-hover a:link, .ui-state-hover a:visited, .ui-state-focus a, .ui-state-focus a:hover, .ui-state-focus a:link, .ui-state-focus a:visited, a.ui-button:hover, a.ui-button:focus {color: var(--theme_default_text);}

.ui-widget-content {background: var(--theme_default_postbg);}
.ui-widget-header {background: var(--theme_default_bg);}
.cloudheader .cloudoption {color: var(--theme_default_alttext);}
.cloudheader .cloudoption:hover {color: var(--theme_default_text);}

.table .thead .th {background: var(--theme_default_link); border: none; color: var(--theme_default_altbtntext);}
.table .thead .th a[style="color:black;"] {color: var(--theme_default_altbtntext) !important;}
.board-speed {color: var(--theme_default_alttext);}

hr {border-color: transparent;}
.hr {border-top-color: var(--theme_default_border);}
.box-data .data {border-color: var(--theme_default_border);}

/* FAQ */
a.logo {background: none; color: var(--theme_default_text) !important; padding: 0;}
a.logo::before {content: "\f0e7"; font: 60px/1 FontAwesome; padding-right: 10px;}

.box {background: var(--theme_default_postbg); border-bottom-width: 2px; border-color: var(--theme_default_border); border-radius: 4px;}
.box hr {border-top-color: var(--theme_default_border);}
.box .boardname {color: var(--theme_default_link);}

/* Donate Abu */
#adminbar ul.menu ul li a {color: var(--theme_default_link);}
#adminbar ul.menu ul li a:hover {color: var(--theme_default_hover);}
#adminbar ul.menu ul li a:visited {color: var(--theme_default_link);}
.logo {color: var(--theme_default_logo);}

.reply {background: var(--theme_default_postbg); border-bottom-width: 2px; border-color: var(--theme_default_border); border-radius: 4px;}

/* Passcode kupi - Abu pomogi */
.header {color: var(--theme_default_text);}

#fixed {background-color: var(--theme_default_postbg); border-top: 1px solid var(--theme_default_border); border-bottom: 1px solid var(--theme_default_border); box-shadow: none;}
.amenu {font-family: inherit !important;}
.topmenu li a.mitem:link, .topmenu li a.mitem:visited {background: none; color: var(--theme_default_link); font-size: 20px;}
.topmenu li a.mitem:hover {color: var(--theme_default_hover);}
.addban, .floatforms {background-color: var(--theme_default_postbg); border: 1px solid var(--theme_default_border);}
.floatforms {font-family: inherit;}

/* Stickers */
.body-stickers .sticker {background-color: var(--theme_default_postbg); border-bottom-width: 2px; border-color: var(--theme_default_border); color: var(--theme_default_text);}
.body-stickers .sticker:hover {border-color: var(--theme_default_altborder);}

.sticker {scrollbar-color: var(--theme_default_scroll) transparent; scrollbar-width: thin;}

/* ===== Catalog ===== */
.logo {font-family: unset; font-size: 2.5em; font-weight: normal;}

.thread_wrap {border-radius: 4px; margin: 5px;}
.thread_wrap.expandble:hover .cthread {border-radius: 4px; box-shadow: 0 0 0 1px var(--theme_default_border);}
.thumb {border-radius: 4px; box-shadow: none;}

/* ===== Page ===== */
.warningcover {background-color: var(--theme_default_bg); backdrop-filter: blur(10px); opacity: .95;}
.warningbox {border: 1px solid var(--theme_default_border); border-bottom-width: 2px; border-radius: 4px; box-shadow: 0 6px 16px var(--theme_default_shadow2); margin-top: -50px; overflow: auto;}

#fullscreen-container {box-shadow: 0 6px 16px var(--theme_default_shadow4); padding: 0; margin: 6px 4px 4px 0;}
#fullscreen-container:hover:after {opacity: 1;}
#fullscreen-container:after {background: var(--theme_default_postbg); border: 1px solid var(--theme_default_border); border-radius: 13px; color: var(--theme_default_text); padding: 1px 5px; top: -8px; left: 50%; transform: translateX(-50%); width: unset; opacity: 0; transition: .1s;}
#fullscreen-container video {outline: none;}

.na {background: transparent; border-radius: 0; height: 50%; width: 35px; padding: 0; right: 0; text-align: center; transition: .1s; z-index: 200;}
.na_type_up:hover, .na_type_up:active {background: linear-gradient(to top, transparent, var(--theme_default_postbg));}
.na_type_down:hover, .na_type_down:active {background: linear-gradient(to top, var(--theme_default_postbg), transparent);}
.na__icon {display: block; fill: var(--theme_default_alttext2); position: fixed;}
.na:hover .na__icon {fill: var(--theme_default_alttext);}
.na_type_up {top: 0;}
.na_type_up .na__icon {padding: 50px 10px 0; width: 35px; height: 65px; top: 0;}
.na_type_down {bottom: 0; transform: none;}
.na_type_down .na__icon {padding: 50px 10px 0; width: 35px; height: 65px; transform: rotate(180deg); bottom: 0;}

.alert {border-radius: 4px; border: 1px solid var(--theme_default_border); border-bottom-width: 2px; box-shadow: 0 1px 6px var(--theme_default_shadow2);}

.media-thumbnail img {border-radius: 4px; box-shadow: 0 1px 6px 0 var(--theme_default_shadow3);}

/* Header */
.adminbar {color: var(--theme_default_alttext2); opacity: 0; transition: .2s;}
.adminbar__menu a {margin: 0 2px;}
.adminbar__boards_sticky {display: none;}
header:hover .adminbar {opacity: 1;}

select {background: var(--theme_default_altbtnbg); border: 1px solid var(--theme_default_btnborder); border-bottom-width: 2px; border-radius: 4px; color: var(--theme_default_text); padding: 2px 2px !important; outline: none; transition: .1s;}
select:focus {background: var(--theme_default_altbtnbghover); border-color: var(--theme_default_altborder);}

.boardname a {color: var(--theme_default_text); font-size: 1.3em; font-weight: normal;}

/* Setting */
.settings__prefs {margin-left: 15px;}
.settings__header span {font-size: 0;}
.settings__header span:first-child::before {content: "\f078"; font: 12px/1 FontAwesome; margin-right: 4px;}
.settings__header span:last-child::before {content: "\f077"; font: 12px/1 FontAwesome; margin-right: 4px;}
.settings__label select {margin-left: 4px;}
.settings__edit {background: none; color: var(--theme_default_text);}
.settings__edit:hover {color: var(--theme_default_link);}
.settings__edit::before {content: "\f044"; font: 14px/1 FontAwesome;}
#setting-editor-textarea-textarea {font-family: Consolas, monospace;}

.ah__header {background-color: var(--theme_default_postbg2);}
.ah__cell {border-color: var(--theme_default_postbg2);}
.error {border-color: var(--theme_default_red) !important; border-bottom-width: 2px !important;}
.ah__input:focus {border-color: var(--theme_default_altbtnborder) !important;  border-bottom-width: 2px !important; box-shadow: 0 1px 3px var(--theme_default_shadow);}

/* Create Post */
.newpost__label {font-weight: bold;}

textarea {min-height: 50px; max-height: 1000px; scrollbar-color: var(--theme_default_scroll) transparent; scrollbar-width: thin;}
input[type="text"], input[type="password"], textarea, .input {background: var(--theme_default_altbtnbg); border: 1px solid var(--theme_default_btnborder); border-bottom-width: 2px; border-radius: 4px; box-shadow: none; color: var(--theme_default_inputtext); padding: 3px 8px; transition: .1s; outline: none;}
input[type="text"]:focus, input[type="password"]:focus, textarea:focus, .input:focus {background: var(--theme_default_altbtnbghover); border-color: var(--theme_default_altbtnborder);}
input[type="text"]::placeholder, input[type="password"]::placeholder, textarea::placeholder {color: var(--theme_default_alttext2);}

button, .button, html input[type=button], input[type=reset], input[type=submit], .arch-searchform input[type="submit"] {background: none; border: none; box-shadow: none; color: var(--theme_default_link); font-weight: bold; padding: 4px 8px; transition: .1s; outline: none;}
button:hover, .button:hover, html input[type=button]:hover, input[type=reset]:hover, input[type=submit]:hover {background: none; color: var(--theme_default_hover);}
button[disabled], html input[disabled] {background: none !important; color: var(--theme_default_alttext2) !important;}

.postform .rules {display: none;}

#qr-CommentToolbar, #CommentToolbar {position: relative;}
#qr-CommentToolbar span, #CommentToolbar span {display: inline-block; height: 18px;}
.markup {padding: 1px 3px; opacity: 0;}
.m-bold:hover, .m-italic:hover, .m-underline:hover, .m-overline:hover, .m-strike:hover, .m-sup:hover, .m-sub:hover {color: var(--theme_default_link);}
.m-bold:before {content: "\f032"; font: 14px/18px FontAwesome; position: absolute; height: 18px; width: 22px; text-align: center;}
.m-italic:before {content: "\f033"; font: 14px/18px FontAwesome; position: absolute; height: 18px; width: 22px; text-align: center;}
.m-quote {color: var(--theme_default_postnum);}
.m-quote:hover {color: var(--theme_default_postnum2);}
.m-quote:before {content: "\f054"; font: 14px/18px FontAwesome; position: absolute; height: 18px; width: 22px; text-align: center;}
.m-underline:before {content: "\f0cd"; font: 14px/18px FontAwesome; position: absolute; height: 18px; width: 22px; text-align: center;}
.m-overline:before {content: "O"; font: bold 14px/20px serif; position: absolute; height: 18px; width: 22px; text-decoration: overline; text-align: center;}
.m-spoiler {color: var(--theme_default_alttext2); width: 31px;}
.m-spoiler:hover {color: var(--theme_default_text);}
.m-spoiler img {width: 31px; height: 18px;}
.m-spoiler:before {background: var(--theme_default_spoiler); content: "SP"; line-height: 16px; position: absolute; margin-top: 1px; height: 16px; width: 31px; text-align: center; transition: .1s;}
.m-strike:before {content: "\f0cc"; font: 14px/18px FontAwesome; position: absolute; height: 18px; width: 22px; text-align: center;}
.m-sup:before {content: "\f12b"; font: 14px/18px FontAwesome; position: absolute; height: 18px; width: 22px; text-align: center;}
.m-sub:before {content: "\f12c"; font: 14px/18px FontAwesome; position: absolute; height: 18px; width: 22px; text-align: center;}

input[type="checkbox"][name="delete"] {display: none;}
input[type="checkbox"] {cursor: pointer; height: 15px; width: 15px; -webkit-appearance: none; -moz-appearance: none; appearance: none; outline: none; margin-top: -2px;}
input[type="checkbox"]::before {background: var(--theme_default_altbtnbg); border: 1px solid var(--theme_default_altborder); border-bottom-width: 2px; border-radius: 4px; box-sizing: border-box; content: ""; display: block; height: 15px; width: 15px; transition: .1s;}
input[type="checkbox"]:focus::before {background: var(--theme_default_altbtnbghover);}
input[type="checkbox"]::after {color: transparent; content: "\f00c"; display: block; font: 12px/15px FontAwesome; height: 15px; width: 15px; position: relative; bottom: 15px; text-align: center; transition: .1s;}
input[type="checkbox"]:checked::after {color: var(--theme_default_text);}

.filer__limits {color: var(--theme_default_drag); font-size: 12px; margin: 2px 0;}
.filer__drag-area {background-color: var(--theme_default_btnbg); border: 1px solid var(--theme_default_btnborder); border-bottom-width: 2px; border-radius: 4px; color: var(--theme_default_alttext2); padding-top: 11px;}
.filer__drag-area_hover {background-color: var(--theme_default_btnbghover);}
.filer__thumb {background: var(--theme_default_postbg); border-radius: 4px; overflow: hidden;}
.filer__meta {background: var(--theme_default_glassbg); backdrop-filter: blur(5px); opacity: 1;}
.filer__size {margin-left: 4px;}
.filer__delete {font-size: unset;}

.oekaki-width, .oekaki-height {width: 44px;}

.grecaptcha-badge {display: none;}

/* Drawing */
.literally .lc-picker, .literally .horz-toolbar, .literally .lc-options .lc-font-settings {background-color: var(--theme_default_postbg) !important;}
.literally .lc-picker, .literally.toolbar-at-top .lc-options {border-color: var(--theme_default_border) !important;}
.literally .lc-picker .toolbar-button:hover:not(.disabled) {background-color: var(--theme_default_border) !important; border-color: var(--theme_default_border) !important;}
.literally .lc-picker .toolbar-button.selected:not(.disabled) {background-color: var(--theme_default_link) !important; border-color: var(--theme_default_link) !important; color: var(--theme_default_postbg);}
.literally .color-well-color-container {background-color: transparent !important; border-color: var(--theme_default_border) !important;}
.literally .color-well-color-container:hover:not(.disabled) {border-color: var(--theme_default_link);}
.literally .horz-toolbar .square-toolbar-button {border-color: var(--theme_default_border) !important;}
.literally .horz-toolbar .square-toolbar-button:hover:not(.disabled) {border-color: var(--theme_default_altborder) !important;}
.literally .horz-toolbar .square-toolbar-button.selected:not(.disabled) {background-color: var(--theme_default_postbghighlight) !important; border-color: var(--theme_default_link) !important;}
.lc-font-settings select {margin-right: 4px;}
.square-toolbar-button circle {fill: var(--theme_default_text);}
.literally .color-well {padding-bottom: 2px; border-radius: 4px 0 0 4px;}
.literally .color-well.open {background-color: var(--theme_default_altpostbg);}
.literally .color-picker-popup {background-color: var(--theme_default_postbg);}

img[src="/makaba/templates/js/lcanvas/img/dashed-line.png"], img[src="/makaba/templates/js/lcanvas/img/line-with-arrow.png"] {display: none;}
.lc-options.horz-toolbar .square-toolbar-button[style="float:left;margin:1px;"]:first-child::before {content: "\f142"; display: block; font: 22px/28px FontAwesome; width: 27px; text-align: center;}
.lc-options.horz-toolbar .square-toolbar-button[style="float:left;margin:1px;"]:nth-child(2)::before {content: "\f176"; display: block; font: 22px/26px FontAwesome; width: 27px; text-align: center;}

.lc-pick-tool, .lc-undo, .lc-redo, .lc-zoom-out, .lc-zoom-in {background: none !important;}
.lc-pick-tool[style="background-image:url(/makaba/templates/js/lcanvas/img/pencil.png);"]::before {content: "\f040"; font: 22px/22px FontAwesome;}
.lc-pick-tool[style="background-image:url(/makaba/templates/js/lcanvas/img/eraser.png);"]::before {content: "\f12d"; font: 22px/22px FontAwesome;}
