@-moz-document domain(coub.com) {

/*--------------------------------------*/
.coub.coub--normal-card .coub__viewer {
    width: 150%;
}

.coub.coub--normal-card .coub__description {
    width: 150%;
}


.coub .coub__inner {
    position: absolute;
    right: 150px;
    top: -25px;
}

.viewer.viewer--v2.x2 {
    width: auto !important;
    height: auto !important;
    margin: auto !important;
}


.viewer.viewer--v2.x2.-fullscreen {
    width: auto !important;
    height: 100% !important;
    margin: auto !important;
}


/*--------------------------------------*/
.stories-block__container {
    visibility: hidden;
    position: absolute;
}

.channels-block {
    visibility: hidden;
    position: absolute;
}

.timeline-right-block {
    position: absolute;
    visibility: hidden;
    margin-left: -1000px;
}

.-fill.viewer__suggests {
    visibility: hidden;
    position: fixed;
}


.page-menu__view-selector {
    right: 280px;
}

.page-menu__view-selector {
    right: 170px;
}

.page-menu,
.page-menu__inner {
    height: 35px
}

.page-menu__item:not(.list__item) {
    height: 10px
}

.page-menu__inner:not(.-mouse-over) .page-menu__item:not(.list__item).-active::before,
.page-menu__item:not(.list__item).-hovered::before {
    height: 3px;
    bottom: -5px
}

.page-menu__item:not(.list__item) {
    font-size: 20px;
    letter-spacing: -0.6px;
    padding: 0px 20px;
    cursor: pointer;
    position: relative;
    text-align: center;
    line-height: 0px;
    white-space: nowrap;
}


.page-menu.-fixed {
    position: absolute;
    top: 70px;
    z-index: 798;
    width: 1050px;
    border-bottom: 1px solid #e1e1e1;
}

.page-menu {
    border-bottom: 1px solid #010101;
}


.page-menu {
    border-bottom: 0px solid #010101;
}

.header.-scrolled > .header__bg {
    filter: drop-shadow(0 0 0px rgba(0, 050, 125, 0.5)) invert(0%) contrast(125%);
    border-bottom: 0px solid #00000075;
}

.timeline-right-block {
    z-index: 200;
    float: right;
    position: relative;
    width: 210px;
    height: -22px;
    left: 60px;
}

.page-menu,
.page-menu__inner {
    height: 35px;
    width: 950px;
    left: 0px;
    top: 3px;
}

.page-menu__inner:last-of-type {
    margin-left: 130px;
    left: 20px;
}

.page-menu.-fixed {
    position: relative;
    top: 2px;
    z-index: 798;
    width: 755px;
    border-bottom: 1px solid #e1e1e1;
}

.page-menu-dummy {
    visibility: hidden;
    position: fixed;
}

.coub .coub__inner {
    position: relative;
    right: 30px;
}


.header.-scrolled > .header__bg {
    filter: drop-shadow(0 0 0px rgba(0, 050, 125, 0.5)) invert(0%) contrast(125%);
    border-bottom: 0px solid #00000075;
}

.main-menu__inner {
    position: relative;
    background-color: #0000;
    box-shadow: 0 0 0 1px rgba(0, 0, 0, .1);
    height: 1050px;
    width: 250px;
    transition: transform 0.15s ease, visibility 0s linear 0.15s;
    transform: translateX(-100%);
}


.page-menu__inner:last-of-type {
    margin-left: 110px;
    left: 15px;
}


.main-menu.-always-visible:not(.-folded) + .page-container {
    border-left-width: 200px;
}


.header__contents {
    height: 55px;
    width: 100%;
    padding: 1%;
    position: relative;
    margin: 0 auto;
    transition: height .2s ease-in-out, padding-top .2s ease-in-out;
}

.main-menu__fold {
    display: block;
    width: 18px;
    height: 28px;
    background-color: #fff0;
    border: 1px solid #fff0;
    border-left: none;
    border-top-right-radius: 5px;
    border-bottom-right-radius: 5px;
    position: absolute;
    bottom: 500px;
    left: 200px;
    box-sizing: content-box;
    cursor: pointer;
}

.page-menu {
    margin-bottom: 40px;
}

.channel-menu-dropdown__content .list__item.change-channel {
    text-indent: 43px;
    padding-bottom: 25px;
}

.channel-menu-dropdown__content .list__item.logout {
    text-indent: 48px;
    top: -3px;
}

div.page-menu__view-selector {
    position: absolute;
}

.main-menu__nav-item.more-less-toggler {
    padding-left: 23px;
    color: #999999;
    line-height: 40px;
}

.main-menu__fold {
    border-top: none;
}

.main-menu__fold {
    position: relative;
    bottom: 0;
    left: 250px;
}


body {
    font-family: helveticaneuecoub-normal, Helvetica, Arial, "Lucida Grande", sans-serif;
    font-weight: 400;
    color: #ffffff;
    min-height: 100%;
    overflow-x: hidden;
    overflow-y: scroll;
    background-color: #000000eb;
    -webkit-text-size-adjust: 100%;
    -ms-text-size-adjust: 100%;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-size: 16px;
    font-size: 1rem;
    line-height: 1.05;
}

.page-menu::before {
    content: '';
    height: 100%;
    width: 10000px;
    left: -5000px;
    position: absolute;
    background-color: #141414;
}



.header__bg {
    background: #141414;
    transition: all .2s ease-in-out;
    box-shadow: 0 1px 0 0 rgb(20, 20, 20);
}

.header.-scrolled>.header__bg {
    background: #141414;
}

.page-menu__inner:not(.-mouse-over) .page-menu__item:not(.list__item).-active::before, .page-menu__item:not(.list__item).-hovered::before {
    content: '';
    position: absolute;
    bottom: -3px;
    left: 0;
    width: 100%;
    height: 3px;
    background-color: #ce1651f2;
    border-radius: 2px;
}


a {
    color: #ce1651f2;
    text-decoration: none;
}


.description__controls>div i svg path {
    fill: #ffffff;
}


.coub__tags-tag.tags-community, .story__tags-tag.tags-community {
    color: #000000;
    background-color: #ce1651f2;
    border-radius: 15px;
}


.main-menu__nav-item.active i svg path {
    fill: #ce1651f2;
}


.main-menu__nav-item.active {
    background-color: rgb(27, 27, 27);
}	

.dropdown__content {
    text-align: left;
    position: relative;
    background-color: #141414;
    box-shadow: 0 1px 2px rgb(20, 20, 20);
    border-radius: 8px;
    overflow: hidden;
    z-index: 0;
}

.description__controls>div.-on i svg path {
    fill: #ce1651f2;
}

.channel-menu-dropdown__content .list__item {
    background-color: #141414;
    cursor: pointer;
}

.channel-menu-dropdown__content .list__item>a {
    color: #ffffff;
    padding-right: 52px;
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
}

.active>.active-channel-selector::after {
    content: '';
    width: 8px;
    height: 8px;
    border-radius: 50%;
    background: #ce1651f2;
    position: absolute;
    top: 6px;
    left: 6px;
}

.header__channel .header__channel-chat-button .sb:hover svg path {
    fill: #ce1651f2;
}

.header-channel__notify-badge .sb.-notify-handler:active svg path, .header-channel__notify-badge .sb.-notify-handler:hover svg path {
    fill: #ce1651f2;
}

.switcher {
    background-color: #ce1651f2;
    border-radius: 10px;
    cursor: pointer;
    height: 20px;
    position: relative;
    width: 32px;
    flex-shrink: 0;
}

.main-menu__community-item:hover {
    background-color: #1f1f1f;
}

.main-menu__community-item.active {
    background-color: rgba(33, 33, 33, 0.8);
    letter-spacing: -0.04em;
    font-family: helveticaneuecoub-bold, Helvetica, Arial, "Lucida Grande", sans-serif;
    font-weight: 800;
}
}
