/* ==UserStyle==
@name           darkRYM
@namespace      github.com/jgchk/RateYourMusic-Dark
@version        1.2.15
@description    dark theme for rateyourmusic.com based on RateYourMusic Tomorrow 2
@author         mocha
==/UserStyle== */

@-moz-document domain("rateyourmusic.com") {
    :root {
        --background: #222427;
        --foreground: #27292d;
        --border: #373b41;
        
        --text: #c5c8c6;
        --text-gray: #969896;
