        --yellow: #dbb66b;
        --yellow-background: rgba(219, 182, 107, 0.2);
        
        
        --spoiler: #c9b4cf;
        
        --rating-10: #b8d0de;
        --rating-9: #9fc2d6;
        --rating-8: #86b4cf;
