    div.comment {
        color: var(--text);
        border-bottom: 1px solid var(--border);
    }
    
    div.commentbody {
        color: var(--text) !important;
    }
    
    table.mbgen[style^='margin:14px;'] {
        margin: 0 !important;
    }
    
    a[title^='add user'] {
        color: var(--button-text) !important;
    }
    
    .contribution {
        border: 2px solid var(--border);
        background: var(--button);
    }
    
    .contribution > a {
        color: var(--button-text);
    }
    
    .contribution > a:hover {
        color: var(--text);
    }
    
    div.date_element {
        background: var(--foreground);
    }
    
    div.date_element_month,
    div.date_element_year {
        color: var(--text-gray);
    }
    
    div.date_element_day {
        color: var(--text);
    }
    
    .film_right_column,
    .game_right_column,
    .release_right_column,
    .film_left_column,
    .release_right_column .section_my_catalog.section_outer,
    .release_left_column {
        border: none;
        background: var(--background);
    }
    
    div.hide-for-small {
        background: var(--background) !important;
    }
    
    .release_right_column .section_reviews.section_outer {
        border: none;
    }
    
    div.large-4.pull-8.columns.release_left_column > div {
        background: var(--background) !important;
    }
    
    .catalog_list span.navspan,
    .review_list {
        color: var(--text);
        border: 1px solid var(--border);
        border-radius: 0;
        background: var(--foreground);
    }
    
    .review_sort,
    .navpage {
        color: var(--text);
    }
    
    .sort_choice {
        border-right: 1px solid var(--border);
    }
    
    th.info_hdr {
        color: var(--text-gray);
    }
    
    .num_ratings,
    .max_rating,
    .release_pri_descriptors {
        color: var(--text-gray);
    }
    
    td[style^='font-size:0.9em;color:#555;'],
    td[style^='font-size:x-small;font-weight:normal;color:#555;'],
    td[style^='color:#333;font-size:0.9em;'] {
        color: var(--text-gray) !important;
    }
    
    .sort_choice.selected,
    .sort_choice:hover {
        color: var(--text-gray);
    }
    
    .catalog_list span.navlinkcurrent,
    .review_list span.navlinkcurrent {
        color: var(--text-gray);
        border: none;
    }
    
    a.navlinknext:visited,
    a.navlinknum:visited,
    a.navlinkprev:visited {
        color: var(--link);
    }
    
    a.navlinknext:hover,
    a.navlinknum:hover,
    a.navlinkprev:hover {
        color: var(--link-hover);
    }
    
    a.genre_vote_btn,
    a.descriptor_vote_btn,
    tr.release_genres:hover a.genre_vote_btn,
    tr.film_genres:hover a.genre_vote_btn,
    tr.release_descriptors:hover a.descriptor_vote_btn,
    tr.film_descriptors:hover a.descriptor_vote_btn {
        padding: 3px;
        color: var(--button-text);
        border: 1px solid var(--border);
        border-radius: 0;
        background: var(--button);
    }
    
    a.genre_vote_btn:hover,
    a.descriptor_vote_btn:hover,
    tr.release_genres:hover a.genre_vote_btn:hover,
    tr.film_genres:hover a.genre_vote_btn:hover,
    tr.release_descriptors:hover a.descriptor_vote_btn:hover,
    tr.film_descriptors:hover a.descriptor_vote_btn:hover {
        background: var(--button-hover);
    }
    
    .buy_button {
        color: var(--button-text);
        border: 1px solid var(--border);
        background: var(--button);
    }
    
    .buy_button:hover {
        background: var(--button-hover);
    }
    
    .ui_stream_label {
        color: var(--text-gray);
    }
    
    .ui_stream_link_btn {
        background-color: var(--button);
        background-repeat: no-repeat;
        background-position: center;
        border: 1px solid var(--border);
        border-radius: 0;
    }
    
    .ui_stream_link_btn:hover {
        background-color: var(--button-hover);
    }
    
    .ui_stream_link_btn > i.fa {
        visibility: hidden;
    }
    
