    a.queue_btn:hover {
        background-color: var(--button-hover);
    }
    
    [class*="rtop"], [class*="rbottom"], #msg_body_preview, #pmsubject, #pmsubject .subject {
        background: none !important;
    }
    
    #pminfo, #pminfo .rbottom *, #pminfo .rtop * {
        background: var(--foreground);
    }
    
    #pminfo,
    #pmbody {
        border: 1px solid var(--border);
    }
    
    #pmbody, [class*="rtop"] *, [class*="rbottom"] * {
        background: var(--foreground) !important;
    }
    
    #conversation_history,
    #conversation_history tr {
        background: var(--foreground);
        border: 1px solid var(--border);
    }
    
    td[style='background:#fff;text-align:center;'] {
        background: var(--foreground) !important;
    }
        
    .ch_selected {
        background: var(--border);
    }
    
    .ch_body {
        color: var(--text);
    }
    
    .ch_date {
        color: var(--text-gray);
    }
    
    #pminfo a.btn {
        color: var(--button-text);
        background: var(--button);
        border-radius: 0;
        border: 1px solid var(--border);
    }
    
    #pminfo a.btn:hover {
        background: var(--button-hover);
    }
    
    div[style='border:1px #888 solid; padding:20px; color:#333; background:#fffff8; width:70%'] {
        border: 1px solid var(--yellow) !important;
        background: var(--yellow-background) !important;
        color: var(--yellow) !important;
    }
    
    div[style='border:1px #888 solid; padding:20px; color:#333; background:#fffff8; width:70%'] h3 {
        color: var(--yellow) !important;
    }
    
    .quote {
        background: none;
        border: 1px solid var(--border);
        color: var(--text);
    }
    
    .mbgen .zerotable tr {
        border: none;
    }
    
    .mbtime {
        color: var(--text-gray);
    }
    
    .zerotable td[style='border:none;text-align:right;font-size:x-small;color:darkgray;'] {
        color: var(--text-gray) !important;
    }
    
    blockquote span[style='font-size:9px;font-weight:bold;color:#999999;'] {
        color: var(--text-gray) !important;
    }
    
    div[style='background-color: #eee; padding: 5px;'] {
        background: none !important;
    }
    
    #genre_rec_shell {
        background: var(--foreground) !important;
        border: 1px solid var(--border) !important;
        color: var(--text) !important;
    }
    
    .settings-btn.brym {
        background-color: var(--button);
        border: 1px solid var(--border);
        box-sizing: border-box;
        width: 24px;
        height: 24px;
    }
    
    .settings-btn.brym:hover {
        background-color: var(--button-hover);
    }
    
    .settings-panel.brym {
        top: 33px;
        box-sizing: border-box;
        border: 1px solid var(--border);
        background-color: var(--foreground);
    }
    
    .settings-panel.brym .setting.list .setting.list-item {
        color: var(--text);
        background-color: var(--foreground);
        border: 1px solid var(--border);
    }
    
    .settings-panel.brym .setting.list .setting.list-item.disabled {
        color: var(--border);
        background-color: var(--background);
    }
    
    .or_q_review {
        color: var(--text);
        border-left: 30px solid var(--border);
    }
    
    .track_rating_header > div[style='float:left;'] {
        color: var(--dark-text) !important;
    }
    
    .track_rating_header {
        border-bottom: none;
    }
    
    .track_rating_header:hover {
        color: var(--text) !important;
        background-color: var(--border);
    }
}
