data.css:
@-moz-document domain("craigslist.org")
{
  :root
  {
    --gray-6: #8b8f9e;
    --gray-5: #959ebd;
    --gray-4: #373c4c;
    --gray-3: #233640;
    --gray-2: #1d2d35;
    --gray-1: #192227;
    --gray-0: #0f171b;
    --black: #080d0f;
    --primary-1: #5588a2;
    --primary-0: #123d54;
  }

  body,
.querybox,
.search-options-container,
.homepage .col ul,
.homepage #rightbar h5.active,
.cal .dys th, .cal td,
a.show-contact,
#loginWidget,
fieldset
  {
    color: var(--gray-6);
    background-color: var(--gray-0);
    border-color: var(--gray-1);
  }

  a,
a:hover,
a:visited,
a:focus,
.homepage a:hover,
.linklike,
.buttongroup a:visited,
.homepage #rightbar h5,
.alert-info a,
.json-form .json-form-item.req .label
  {
    color: var(--primary-1);
  }

  a:hover
  {
    text-decoration: underline;
  }

  .header-logo,
.global-header,
.homepage #rightbar,
#leftbar,
#topban .sublinks li,
#topban .sublinks a,
.homepage .col ul li a:hover,
.homepage .ban a:hover,
footer,
.homepage footer,
.changed_input,
.resetsearch .searchlink.changed_input,
.simple header,
a.show-contact:hover,
.json-form .numeric label.input input, .json-form .text label.input input, .json-form .date label.input input, .json-form .numeric label.input span, .json-form .text label.input span, .json-form .date label.input span,
.json-form .json-form-item label.input, .json-form .json-form-item textarea, .json-form .json-form-item select,
.json-form .json-form-item.disabled.numeric label.input, .json-form .json-form-item.disabled.text label.input, .json-form .json-form-item.disabled.numeric label input, .json-form .json-form-item.disabled.text label input,
.accountform-field input
  {
    color: var(--gray-6);
    background-color: var(--gray-1);
    border-color: var(--gray-2);
  }

  input,
select,
textarea,
.flatinput,
.form-tab,
.search-options,
#mapcontainer .date:after, .result-price,
.posting .email-friend,
.gallery .slider-info,
.gallery .arrow,
.ban, #search_form_input_homepage,
.daybubbles,
.accountform,
.homepage #rightbar h5:hover,
.to-banish-page
  {
    background-color: var(--gray-0);
    border-color: var(--gray-1);
    color: var(--gray-6);
  }

  .searchbtn.changed_input,
.searchbtn,
.grid .result-image .result-price,
.dropdown-item:hover,
.dropdown a:focus, .dropdown button:focus,
.posting .attrgroup span,
#topban,
.homepage #postlks #post,
.cal,
.cal td:hover,
.alert-info,
.ban .bantext,
.highlight, .error,
.accountform-banner,
.accountform-btn,
.loginlink-btn,
.favorites,
.actions-combo .reply-button
  {
    background-color: var(--primary-0);
    border-color: var(--gray-1);
    color: var(--gray-6);
  }

  .accountform-btn,
.loginlink-btn
  {
    background: var(--primary-0);
  }

  .loginlink-btn:hover,
.accountform-btn:hover,
.actions-combo .reply-button:hover
  {
    background: var(--gray-1);
    cursor: pointer;
  }

  .icon
  {
    -moz-filter: invert(1) brightness(2.5);
    -webkit-filter: invert(1) brightness(2.5);
    filter: invert(1) brightness(2.5);
  }

  .grid .result-row .result-image.empty,
#search_button_homepage
  {
    -moz-filter: invert(1);
    -webkit-filter: invert(1);
    filter: invert(1);
  }

  .dropdown-list,
.buttongroup,
.hasPic,
.tsb,
.grid .result-row,
.resetsearch .searchlink
  {
    background-color: var(--gray-1);
    border-color: var(--gray-2);
  }

  .dropdown .toggle-arrow
  {
    border-top-color: var(--gray-6);
  }

  .homepage .col ul li a
  {
    border-bottom-color: var(--gray-1);
  }

  .dropdown a, .dropdown button,
.paginator .pagenum,
.gallery .arrow,
.actions-combo .action .action-label,
.result-date
  {
    color: var(--gray-6);
  }

  .grid .result-row .gallery .slider-info
  {
    color: #fff;
  }

  .ban.nearby
  {
    border-top-color: var(--gray-2);
  }

  .gallery .arrow.slider-back
  {
    border-right-color: var(--gray-2);
  }

  .buttongroup .button,
.gallery .arrow.slider-forward
  {
    border-left-color: var(--gray-2);
  }

  .grid .result-row
  {
    box-shadow: none;
  }

  .grid .swipe-wrap>div,
.grid .result-row,
.grid .result-row .result-image
  {
    border-radius: 0;
  }

  .result-meta .result-tags .maptag
  {
    color: #8BC34A;
  }

  .buttongroup .button.sel
  {
    background-color: #3F51B5;
  }

  .cal td.today
  {
    background-color: var(--primary-1);
  }

  .cal td.today a
  {
    color: #000;
  }

  .json-form .json-form-item.req label.input, .json-form .json-form-item.req textarea, .json-form .json-form-item.req select
  {
    border-color: var(--primary-1);
  }

  .post .json-form .html.error-list .err
  {
    color: #ff8c8c;
  }
/*scrolllbar*/
  ::-webkit-scrollbar
  {
    width: 1rem;
  }
/* Track */
  ::-webkit-scrollbar-track,
#query
  {
    background-color: var(--black);
  }
/* Handle */
  ::-webkit-scrollbar-thumb
  {
    background-color: var(--gray-1);
    border: 2px solid var(--gray-0);
  }
/* Handle on hover */
  ::-webkit-scrollbar-thumb:hover
  {
    background-color: var(--primary-0);
    ;
  }
}

@-moz-document domain("avaloniaui.net") {
:root {
    --bg-color: rgb(35, 35, 35);
    --fg-color: rgb(220, 220, 220);
    --border-color: rgb(100, 100, 100);
    --sides-bg-color: rgb(20, 20, 20);
    --link-normal-color: hsl(280, 43%, 60%);
    --link-hover-color: hsl(280, 43%, 50%);
    --note-bg-color: rgba(255, 229, 100, 0.1);
    --note-border-color: rgba(255, 229, 100, 0.3);
    --code-bg-color: rgb(50, 50, 50);
    --code-fg-color: rgb(185, 185, 185);
    --widget-fg-color: rgb(235, 235, 235);
    --widget-bg-color: rgb(45, 45, 45);
    --widget-hover-bg-color: rgb(70, 70, 70);
    --widget-border-color: rgb(70, 70, 70);
    --listitem-even-bg-color: rgb(45, 45, 45);
    --listitem-odd-bg-color: rgb(50, 50, 50);
    --listitem-hover-bg-color: rgb(70, 70, 70);
    --sidemenu-tree-active-bg-color: hsl(280, 35%, 20%);
}

body.layout-boxed {
    background: var(--sides-bg-color);
}

a {
    color: var(--link-normal-color);
}

a:hover, a:active, a:focus {
    color: var(--link-hover-color);
}

code {
    color:  var(--code-fg-color);
    background-color: var(--code-bg-color);
}

.content-wrapper, .main-footer, .content h1 {
    border-color: var(--border-color);
}

.main-footer {
    background-color: var(--border-color);
}

.wrapper, .content-wrapper, .main-sidebar, .left-side, .right-side {
    color: var(--fg-color);
    background: var(--bg-color);
}

.sidebar-menu li a:link, .sidebar-menu li a:visited, .sidebar-menu li a:active {
    color: var(--fg-color);
}

.note {
    background-color: var(--note-bg-color);
    border-color: var(--note-border-color);
}

.nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus,
.tab-content.platform-choice {
    color: var(--widget-fg-color);
    background-color: var(--widget-bg-color);
    border-color: var(--widget-border-color);
}

.nav-tabs {
    border-color: var(--widget-border-color);
}

.nav > li > a:hover, .nav > li > a:active, .nav > li > a:focus {
    color: var(--widget-fg-color);
    background-color: var(--widget-hover-bg-color);
    border-color: var(--widget-border-color);
}

.box {
    background-color: var(--widget-bg-color);
    border-color: var(--widget-border-color);
}

.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    border-color: var(--widget-border-color);
}

.table-striped > tbody > tr:nth-of-type(2n+1) {
    background-color: var(--listitem-even-bg-color);
}

.table-striped tr {
    background-color: var(--listitem-odd-bg-color);
}

.table-hover > tbody > tr:hover {
    background-color: var(--listitem-hover-bg-color);
}

.sidebar-menu li.selected {
    background-color: var(--sidemenu-tree-active-bg-color);
}
}

