	height: 2rem!important;
}

select {
	height: 38px!important;
}

input[type="checkbox"]:not(.slider) {
	height: 20px!important;
}

.search-result,
.search-result-empty {
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

.select-resizer {
	max-width: none!important;
}

#match {
	width: 100%!important;
}

#installed {
	width: 96%!important;
}

#header {
	width: 23%!important;
}

.filter-selection .select-resizer select {
	width: 17.5rem!important;
}

body,
body.search-results-shown {
	width: 100%!important;
}

body>div:not(#installed):not(#message-box):not(.colorpicker-popup):not(.big) {
	width: 25rem!important;
}

select {
	width: 13rem!important;
}

#options [type="number"] {
	width: 5.5em!important;
}

.colorpicker-popup {
	width: 28rem!important;
}
}

@-moz-document regexp("chrome-extension://.*"), regexp("moz-extension://.*") {
/*________________________________________________________________________*/

a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

.dirty>label::before,
.error {
	color: red!important;
}

/*________________________________________________________________________*/

a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________*/

#backup-message,
#confirm>div span,
#help-popup .contents,
#message-box.center #message-box-contents,
#write-style-for,
.beautify-options div,
.newUI .target,
.search-result-description,
dd,
li,
p,
td {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Khaki ________________________________________________________________*/

#confirm > div > b,
#confirm>div b,
#help-popup .title,
#message-box-title,
.menu-title,
h1,
h2 {
	color: #F0E890!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Burlywood ____________________________________________________________*/

h3,
h4 {
	color: #e0b880!important;
	text-shadow: 1px 1px #000!important;
}

/*__ Sandy Brown __________________________________________________________*/

h5,
h6,
label {
	color: #F0A060!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

time {
	color: #07beff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/

#no-styles {
	color: #ff0707!important;
	text-shadow: 1px 1px #000!important;
}

/*________________________________________________________________________*/

.a-input-text,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
	padding: 3px 1rem!important;
}

/*________________________________________________________________________*/

::selection {
	background: #ffff3e!important;
	color: #000!important;
}

pre::selection {
	background: #9eff99!important;
	color: #f0c46c!important;
}

.CodeMirror-selected {
    background: #ff00004f!important;
}

pre span {
	color: #fafbfb!important;
}

.cm-property {
	color: #7fff6e!important;
}

.svg-icon,
.svg-icon.info:hover,
.svg-icon.settings:hover {
	fill: #fcf9f9!important;
}

.cm-s-panda-syntax .cm-property {
	color: #ff7900!important;
}

.cm-qualifier {
	color: #00ffe5!important;
}

.cm-comment {
	color: #faf7c7!important;
}

.cm-atom {
    color: #aafad6!important;
}

.CodeMirror-activeline-gutter .CodeMirror-linenumber {
	background-color: #000!important;
	color: #e9ee3c!important;
	font-weight: 700!important;
}

.cm-number {
	color: #76c2ff!important;
}

.cm-keyword {
	color: #d603fa!important;
}

.cm-tag {
	color: #ce0000!important;
}

.cm-variable-3 {
	color: #ec5881!important;
}

.cm-s-panda-syntax .cm-string,
.newUI .has-favicons .target {
	color: #19F9D8!important;
}

.cm-s-default .cm-builtin {
    color: #ded1fe!important;
}
}

@-moz-document regexp("chrome-extension://.*"), regexp("moz-extension://.*") {
/*________________________________________________________________________*/

html,
body {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

.CodeMirror-activeline-background {
	background: #a2040447!important;
}

.CodeMirror-gutters {
	background-color: #f7f7f70d!important;
}

dialog,
.applies-to {
	background: #060505!important;
}

/*___ Background-color 0000 ________________________________________________________*/

#filters label:hover,
#filters .filter-selection:hover,
#message-box-buttons,
#message-box-title,
.cm-s-panda-syntax,
:root {
	background-color: rgba(0, 0, 0, 0)!important;
}

/*__________________________________________________________________________________*/

.CodeMirror-scroll {
	background: #000!important;
}

#help-popup .contents .CodeMirror pre {
	background: #413d3d!important;
}

/*___ Highlight Background _________________________________________________________*/

.CodeMirror pre:hover {
	background: -webkit-gradient(linear, left top, right top, from(#383838), to(#0000))!important;
	background: linear-gradient(to right, #383838, #0000)!important;
	border-left-color: #434343!important;
}

/*___ Blue _________________________________________________________________________*/

button {
	background-image: linear-gradient(to bottom right, #05497F, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

button:hover {
	background-image: linear-gradient(to bottom right, #06508F, #000)!important;
	color: #ff0!important;
}

button:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Plain Black __________________________________________________________________*/

#header,
#sections>div,
.newUI #installed {
	background-color: #000!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
}

#header:hover,
#sections>div:hover,
.newUI #installed:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Plain ________________________________________________________________________*/

#help-popup,
#message-box>div,
.CodeMirror-hints,
.colorpicker-popup,
.menu-items-wrapper,
.options-column,
.search-result,
.settings-column,
body > .actions {
	background-color: #111!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
}

#help-popup:hover,
#message-box>div:hover,
.CodeMirror-hints:hover,
.colorpicker-popup:hover,
.menu-items-wrapper:hover,
.options-column:hover,
.search-result:hover,
.settings-column:hover,
body > .actions:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*__ Red Gradient ________________________________________________________________________*/

#confirm > div,
.disabled .actions,
.disabled .applies-to,
.disabled h2 .style-name-link,
.disabled h2::after {
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

.CodeMirror-selected {
	border-bottom: 1px solid #000!important;
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

.newUI .entry:hover {
	border-bottom: 1px solid #000!important;
	background: linear-gradient(to right, #240202 35%, #590505 100%)!important;
}

.search-result-meta {
    background-color: hsla(0, 0%, 0%, 0.75)!important;
}
}

@-moz-document regexp("chrome-extension://.*"), regexp("moz-extension://.*") {
/*__________________________________________________________________________________*/

.cm-s-panda-syntax .CodeMirror-linenumber,
.search-result,
.entry {
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.cm-s-panda-syntax .CodeMirror-linenumber:hover,
.CodeMirror-activeline-gutter .CodeMirror-linenumber,
.entry:hover,
.search-result:hover {
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

img {
	border-radius: 22px!important;
}
}

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.voxed.net") {
body{
    font-size: 11pt;
}

.voxData, textarea#commentTextarea, #searchInput{
    background:#000000!important;
}
.voxBar, nav, .attachButton, .buttonPress, .comment, .floatQuote, .categoryListNavbar, .menuModal ul li, .slideMenuOptions{
    background:#1e1e1e!important;
}
.voxCommentList, .darkmode, .voxAttach{
    background:#121212!important;
}
.commentsVoxCount, .commentBox{
    background:#0c0c0c!important;
}
.newVox, .navbarCategories:hover, .menuIcon:hover, .categoryIndividual:hover, .menuModal ul li:hover, .slideMenuOptions li:hover{
    background:#2f2f2f!important;
}
.commentTag, .op, .countNotifications{
    background:#3b3b3b!important;
}

.buttonPress, .attachButton, textarea#commentTextarea, .floatQuote, .slideMenuOptions li{
    border-color:#373737!important;
}

.comment{
    margin:5px;
}

a {
    color: #7e7e7e!important;
    text-decoration:none;
}

a:hover{
    color: #646464!important;
}
a.categoryIndividual{
    color:white!important;
}

.quoteFocus{
    font-weight:normal!important;
    text-decoration: underline;
    text-decoration-style: dashed;
    border-bottom: none;
}

::-webkit-scrollbar{
    width:10px!important;
}
::-webkit-scrollbar-thumb{/*scroll*/
    background:#3a3a3a!important;
}
::-webkit-scrollbar-track{/*scroll fondo*/
    background:#1b1b1b!important;
}
.avatarColor.yellow{
    background:#ffcc33;
}
.avatarColor.red{
    background:#ca0000;
}
.avatarColor.blue{
    background:#006699;
}
.avatarColor.green{
    background:#009933;
}
.avatarColor.black{
    background:black;
}
.avatarColor.multi{
    background:linear-gradient(#ffcc33 25%, #006699 25%, #006699 50%, #009933 50%, #009933 75%, #ca0000 75%, #ca0000 100%);
}
@keyframes multi{
    20% {
        background: linear-gradient(#ca0000 25%, #ffcc33 25%, #ffcc33 50%, #006699 50%, #006699 75%, #009933 75%, #009933 100%);
    }
    60% {
        background: linear-gradient(#009933 25%, #ca0000 25%, #ca0000 50%, #ffcc33 50%, #ffcc33 75%, #006699 75%, #006699 100%);
    }
    80% {
        background: linear-gradient(#006699 25%, #009933 25%, #009933 50%, #ca0000 50%, #ca0000 75%, #ffcc33 75%, #ffcc33 100%);
    }
    100% {
        background: linear-gradient(#ffcc33 25%, #006699 25%, #006699 50%, #009933 50%, #009933 75%, #ca0000 75%, #ca0000 100%);
    }
}

.avatarColor.invested{
    background:linear-gradient(#0033cc 25%, #ff9966 25%, #ff9966 50%, #ff66cc 50%, #ff66cc 75%, #35ffff 75%, #35ffff 100%);
}
@keyframes invested{
    20% {
        background: linear-gradient(#35ffff 25%, #0033cc 25%, #0033cc 50%, #ff9966 50%, #ff9966 75%, #ff66cc 75%, #ff66cc 100%);
    }
    60% {
        background: linear-gradient(#ff66cc 25%, #35ffff 25%, #35ffff 50%, #0033cc 50%, #0033cc 75%, #ff9966 75%, #ff9966 100%);
    }
    80% {
        background: linear-gradient(#ff9966 25%, #ff66cc 25%, #ff66cc 50%, #35ffff 50%, #35ffff 75%, #0033cc 75%, #0033cc 100%);
    }
    100% {
        background: linear-gradient(#0033cc 25%, #ff9966 25%, #ff9966 50%, #ff66cc 50%, #ff66cc 75%, #35ffff 75%, #35ffff 100%);
    }
}
}
