/* i really want this to be global */

@-moz-document domain("codeforces.com") {
a {
 	text-decoration: none;   
}

body {
	background-color: #F0F2F5;
}

.roundbox-lt,
.roundbox-rt,
.roundbox-lb,
.roundbox-rb {
    display: none;
}

.roundbox {
    padding-bottom: 10px;
 	border-radius: 10px;   
}

.sidebox.roundbox {
	margin-top: 10px;   
}

.menu-box {
 	margin-top: 0px !important;
	-webkit-transition: none !important;
	-moz-transition: none !important;
	-ms-transition: none !important;
	-o-transition: none !important;
	transition: none !important;
}

.menu-box:hover {
	-webkit-box-shadow: none !important;
	-moz-box-shadow: none !important;
	box-shadow: none !important;
}

/** Custom sidebar */
.roundbox {
    margin-top: 10px;
	border: none;    
	-webkit-transition: all 0.3s linear 0s;
	-moz-transition: all 0.3s linear 0s;
	-ms-transition: all 0.3s linear 0s;
	-o-transition: all 0.3s linear 0s;
	transition: all 0.3s linear 0s;    
}

.roundbox:hover {
	-webkit-box-shadow: 0 1px 15px 0 rgba(0, 0, 0, 0.1);
	-moz-box-shadow: 0 1px 15px 0 rgba(0, 0, 0, 0.1);
	box-shadow: 0 1px 15px 0 rgba(0, 0, 0, 0.1);
}
.roundbox .titled {
 	padding: 10px;
    border-bottom-color: #e4e6eb;
}

.sidebox div {
    border-bottom-color: #e4e6eb !important;
}

/** Sidebar avatar custom */
.personal-sidebar .for-avatar {
	float: none !important;
	display: block;
    font-size: 18px;
    padding: 10px;
}

.personal-sidebar .for-avatar .avatar img {
 	border-radius: 50px;   
	width: 90px;
	height: 90px;
    object-fit: cover;
}

/** Sidebar remove property links*/
.personal-sidebar .propertyLinks {
    display: none;
}

.personal-sidebar .nav-links {
	border-top-style: solid;
    border-top-width: 1px;
    border-top-color: #e4e6eb;
    padding-top: 10px;
}

.personal-sidebar .nav-links li {
 	list-style-type: none !important;
    padding: 10px;
    font-size: 13px;
    border-radius: 5px;
	-webkit-transition: all 0.2s linear 0s;
	-moz-transition: all 0.2s linear 0s;
	-ms-transition: all 0.2s linear 0s;
	-o-transition: all 0.2s linear 0s;
	transition: all 0.2s linear 0s;    
}

.personal-sidebar .nav-links li a, .personal-sidebar .nav-links li a:visited {
    color: #050505;
}

.personal-sidebar .nav-links li:after {
 	content: 'â†’';
    float: right;
    color: #050505;
}

.personal-sidebar .nav-links li:hover {
 	background-color: rgba(0, 0, 0, 0.05);   
}

.personal-sidebar .nav-links li a {
    text-decoration: none !important;
}

.roundbox .bottom-links {
    padding: 10px;
    background-color: #fff;
    border-top-color: #e4e6eb !important;
	border-bottom-left-radius: 10px !important;
	border-bottom-right-radius: 10px !important;
}

.roundbox .rtable .dark {
	background-color: #fff;
}
/** Topic custom */
.topic {
    margin-top: 10px;
    padding: 20px;
	background-color: #fff;
	border-radius: 10px;
	-webkit-transition: all 0.3s linear 0s;
	-moz-transition: all 0.3s linear 0s;
	-ms-transition: all 0.3s linear 0s;
	-o-transition: all 0.3s linear 0s;
	transition: all 0.3s linear 0s;    
}

.topic:hover {
	-webkit-box-shadow: 0px 1px 15px 0px rgba(0, 0, 0, 0.1);
	-moz-box-shadow: 0px 1px 15px 0px rgba(0, 0, 0, 0.1);
	box-shadow: 0px 1px 15px 0px rgba(0, 0, 0, 0.1);
}

.topic .roundbox {
    border-style: solid;
    border-color: #e4e6eb;
}

.topic .meta a {
    text-decoration: none;
}

.meta {
	padding-bottom: 0px !important;   
}

.topic .content {
    border-left-color: #e4e6eb;
}
.roundbox .rtable td, .roundbox .rtable th {
	border-bottom: 1px solid #e4e6eb;
    border-left: none;
    padding: 7px;
}

/** Header custom */
#header {
    padding: 20px;
    margin-top: -14px;
    background-color: #fff;
}

.menu-box {
    padding: 10px;
 	border-radius: 0;
    border-top: none;
    border-left: none;
    border-right: none;
    border-bottom-left-radius: 20px;
    border-bottom-right-radius: 20px;
    border-bottom-color: #e4e6eb;
}

/** Footer custom */
#footer {
 	background-color: #fff;
    width: 100%;
    height: 100%;
    border-top-left-radius: 20px;
    border-top-right-radius: 20px;
    border-top-color: #e4e6eb;
}

/** Contest data table */
.datatable .lt, .datatable .rt, .datatable .lb, .datatable .rb, .datatable .ilt, .datatable .irt {
	display: none;
}

.datatable table {
    margin-top: 10px;
 	border-style: solid;
    border-color: #e4e6eb;
    border-width: 1px;
}

.datatable table .dark {
 	background-color: #fff;   
}

.datatable table td {
 	padding-top: 5px;
    padding-bottom: 5px;
    padding-left: 10px;
    padding-right: 10px;
}

.datatable {
	padding: 20px !important;
	width: inherit;
    padding-bottom: 40px !important;
	background-color: #fff !important;
  	border-radius: 10px;
	-webkit-transition: all 0.3s linear 0s;
	-moz-transition: all 0.3s linear 0s;
	-ms-transition: all 0.3s linear 0s;
	-o-transition: all 0.3s linear 0s;
	transition: all 0.3s linear 0s;    
}

.datatable:hover {
	-webkit-box-shadow: 0px 1px 15px 0px rgba(0, 0, 0, 0.1);
	-moz-box-shadow: 0px 1px 15px 0px rgba(0, 0, 0, 0.1);
	box-shadow: 0px 1px 15px 0px rgba(0, 0, 0, 0.1);
}

.contests-table::first-line {
    font-size: 20px;
    font-weight: bold !important;
}


.datatable {
	margin-top: 10px !important;
}

/** Custom input */
input[type="submit"], button {
    outline: none;
    height: auto !important;
	padding: 5px 30px 5px 30px !important;
    border-radius: 10px;
    border-width: 1.5px;
    border-color: #e4e6eb;
    font-weight: bold;
	-webkit-transition: all 0.3s linear 0s;
	-moz-transition: all 0.3s linear 0s;
	-ms-transition: all 0.3s linear 0s;
	-o-transition: all 0.3s linear 0s;
	transition: all 0.3s linear 0s;
}

input[type="submit"]:hover, button:hover {
    color: rgba(0, 0, 0, 0.7);
    background-color: #e4e6eb;
}

label[for=searchByProblemCheckbox] {
 	margin-top: 20px !important;
    color: rgba(0, 0, 0, 0.5);
}

.notice {
	margin-top: 10px !important;
}

.highlighted-row td, .highlighted-row th {
	background-color: rgba(221, 238, 255, 0.47) !important;
}

input[name=sourceFile] {
 	border-style: dashed;
    border-width: 2px;
    border-color: #e4e6eb;
    border-radius: 10px;
    padding: 5px;
    width: 100%;
}

.submit-form {
    margin-top: 20px;
 	background-color: #fff;
	padding: 20px 0 20px 0;
	border-radius: 10px;
	-webkit-transition: all 0.3s linear 0s;
	-moz-transition: all 0.3s linear 0s;
	-ms-transition: all 0.3s linear 0s;
	-o-transition: all 0.3s linear 0s;
	transition: all 0.3s linear 0s;    
}

.submit-form:hover {
	-webkit-box-shadow: 0px 1px 15px 0px rgba(0, 0, 0, 0.1);
	-moz-box-shadow: 0px 1px 15px 0px rgba(0, 0, 0, 0.1);
	box-shadow: 0px 1px 15px 0px rgba(0, 0, 0, 0.1);
}

.source-popup pre {
 	padding: 10px;
 	border-style: solid;
    border-width: 2px;
    border-color: #e4e6eb;
}

hr {
	border: 0;
    height: 2px;
    background-image: linear-gradient(to right, rgba(228, 230, 235, 0.1), rgba(228, 230, 235, 1), rgba(228, 230, 235, 0.1));
}

.popup-input-div div, .popup-output-div div, .popup-answer-div div, .popup-checker-div div {
 	margin-top: 10px;
    margin-bottom: 5px;
}

.table-form {
 	padding: 10px;   
}

.problemindexholder {
    margin-top: 10px;
	background-color: #fff;
	padding: 20px;
	border-radius: 10px;
	-webkit-transition: all 0.3s linear 0s;
	-moz-transition: all 0.3s linear 0s;
	-ms-transition: all 0.3s linear 0s;
	-o-transition: all 0.3s linear 0s;
	transition: all 0.3s linear 0s;  
}

.sample-test {
 	margin-top: 10px;   
}

.sample-test .input .title, .sample-test .output .title {
 	font-size: 15px;   
}

.sample-test pre {
 	padding-top: 10px !important;   
	padding-bottom: 10px !important;   
    padding-left: 5px !important;   
    padding-right: 5px !important;   
}

.placeholder {
    margin-bottom: 10px !important;
}

a[href^="/passwordRecovery"] {
    display: block;
 	margin-top: 20px !important;   
}
}

::-webkit-scrollbar {
  display: none;
}

ytm-app.sticky-player ytm-pivot-bar-renderer {
    box-shadow: 0px 3.75px 11px rgba(0, 0, 0, 0.19), 0px 0.5px 5px rgba(0, 0, 0, 0.039);
}

.pivot-bar-item-tab {
    transition: color 0.3s;
}

.pivot-bar-item-tab:hover {
    color: red;
    cursor: pointer;
}

@media only screen and (min-width: 700px) {
  div div ytm-section-list-renderer {    
