@-moz-document domain("okky.kr") {
.layout-container {
    width: auto;
    max-width: 100%;
    margin-right: 150px;
    padding-right: 150px;
}
.main {
    width: 100%;
    margin: 0 auto;
    margin-left: 210px;
}


/**
	Footer
*/
#footer {
    width: auto;
}
#footer a {
    color: inherit;
}

#list-article .list-group {
    display: flex;
    flex-wrap: wrap;
    flex-direction: column;
}
#list-article .list-group > .list-group-item {
    display: inline-flex;
    background-color: #fdfdfd;
    width: 100%;
}
#list-article .list-group > .list-group-item > .list-title-wrapper {
    width: auto;
    float: none;
    margin: 0;
    flex: 5;
}
#list-article .list-group > .list-group-item > .list-summary-wrapper {
    width: auto;
    float: none;
    flex: 1;
}
#list-article .list-group > .list-group-item > .list-group-item-author {
    width: auto;
    float: none;
    flex: 1;
}
.list-sort li > a.active {
    color: #ffbf00;
}
#index.content {
    width: 100%;
}
.content, .content-text {
    width: inherit;
}
.content-body {
    width: calc(100% - 100px);
}
.content-function.fixed {
    left: auto;
    right: 90px;
}
.list-group-success {
    border-left: 3px #d02cb1 solid !important;
}

@media (max-width: 768px) {
    .layout-container {
        margin-right: 20px;
        padding-right: 0px;
    }
    .main {
        margin-left: 0;
        margin-top: 30px;
    }
    .content {
        width: 100%;                                    
    }
    .content .content-body {
        width: 100%;
    }
}
}

