@-moz-document url-prefix("http://heliohost.grd.net.pl/") {
    /***** body *****/
    body {
        background-color: #f1f1f1;
        font-family: 'Segoe UI', 'Roboto', arial, sans-serif;
    }

    #bodyc {
        width: 75%;
    }

    #bodycont {
        width: 85%;
    }



    /***** header *****/
    #header {
        width: 100%;
        background: #444;
        height: 80px;
        box-shadow: 0px 2px 3px 0px rgba(0, 0, 0, 0.3);

        position: absolute;
        transform: translateX(-50%);
        left: 50%;
        z-index: 1;
    }
    #headid {
        width: 100%;
        text-align: center;
    }
    #headid h1 {
        text-align: center;
        color: #eee;
        padding-top: 8px;
    }
    #headid p {
        text-align: center;
        color: gray !important;
    }
    #headid p span {
        color: gray !important;
    }
    #headid p::after {
        content: ' & styled by Kristoffer';
    }


    /***** mains *****/
    /* Various resources: HelioHost support forum */
    #mainl {
        display: none;
    }


    /* main */
    #mainr {
        width: 100%;
        margin-top: 100px;
    }
    #mainright {
        width: 100%;
    }


    /***** contents *****/
    /* top menu */
    div.topmenu {
        width: 100%;
        margin-bottom: 20px;
        background: none;
    }
    .topmenu a.current {
        background: #69bbe3 !important;
    }
    .topmenu a {
        margin: 0;
        font-size: 14px;
        background: #737373;
        color: white;
        padding: 8px 16px;
    }
    .topmenu a:hover {
        background: #888;
        color: white;
    }

    /* shadow */
    .shadow728 {
        display: none;
    }

    /* quote */
    div.randomquote {
        width: 100%;
        margin-bottom: 20px;
        padding: 0;
    }
    .bgred {
        background: #fa3737;
        color: white;
    }

    .quotetitle {
        display: none;
    }

    .randomquote .quotecontent {
        padding-top: 14px;
    }

    /* diskspace div */
    #diskspace{
        width: 100%;
    }

    /* archive */
    .archive {
        text-align: center;
        background: none;
        background: rgba(0, 0, 0, 0.08);
        /* border-radius: 0 0 4px 4px; */
    }
    .archive div div {
        height: 125px !important;
