@-moz-document domain("www.tumblr.com") {
/* bumps username to right */
.reblog-list-item .reblog-header,
.reblog-header {
    padding: 0;
    margin: 0;
    text-align: right;
}

/* gray out inactive blogs */
.inactive {
    color: #999999;
}

/* highlight contributed content */
.contributed-content {
    border-left: 10px solid rgba(0,150,255,0.25);
}

/* removes unnecessary padding but also makes images pop out a little which is odd */
.reblog-list-item {
    padding: 0px 10px 10px 10px;
}

/* removes lines at the top of posts and associated padding */
.reblog-list {
    border: none;
}
.post_header,
.post_full .post_header {
    margin: 0;
}

/* removes avatar and that "deactivated" text that only shows up on hover */
.reblog-avatar, .reblog-tumblelog-deactivated-status {
    display: none !important;
}


/*** hide ads ***/

.yamplus-unit-container, .standalone-ad-container, .video-ad-container, .remnant_ad, .post[data-sponsored], .dfp-ad-container, .timeline-yam-plus-ad-container {
    display: none !important;
}


/*** hide reccommendations ***/

/* recommendations when liking a post */
.rapid-recs-container,
/* radar (sidebar) */
.radar, .radar_header, .controls_section_radar,
/* recommended blogs (sidebar) */
.recommended_tumblelogs, .recommended-blogs-unit-header, .sidebar_link,
/* recommended blogs and tags (search/explore pages) */
.tags-and-tumblelogs,
/* 'ask x about y' banners */
.takeover-container,
/* 'watch on yahoo view' buttons */
.recommendation-reason-footer, .div[data-yahoo-view-link] {
    display: none !important;
}


/*** new ***/

/* ads */
._1aQrn,
._3bMU2,
._2HNMQ,
._2QPW9 {
    background-color: #FF0000 !important;
    display:none !important;
}

/* blog recs & radar */
._2biQz,
._3LdQR,
.zstKO,
.SVPap {
    background-color: #FF0000 !important;
    display:none !important;
}
}

