.b-videosaves__list_item .new-episode:hover {
	background-color: #777;
}
.b-videosaves__list_item .new-episode.own {
	color: #777;
	font-weight: 600;
	padding: 5px 7px;
	background-color: #333;
 border-radius: 3px;
}
