@-moz-document domain("twitter.com") {
/* Fast Twitter 2020 */
/* Ver.20200801 by nukumori.org is licensed under CC BY-NC-SA */
/* https://userstyles.org/styles/179967/ */
/* Created: February 8, 2020 */
/* Updated: August 1, 2020 */
/* *** give us your feedback and donate us *** */

/* font-color bright theme or dark theme */
:root {
  --custom-text-color: #000;
  --custom-border-color: rgb(230, 236, 240);
  --custom-footer-background: #fff;
}
body:not([style*="background-color: rgb(255, 255, 255);"]) {
  --custom-text-color: #fff;
  --custom-border-color: rgb(47, 51, 54);
  --custom-footer-background: #000;
}
body[style*="background-color: rgb(21, 32, 43);"] {
  --custom-footer-background: rgb(21, 32, 43);
}

/* show top tweet on or off */
[aria-label^="Top Tweet"]:before {
  content: attr(aria-label);
  display: block;
  font-size: 10px;
  letter-spacing: -.05em;
  font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Ubuntu, "Helvetica Neue", sans-serif;
  font-weight: 800;
  text-transform: capitalize;
  color: var(--custom-text-color);
}
[data-testid="tweet"] > div:first-child {
  display: block;
}

/* updated message */
[role=status] {
  opacity: .8;
  transition: 0s;
}
[role=status]:hover {
  opacity: 1;
}

/* selected tweeet */
.r-cv4lhi {
  font-size: inherit;
}

/* shrink image */
[data-testid="tweet"] div:not(:first-child) .css-1dbjc4n .css-1dbjc4n.r-1adg3ll.r-1udh08x {
  height: 5em;
  margin-top: auto;
  margin-bottom: auto;
  border-radius: 0;
}
/* 2020/7/18 comment out to fix video controls hidden */
/*
[data-testid="tweet"] div:not(:first-child) .css-1dbjc4n .css-1dbjc4n.r-1adg3ll.r-1udh08x div[style*="background-image"] {
  opacity: 0;
}
*/
[data-testid="tweet"] div:not(:first-child) .css-1dbjc4n .css-1dbjc4n.r-1adg3ll.r-1udh08x img {
  max-width: 100%;
  max-height: 100%;
  width: auto;
  height: auto;
  z-index: 1;
  display: block;
  opacity: 1;
  margin: auto;
}
[data-testid="tweetPhoto"][style*="margin-bottom"] {
  margin-bottom: 0 !important;
}
[data-testid="tweetPhoto"][style*="margin-top"] {
  margin-top: 0 !important;
}
[data-testid="tweet"] .r-4gszlv[style*="background-image"],/* single thumbnail */
[data-testid="tweetPhoto"] .r-4gszlv {/* for multi thumbnails */
  background-size: contain;
}

/* sidebar */
[data-testid="sidebarColumn"] .r-1u4rsef,
.css-1dbjc4n.r-t23y2h.r-1phboty.r-rs99b7.r-1udh08x {
  background-color: transparent;
  border: 1px solid var(--custom-border-color);
}
/* sidebar/hide & show */
[data-testid="sidebarColumn"] .r-1ysxnx4,/* dark */
[data-testid="sidebarColumn"] .r-1uaug3w,/* dim */
[data-testid="sidebarColumn"] .r-1u4rsef/* default */ {
  height: calc(1em * 3);
  min-height: 0;
  opacity: .6;
}
[data-testid="sidebarColumn"] .r-1ysxnx4:hover,/* dark */
[data-testid="sidebarColumn"] .r-1uaug3w:hover,/* dim */
[data-testid="sidebarColumn"] .r-1u4rsef:hover/* default */ {
  height: auto;
  opacity: 1;
}
[data-testid="sidebarColumn"] .r-1ysxnx4 h2,/* dark */
[data-testid="sidebarColumn"] .r-1uaug3w h2,/* dim */
[data-testid="sidebarColumn"] .r-1u4rsef h2/* default */ {
  cursor: default;
}

/* sidebar/about twitter */
[data-testid="sidebarColumn"] nav[aria-label][role="navigation"] {
  position: fixed;
  bottom: 0;
  padding-bottom: 9px;
  width: 350px;
  background: var(--custom-footer-background);
}
[data-testid="sidebarColumn"] nav[aria-label][role="navigation"]:before {
  display: block;
  content: "";
  position: absolute;
  top: -9px;
  left: 0;
  width: 100%;
  height: 9px;
  background: linear-gradient(to bottom, transparent, var(--custom-footer-background));
}

/* tweet button */
.r-1waj6vr {
  box-shadow: none;
}

/* sidebar/list link width full */
header[role="banner"] nav[aria-label][role="navigation"] > a > div,
header[role="banner"] nav[aria-label][role="navigation"] > div > div {
  width: 100%;
  margin-left: 0;
}
header[role="banner"] nav[aria-label][role="navigation"] > div > div > div:nth-child(2) {
  margin-right: auto;
}
header[role="banner"] nav[aria-label][role="navigation"] > a > div > div:last-child {
  flex-grow: 1;
}

/* navigation reaction should be quick */
.r-sdzlij:hover,/* css corner rounded items should be navigation */
header[role="banner"] nav[aria-label][role="navigation"] > a > div:hover,
header[role="banner"] nav[aria-label][role="navigation"] > div > div:hover {
  transition-duration: 0s;
}

/* youtube inline player height overflow fix */
/* <iframe allow="autoplay; fullscreen" allowfullscreen="" src="https://cards-frame.twitter.com/i/cards/tfw/v1/uc/1287338448576118785" sandbox="allow-popups allow-popups-to-escape-sandbox allow-same-origin allow-scripts" class="r-1yadl64 r-16y2uox" style="height: 100%;"></iframe> */
iframe[src^="https://cards-frame.twitter.com/"] {
  max-width: 100%;
  max-height: 100%;
  width: auto;
  height: auto;
}
[data-testid="placementTracking"] video {
  object-fit: contain !important;
}

/* list default hero image + opacity */
[style*='background-image: url("https://pbs.twimg.com/media/EXZ'][style*='?format=png&name=medium'] {
  opacity: .3;
}

/* profile icon */
@media (max-width: 1004px) {
  [data-testid="tweet"] > div:first-child:not(.r-1777fci) {
    max-width: 1.2em;
  }
  [data-testid="tweet"] > div:first-child .r-ahm1il,
  [data-testid="tweet"] > div:first-child .r-j66t93 {
    max-height: 1.2em;
  }
  [data-testid="tweet"] > .r-1h3ijdo {
      height: auto;
  }
}

/* profile icon/fix dm multi patients */
[href*="/participants"] .r-4gszlv {
    background-size: cover;
}

/* fix analysis page overflows when narrow */
@media (max-width: 1199px) {
  body[data-page=home] .full-page {
    min-width: 0;
    width: auto;
    max-width: 1000px;
  }
  .navbar-fixed-top {
    width: 100%;
  }
  .SharedNavBar-contents {
    width: calc(100% - 44px);
  }
  .container {
    width: calc(100% - 16px);
  }
  .home-summary-panel {
    display: flex;
    align-items: flex-end;
  }
}

@media (min-width: 1265px) {
  header[role="banner"] nav[aria-label][role="navigation"] > a > div,
  header[role="banner"] nav[aria-label][role="navigation"] > div > div {
    border-radius: 0
  }
}

/* avoid footer hidden by dm pop up window header */
@media (min-width: 1078px) and (max-width: 1960px) {
  [data-testid="sidebarColumn"] nav[aria-label][role="navigation"] {
    padding-bottom: 62px;
  }
}
}

@-moz-document domain("share.dmhy.org"), domain("dmhy.anoneko.com")
{
	#bebiv3_ad,
	iframe,
	.footer,
	a[href="http://www.pixivbook.com/"],
	.ad,
	[class^="kiwi-ad-"],
	.user-sidebar .box:last-of-type,
	div.table:nth-of-type(4):nth-last-of-type(3),
	div.table:nth-of-type(5):nth-last-of-type(2),
	a[href="http://pixivbook.com/"] {
		display: none !important
	}
	
	body {
		font: 400 12pt sans-serif;
		min-width: 1130px
	}
	
	.headerleft {
		height: 82px;
		overflow: hidden;
		margin-top: -39px
	}
	
	.headerright .links,
	.headerright .links a {
		display: inline-block;
		font-size: 13pt;
		height: 30px
	}
	
	.bg {
		min-width: 900px;
		margin: 0 5mm;
	}
	
	.bg>.header {
		position: fixed;
		width: 100vw;
		height: auto;
		background: #247;
		box-shadow: 0 0 0 1px #666, 0 0 2mm #000;
		left: 0;
		top: -43px;
		z-index: 2;
		transition: top .8s .5s
	}
	
	.bg>.header:hover {
		top: 0;
		transition: top .1s .1s
	}
	
	.top_sort .menu ul {
		white-space: nowrap;
		box-shadow: 1px 3px 3mm #888;
		border-radius: 0 0 2mm 2mm
	}
	
	.top_sort .menu ul li {
		width: auto
	}
	
	.top_sort .menu ul li:last-child a {
		border-radius: 0 0 2mm 2mm
	}
	
	.top_sort .menu ul li a {
		font-size: 13pt;
		width: auto;
		min-width: 3em;
		padding: 3px 13px
	}
	
	.top_sort .menu ul li:hover ul {
		width: auto
	}
	
	.quick_search {
		padding: 2mm 4mm;
		border-radius: 10mm;
		box-shadow: inset 0 0 2mm #333
	}
	
	.main {
		background: linear-gradient(100deg,
				#bbb 0,
				#ddd 30%,
				#ddd 50%,
				#bbb 100%);
		padding: 1em;
		margin: 36px 0 0;
		border-radius: 10pt;
		box-shadow: 0 0 2mm #000
	}
	
	.main>.table.clear {
		width: auto !important;
		margin: 0
	}
	
	table.jmd tr th {
		text-align: right;
		width: 5.5em;
		white-space: nowrap
	}
	
	table.jmd tr.today {
		background: #ffd700 !important
	}
	
	table.jmd a {
		transition: all 220ms;
		display: inline-block;
		color: #FFD !important;
		background: rgba(50, 50, 80, 0.8);
		line-height: 1.2 !important;
		padding: 1px .6em 0 !important;
		border: none !important;
		border-radius: 1em;
		margin: 1px 5px 1px 0 !important
	}
	
	table.jmd tr.today a {
		background: rgba(80, 50, 30, 0.8)
	}
	
	table.jmd a:hover {
		transition: all 60ms;
		text-shadow: 0 0 1px #fff;
		box-shadow: 0 1px 6px rgba(0, 0, 0, 0.8);
		text-decoration: none !important;
		color: maroon !important;
		background: #DDD !important
	}
	
	table.jmd a:active {
		box-shadow: inset 0 1px 3px rgba(0, 0, 0, 0.6);
		color: red !important;
		background: #CCC !important
	}
	
	.nav_title {
		padding: 2px;
		line-height: 1.33;
	}
	
	.table {
		padding: 0;
		background-color: transparent
	}
	
	table.tablesorter {
		border-spacing: 0
	}
	
	table.tablesorter tr td:nth-child(even) {
		background-color: rgba(120, 120, 120, .1) !important
	}
	
	table.tablesorter tr:nth-child(odd) {
		background-color: rgba(180, 200, 180, .5)
	}
	
	table.tablesorter tr:nth-child(even) {
		background-color: rgba(180, 180, 200, .5)
	}
	
	table.tablesorter tr:hover {
		background: rgba(200, 220, 230, .5) linear-gradient(0deg,
				rgba(100, 120, 130, .5) 0,
				rgba(245, 245, 255, .3) 4px,
				rgba(245, 245, 255, .3) calc(100% - 5px),
				rgba(100, 120, 130, .5) 100%);
	}
	
	table.tablesorter th:nth-child(1),
	table.tablesorter td:nth-child(1) {
		width: 5.5em !important;
		-webkit-user-select: none
	}
	
	table.tablesorter th:nth-child(2),
	table.tablesorter td:nth-child(2),
	table.tablesorter th:nth-child(4),
	table.tablesorter td:nth-child(4) {
		width: 2.1em !important;
		-webkit-user-select: none
	}
	
	table.tablesorter th:nth-child(5),
	table.tablesorter td:nth-child(5) {
		width: 4.5em !important;
		-webkit-user-select: none
	}
	
	table.tablesorter th:nth-child(6),
	table.tablesorter td:nth-child(6),
	table.tablesorter th:nth-child(7),
	table.tablesorter td:nth-child(7),
	table.tablesorter th:nth-child(8),
	table.tablesorter td:nth-child(8) {
		width: 2.1em !important;
		white-space: nowrap;
		-webkit-user-select: none
	}
	
	table.tablesorter th:nth-child(9),
	table.tablesorter td:nth-child(9) {
		width: 6em !important;
	}
	
	table.tablesorter th,
	table.tablesorter td:nth-child(1),
	table.tablesorter td:nth-child(2),
	table.tablesorter td:nth-child(n+5) {
		font-size: .92rem;
	}
	
	table.tablesorter tbody td {
		line-height: 1.25;
		padding: 0 2px;
		background: transparent linear-gradient(180deg,
				#aaa 0,
				transparent 1px,
				transparent 100%) !important
	}
	
	table.tablesorter td.title {
		padding-left: 5px;
	}
	
	table.tablesorter td.title .tag {
		float: left;
		height: auto;
		line-height: 1.4;
		background-color: #faf0d3 !important;
		padding: 0 1mm !important;
		margin: .6em 2mm .6em 0;
		border-radius: 6px;
	}
	
	table.tablesorter td.title .tag:hover {
		background-color: #d3fade !important;
		border-color: #800 !important;
		box-shadow: 0 1px 5px #888;
	}
	
	table.tablesorter td.title {
		position: relative;
	}
	
	table.tablesorter td.title span[style="color: gray;"] {
		position: absolute;
		display: block;
		right: 1mm;
		bottom: 1px;
		font-size: 8pt;
		letter-spacing: 1px;
		padding: 0 2mm;
		border-radius: 1em;
		color: #555 !important;
		background: rgba(245, 215, 220, .6);
		box-shadow: inset 0 0 0 1px #999;
		z-index: 0;
	}
	
	table.tablesorter td:last-child>a {
		display: -webkit-box;
		-webkit-line-clamp: 1;
		-webkit-box-orient: vertical;
		overflow: hidden;
	}
	
	table.tablesorter .title>a {
		position: relative;
		color: #334;
		padding: 1px 0;
		height: 2.6em;
		line-height: 2.6;
		display: -webkit-box;
		-webkit-line-clamp: 1;
		-webkit-box-orient: vertical;
		overflow: hidden;
		text-shadow: 0 0 2px #ddd;
		z-index: 1;
	}
	
	table.tablesorter .title>a:hover {
		color: #a00;
	}
	
	table.tablesorter .title>a:active {
		color: #620
	}
	
	table.tablesorter a:hover {
		border: none
	}
	
	table.tablesorter b {
		font-weight: normal;
	}
	
	.table a.sort-7 {
		font-size: 0;
	}
	
	.table a.sort-7:after {
		display: inline;
		font-size: .9rem;
		content: "RAW";
	}
	
	.arrow-magnet {
		display: block;
		width: 100%;
		font-size: 0;
		background: none;
		padding: .2rem 0 0;
		margin: 0 auto;
	}
	
	.arrow-magnet:after {
		font-size: 24px;
		line-height: 2.4rem;
		content: "ğŸ“¥";
		color: #263;
	}
	
	.topic-main {
		position: relative
	}
	
	.topic-title h3,
	.topic-main #resource-tabs p {
		white-space: nowrap;
		overflow: hidden;
		text-overflow: ellipsis;
		margin: 0 0 .1em
	}
	
	a[name="description-end"] {
		position: absolute;
		top: 0;
	}
	
	.topic-main #resource-tabs {
		position: absolute;
		top: 3.3em;
		left: 1em;
	}
	
	.topic-main #resource-tabs {
		width: calc(100% - 300px);
		max-height: 160px;
		padding: 2mm;
		margin: 0;
		overflow: hidden;
		background: rgba(255, 255, 255, 0.7);
		box-shadow: 0 1px 2mm #666;
		transition: max-height .5s .3s
	}
	
	.topic-main #resource-tabs:hover {
		max-height: 60vh;
		overflow: scroll;
		transition: max-height .3s
	}
	
	#resource-tabs .ui-tabs-nav,
	#resource-tabs hr {
		display: none
	}
	
	#resource-tabs .file_list {
		width: auto !important;
		max-height: 100% !important;
		margin: 0 !important;
		z-index: -99
	}
	
	.ui-tabs .ui-tabs-panel {
		padding: 0 !important;
		margin: 0 !important
	}
	
	#preview {
		border: none
	}
	
	#preview>img {
		object-fit: contain;
		max-width: 90vw;
		max-height: 90vh;
		background: #fff;
		padding: 2.5mm;
		border-radius: 2.3mm;
		box-shadow: 0 1.5mm 15mm 1mm rgba(0, 0, 0, .8)
	}
	
	img[alt="rss"] {
		display: inline-block;
		margin: 0 0 4px
	}
	
	.ui-widget {
		font-size: 11pt;
	}
	
	#mini_jmd {
		position: relative;
		max-height: 4em;
		overflow: hidden;
		transition: .3s;
	}
	
	#mini_jmd:after {
		content: "";
		display: block;
		position: absolute;
		bottom: 0;
		width: 100%;
		height: 2em;
		max-height: 2em;
		background: linear-gradient(0deg, #333 0, transparent 90%);
		transition: .7s;
	}
	
	#mini_jmd:hover {
		max-height: 15em;
		overflow: auto;
	}
	
	#mini_jmd:hover:after {
		max-height: 0;
	}
	
	.file_list li {
		display: -webkit-box;
		-webkit-line-clamp: 1;
		-webkit-box-orient: vertical;
		overflow: hidden;
	}
	
	.topic-main #resource-tabs p {
		text-align: left;
	}
	
	.even {
		background: #e8eafc;
	}
	
	.topic-main img {
		max-width: 100%;
		height: auto !important;
	}
}

@-moz-document domain("bt.acgzero.com")
