	.list-table td:nth-child(1),
	.list-table td:nth-child(3),
	.list-table td:nth-child(n+5) {
		text-align: center;
	}
	
	.list-table td:nth-child(4) {
		text-align: right;
	}
	
	.author {
		min-width: 6em;
	}
	
	.list-cat-td,
	.list-dl-td {
		text-align: center;
		min-width: 3em;
	}
	
	.ml-2,
	.mx-2 {
		padding: .3em .5em;
	}
	
	ul.pagination {
		position: fixed;
		bottom: 0;
		right: 30mm;
		margin: 0 0 4mm;
		box-shadow: 0 0 0 1px #aaa;
		transition: .2s;
	}
	
	ul.pagination:hover {
		box-shadow: 0 1px 3mm 1px #888;
	}
	
	.card-body .content-html * {
		font-size: 12pt;
	}
	
	.card-footer {
		position: fixed;
		left: 0;
		bottom: 0;
		background: rgba(255, 255, 255, .8);
		width: 100vw;
		padding: 0 5% 1mm;
		text-align: center;
		box-shadow: 0 0 2mm #666;
		opacity: .6;
		z-index: 3;
		backdrop-filter: blur(2mm);
		-webkit-backdrop-filter: blur(2mm);
		transition: .4s;
	}
	
	.card-footer:hover {
		opacity: 1;
		transition: .1s;
	}
	
	.btn,
	.form-control {
		padding: 2px 2mm;
	}
}

@-moz-document domain("acg.rip")
{
	#bangumi-box,
	.pagination:not(.top-paginate) {
		display: none;
	}
	
	body {
		font-family: sans-serif;
		font-size: calc(8pt + .4vw);
		background: #bbb;
	}
	
	.post-index .date,
