@-moz-document domain("owl.uwo.ca") {
html {
    background: #000;
    color: #fff;
}
:root {
    background: #000 !important;
    color: #fff;
    --dark: /*[[black]]*/;
    --darker: /*[[darker]]*/;
    --darkish: /*[[darkish]]*/;
    --darkerish: /*[[darkerish]]*/;
    --almostdark: /*[[almostdark]]*/;
    --light: /*[[light]]*/;
    --lighter: /*[[lighter]]*/;
    --accent: /*[[accent]]*/;
    --shadow: rgba(0, 0, 0, 0.10);
    --background: /*[[background]]*/;
    --blue: /*[[blue]]*/;
    --darkblue: /*[[darkblue]]*/;
    --activeblue: /*[[activeblue]]*/;
    --alert: /*[[alert]]*/;
}



/* change all link colours */
 a[href] {
    color: var(--lighter) !important;
}
/* change background colour */
 body, .Mrphs-portalBody, .workspace, .pageBody {
    background: var(--dark) !important;
    color: var(--lighter) !important;
}
.Mrphs-portalWrapper, .Mrphs-pagebody, .Mrphs-mainContent, .portletBody {
    background: var(--dark) !important;
    color: var(--lighter) !important;
}
/* Navigation on side and top */
 .Mrphs-container, .Mrphs-container--nav-tools {
    background: var(--dark) !important;
    color: var(--lighter) !important;
}
.Mrphs-toolTitleNav .Mrphs-container--toolTitleNav, .Mrphs-toolTitleNav, .Mrphs-container--toolTitleNav, .Mrphs-toolTitleNav__text, .Mrphs-sakai-iframe-site {
    background: var(--darker) !important;
    color: var(--lighter) !important;
    border: 1px solid var(--darkerish)!important;
    font-weight: 400 !important;
}
.navIntraTool, .actionToolBar {
    background: var(--almostdark) !important;
    color: var(--lighter) !important;
}
.navIntraTool li span > a {
    background: var(--darkish) !important;
    color: var(--lighter) !important;
    border: none !important;
    margin-left: -5px;
}
.navIntraTool li span > a:hover {
    background: #555 !important;
    color: var(--lighter) !important;
    border: none !important;
}
a.anchorpoint {
