}
#toolMenu ul li.is-current a, #subSites ul li.is-current a {
    color: var(--light) !important;
    background: var(--darker) !important;
}
#toolMenu ul li.is-current a:hover, #subSites ul li.is-current a:hover, #toolMenu ul li a:hover, #subSites ul li a:hover {
