}
.button, #mastLogin #loginForm .Mrphs-loginForm__button, .Mrphs-siteStatus.is-unpublished button, .lessonsToolButton, .Mrphs-sakai-message-bundle-manager .dataTables_wrapper table a[role="button"], .Mrphs-sakai-message-bundle-manager .dataTables_export a, ul#otherSitesMenu li:not(.otherSitesMenuClose) a, #cookie-notice #cookie-notice-close, button.ui-state-default, .btn.btn-default {
