    color: var(--lighter) !important;
}
#selectSite .tab-bar .tab-btn.active, #selectQuickLink .tab-bar .tab-btn.active {
    background: var(--dark) !important;
    color: var(--light) !important;
    border: none;
}
#selectSite .tab-bar .tab-btn, #selectQuickLink .tab-bar .tab-btn {
    background: var(--darker) !important;
    color: var(--lighter) !important;
    border: none;
}
#selectSite .tab-box, #selectQuickLink .tab-box {
    background: var(--dark) !important;
    color: var(--lighter) !important;
    border: none;
}
ul.otherSitesCategorList li {
