@-moz-document domain("192.168.178.1") {
A                   { color: #f48c00; text-decoration: none !important }
A:hover             { color: #fff !important; text-decoration: underline !important }
/* common_page/login.html */
#VM_buttom          { display: block !important; position: absolute; right: 10px; top: 10px }

.logo               { top: 0; background-color: Transparent }
.content_holder     { background: none}
.content,
.content-infotext   { color: #fff }

#loginPassword      { -webkit-text-security: disc }
}

@-moz-document domain("rlslog.net") {
body    { background: #181818; background-size: cover; color: #eee }
ul, li  { padding-left: inherit; margin-left: inherit }
a       { color: #3D89C0 !important }

/* Override background colors and margin */
#wrap, #content, #sidebar ul, #sidebar ul ol            { background: none }
#wrap                                                   { margin-top: 0; border: 0;  }

/* Suppress custom bullet-image for right sidebar menu-items */
#sidebar                                                { padding-right: 0; background: #282828 linear-gradient(0deg, #282828, #181818); border-radius: 0 0 10px 10px }
#sidebar ul ul li, #sidebar ul ol li, 
#sidebar ul li ul li ul.children li                     { list-style-image: none }
#sidebar h2                                             { color: #888 }

/* More padding-reset to maximize content and symmetry */
p                                                       { text-align: left !important }
p.comments_link                                         { padding: 0 }
.commenttext                                            { COLOR: #999 }
.entry                                                  { padding: 20px 0 30px 0; border-bottom: 1px solid #282828 }
.entrybody img                                          { padding: 0; margin: 5px 15px 15px 0; border: 0; border-radius: 5px }
.aligncenter                                            { width: 100%; height: 100% }

/* Invert topic-reply form, including Google Captcha colors */
#commentsform { filter: invert(100%) }
input#submit  { margin: 1px 0px; padding: 1px 3px }
input[name='sbutt'] { width: 28px }

/* Ad-suppression */
*[src='about:blank'],
iframe, #footer, #bottomad, #container-e1a5d2d42442ca9afe60098f9cbc12a2, img[width='1'], 
script[data-cfasync='false'],
div[style]:first-child,
div[style='width: 800px;    margin-right: auto;    margin-left: auto;    margin-top: 20px;    background-color: #FFFFFF;   border: 5px solid #FFFFFF;    z-index: 200;    position: relative;']
{ display: none !important; z-index: -99999 !important; left: -9999px !important; top: -9999px !important; width: 0px !important; height: 0px !important; 
  transform: translateY(-9999px) !important; opacity: 0 !important; visibility: hidden !important }
}

@-moz-document domain("scnsrc.me") {
#header                                                             { margin: 0 auto }
#center, #footer, .comm_meta_wrap, .comm_meta_wrap2, .comm_meta,
.more_auto                                                          { background: #1c1c1c; color: #ddd }
#commentform input, #commentform textarea, #commentform #submit,
blockquote                                                          { background-color: #282828; color: #ddd; border-color: #333 }
a:hover, .content h3, .sidebar, .sidebar a                          { color: #ddd }
.menu                                                               { height: 36px }
.post                                                               { border-bottom: 0; margin-bottom: 50px }
.post img                                                           { border: 0 }
.sidebar a:hover, .sidebar h3                                       { color: #f60 }

.more.trigger, .wp-pagenavi a, .wp-pagenavi span                    { 
    color: #ddd !important; text-decoration: none; background: #333; margin: 8px; padding: 2px 8px;
    border-color: #830; border-radius: 3px; box-shadow: 0 0 7px 0 #f60; 
}
.wp-pagenavi                                                        { margin: 30px 0 0 0 }
.wp-pagenavi span.current                                           { margin: 8px; padding: 2px 8px }

.more.trigger:hover, .wp-pagenavi a:hover                           { background-color: #f60 }
}

@-moz-document domain("omorashi.org"), domain("www.omorashi.org") {
.pageContent {
    background-color: #2b2b2b!important;
}
.ipsAreaBackground_reset {
    background: #2b2b2b!important;
}
.pageContent #ipsLayout_contentWrapper {
    background-color: #2b2b2b!important;
}
#ipsLayout_footer {
    background-color: #171717!important;
    margin: 0;
    padding: 20px 0;
}
.ipsAreaBackground {
    background: #2b2b2b!important;
}

.ipsTabs_panel {
    height: 100%;
    background: #2b2b2b!important;
}

.ipsPageHeader {
    background-color: #3b3b3b!important;
}

.ipsBox:not( .ipsBox_transparent ):not( .ipsModerated ) {
    background-color: #2b2b2b;
}

a {
    color: #8f8f8f;
    text-decoration: none;
}

.ipsStream_comment {
    background: #313131;
}

.ipsWidget.ipsWidget_vertical .ipsWidget_title, .ipsWidget.ipsWidget_horizontal .ipsWidget_title {
    padding: 10px;
    margin: 0;
    font-size: 12px;
    font-weight: 500;
    text-transform: uppercase;
    position: relative;
    color: #828e99;
    background: #282828;
    border-radius: 2px 2px 0 0;
    font-family: "Open Sans","Helvetica Neue",Helvetica,Arial,sans-serif;
}

.ipsApp .ipsButton_light {
    background: #1a1a1a;
    border-color: rgba(0,0,0,.03);
    color: #333;
}

.ipsAreaBackground_light {
    background: #1a1a1a;
}

.cke_wysiwyg_frame, .cke_wysiwyg_div {
    background-color: #353535;
}

.ipsTabs {
    background: #151515;
    padding: 10px 10px 0 10px;
    margin: 0;
    list-style: none;
}

.cke_top {
    white-space: normal;
    background: #1a1a1a;
}

.ipsTabs_activeItem {
    background: #7f7f7f;
    color: #333;
    font-weight: bold;
}

.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected ):not( .ipsModerated ):not( .ipsDataItem_new ):not( .ipsDataItem_success ):not( .ipsDataItem_warning ):not( .ipsDataItem_error ):not( .ipsDataItem_info ):not( .ipsDataItem_status ):nth-child(even) {
    background: #2b2b2b;
}

.ipsType_sectionTitle {
    background: #282828;
}

.ipsApp .ipsButton_important {
    background: #626262;
    color: #fff;
}

.ipsNavBar_primary>ul>li>a {
    color: rgb(0, 0, 0);
    text-transform: uppercase;
}

#elUserNav>li>a, #elFooterSocialLinks>li>a {
    color: #000000;
    font-size: 13px;
    vertical-align: middle;
    line-height: 1;
}

.ipsNavBar_primary>ul>li.ipsNavBar_active>a {
    color: #191d24;
    font-weight: bold;
}

.ipsApp .ipsBreadcrumb a {
    color: #000;
}

.ipsBreadcrumb {
    background: rgba(0,0,0,.3);
    line-height: 36px;
    color: #959595;
    overflow: auto;
    margin: 0;
    padding: 0;
}

.ipsBox, .ipsComment, .ipsComment.ipsBox, .ipsPager, .ipsPageHeader {
    border: 1px solid rgb(0, 0, 0);
    border-radius: 3px;
}

.ipsUserPhoto {
    background: rgba(0, 0, 0, 0);
    vertical-align: middle;
    display: inline-block;
    line-height: 1px;
    position: relative;
    margin: 2px;
}

.ipsDataItem {
    width: 100%;
    position: relative;
    border-width: 0 0 1px 0;
    border-style: solid;
    border-color: #000000;
}

.ipsStream .ipsStreamItem_contentType {
    position: absolute;
    display: block;
    width: 30px;
    height: 30px;
    border-radius: 30px;
    line-height: 25px;
    font-size: 15px;
    text-align: center;
    background: #0c0c0c;
    z-index: 500;
    border: 2px solid #353c41;
}

body {
    font-family: "Open Sans","Helvetica Neue",Helvetica,Arial,sans-serif;
    font-size: 13px;
    line-height: 18px;
    color: #949494;
    height: 100%;
    background-color: #fff;
    margin: 0;
}

.ipsType_richText {
    word-wrap: break-word;
    overflow-wrap: break-word;
    line-height: 1.6;
    color: #808080;
}

.ipsQuote_citation {
    background: rgba(0, 0, 0, 0.54);
    margin: 0 -15px;
    padding: 5px 15px;
    color: #222;
    font-weight: bold;
    font-size: 13px;
    display: block;
}

.ipsQuote {
    margin: 0;
    padding: 0 15px;
    border-width: 1px 1px 1px 2px;
    border-color: #000000 #000000 #000000 #000;
    border-style: solid;
    position: relative;
    background: rgba(18, 18, 18, 0);
    clear: both;
}

.ipsSpoiler_header {
    background: #0e0e0e;
    margin: 0 -15px;
    padding: 5px 15px;
    color: #353535;
    font-weight: bold;
    font-size: 13px;
    display: block;
    cursor: pointer;
}

.ipsNavBar_primary>ul>li>.ipsNavBar_secondary {
    background: rgba(0, 0, 0, 0.93);
    box-shadow: rgba(0,0,0,.26) 0 3px 40px, rgba(0,0,0,.1) 0 1px 2px;
    padding: 5px 0;
}

.ipsMenu {
    background: rgba(0, 0, 0, 0.92);
    box-shadow: 0 2px 10px rgb(0, 0, 0);
    border: 1px solid #000;
    border-radius: 3px;
    z-index: 10000;
    position: absolute;
}

.ipsTabs_item {
    display: block;
    float: left;
    white-space: nowrap;
    line-height: 38px;
    font-size: 14px;
    color: #000;
    background: rgba(255,255,255,.1);
    padding: 0 20px;
    text-overflow: ellipsis;
    max-width: 100%;
    overflow: hidden;
    margin-right: 2px;
}

