/***************
  * Home page
  ****************/

#page-dashboard > .container {
    display: flex;
    flex-wrap: wrap;
    max-width: unset;
    margin-left: 1em;
    margin-right: 1em;
    width: auto;
}

#page-dashboard > .container > header {
    order: 1;
    width: 100%;
}

.c-dashboard-header {
    display: flex;
    margin-bottom: 1em;
    margin-right: 1em;
    padding: 1em;
    justify-content: space-between;
}

.c-dashboard-header__amount-container {
    width: auto;
    margin-right: calc(1em + 25px);
    display: flex;
    align-items: center;
}

.c-dashboard-header__amount {
    margin-left: 0.5em;
    margin-top: 0;
}

.c-dashboard-header__last-connection {
    position: unset;
    width: auto;
}

#page-dashboard > .container > .help-messages {
    order: 2;
}

#page-dashboard > .container > hx\:include:nth-of-type(3) {
    order: 3;
    display: flex;
    width: 100%;
}

#page-dashboard > .container > hx\:include:nth-of-type(3) > * {
    width: 30%;
    margin-right: 1em;
}

/* Add external account card */
#page-dashboard > .container > hx\:include:nth-of-type(3) > :last-child {
    width: auto;
    padding: 1rem 0.5rem;
}

#page-dashboard > .container > hx\:include:nth-of-type(3) > :last-child .o-flag {
    display: flex;
    flex-direction: column;
    align-items: stretch;
}

#page-dashboard > .container > hx\:include:nth-of-type(3) > :last-child .o-flag__img {
    width: auto;
}

#page-dashboard > .container > hx\:include:nth-of-type(3) > :last-child .o-flag__body {
    white-space: normal;
}

/* Flush usless things to the end */

#page-dashboard > .container > hx\:include:nth-of-type(2) {
    order: 8;
}

#page-dashboard .c-promotional-cards-wrapper {
    order: 8;
}

#page-dashboard > .container > hx\:include:nth-of-type(4) {
    order: 9;
}


/************
  * Quick icons
  *************/
.quick-actions .quick-actions__item a .icon {
    margin-top: initial;
}

.quick-actions .quick-actions__item a {
    height: initial;
    padding: 0.2em 0.2em;
    padding-left: 2px;
    padding-right: 3px;
}

.quick-actions .quick-actions__items {
    height: initial;
}

.quick-actions .quick-actions__item {
    border-left: 2px dotted #ffffff5e;
}

.quick-actions .quick-actions__item:first-child {
    border-left: none;
}

.quick-actions-toggle {
    /* Align with the close sign */
    height: 24px;
    width: 24px;
    margin-top: -3px;
    right: 6px;
}

.quick-actions-toggle:not(.is-closed) {
    margin-top: -3px;
}

.quick-actions .quick-actions__item a .txt {
    padding-left: 0.1em;
}

/*******************
  * Automatic payments
  *******************/
div.creditor {
    width: 33%;
    margin-right: 2px;
}

div.creditor.block-show {
    display: inline-block;
}

h3.direct-debit-table__title {
    font-size: 14px;
}

h4.direct-debit-table__title {
    margin: 0;
    margin-bottom: 0.2em;
}

div.direct-debit-table__separator {
    padding: 0;
}

/*****************
  * Transfer history
  ******************/
.table td {
    height: initial;
    padding: 0 1ex;
}

.table .table__line:nth-child(2n) {
    background-color: #eeeeee;
}

/******************
