 */

body {
    background-color: rgb(35, 33, 33);
    color: rgb(170, 170, 185);
}

h1,
h2,
h3,
h4,
h5 {
    color: #CD9AB1;
}

*::selection {
  	background: #F8981D;
    color: black;
}

*::-webkit-scrollbar {
    width: 0.9em;
}

*::-webkit-scrollbar-corner, *::-webkit-resizer {
    background-color: #111;	
}
*::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
    background-color: #1D1D1D;
}
 
*::-webkit-scrollbar-thumb {
    background-color: #474747;
    outline: 1px solid #737373;
}

#TopBar_left {
    color: rgba(174, 195, 229, 1);
}

#TopBar {
    min-width: 0px;
    border-bottom: 1px solid rgba( 0, 30, 255, 1);
    border-radius: 3px;
    background: linear-gradient( to bottom, rgba( 0, 0, 0, 1) 0%, rgba( 0, 69, 122, 1) 100%) repeat scroll 0% 0% transparent;
}

.header-container {
    background-color: rgba( 0, 0, 0, 1);
    border-bottom: 1px solid rgba( 0, 143, 255, 1);
    box-shadow: 0px 2px 2px rgba( 0, 66, 255, 0.15);
}

#logocover {
    width: 0px;
}

.nolinkLESSON,
.nolinkAHEAD,
.nolinkBHEAD,
.nolinkCHEAD,
.nolinkQUESTIONS {
    color: rgb(68, 153, 168);
}

#Contents a,
.NavBit a,
#TagNotes a {
    color: rgb(66, 178, 228);
}

#BreadCrumbs a {
    color: rgb(205, 180, 155);
}

a:link {
    text-decoration: none;
    color: #76B1E6 !important;
}

a:hover {
    color: #deecf9 !important;
}

a:visited,
#Contents a:visited,
.NavBit a:visited,
#TagNotes a:visited {
    color: #94D5FF;
}

#productName {
    color: rgb(227, 168, 108);
}

code {
    color: rgb(186, 210, 255);
}

code.visited {
    color: rgb(196, 220, 255);
}

.TutorialLink {
    color: #5AA3E8;
}

#homeGetStarted a:link,
#homeGetStarted a:active,
#homeGetStarted a:visited,
.threecolumns a:link,
.threecolumns a:active,
.threecolumns a:visited {
    color: #739BC0;
}

.button {
    text-align: center;
    display: inline-block;
    background-color: #525252;
    border: none;
    box-shadow: none;
    border-radius: 2px;
    padding: 2px 5px;
    margin-left: 5px;
}

.button:hover {
    background-color: #626262;
    border: none;
    box-shadow: none;
}

.treeview li,
.treeview li.submenu {
    background-color: rgb(35, 33, 33);
}

.booktitle {
    background: none;
    border-bottom: 1px solid #898989;
    border-radius: 0;
}

#breadcrumb a:link,
#breadcrumb a:visited {
    color: #76B1E6;
}

.footer-container,
