.content-list .content-item-calltoaction,
.content-list .content-item>a:hover .content-item-header {
	color: #6fb8ec;
}
a:hover, a:focus,
p.practical-alert a:hover {
	color: #558db5;
}
.teaser.magazine span.teaser-eyecatcher,
.teaser.magazine a.container:hover span.teaser-header {
	color: #f79577;
}

.breadcrumb a,
.breadcrumb>.active,
.breadcrumb a:hover, .breadcrumb a:focus {
	color: #ccc;
}
.breadcrumb>li+li:before {
	color: #ddd;
}

.quick-results .quick-results-header {
	border-bottom: 1px solid #777;
}
.result-block .result-block-toc ol {
	border-bottom: 1px solid #333;
	color: #aaa;
}
.result-block .sense-group .sense-group-header {
	color: #f79577;
	border-bottom: 1px solid #333;
}
.toc-links a {
	border-top: 1px solid #333;
	border-bottom: 1px solid #333;
}
.toc-links a:hover {
	background-color: rgba(255, 255, 255, 0.1);
}
.result-block .result-block-header,
.dict-entry {
	border-bottom: 1px solid #333;
}
.dict-entry .dict-example strong, .dict-entry .dict-example b {
	background-color: #0f4f7d;
}
.quick-results .quick-result-entry span.suffix, .quick-results .quick-result-entry span.sense-group-description-text,
.dict-entry .dict-translation .dict-source,
.dict-entry span.suffix,
.toc-links-header h5,
.result-block h3 span.suffix,
.result-block .result-block-toc ol li a,
footer.synonym-copyright, p.practical-alert {
	color: #aaa;
}
a.sound, span.sound, a.sound-inline, span.sound-inline {
	color: #aaa !important;
}
a.sound:hover, a.sound-inline:hover {
	color: #6fb8ec !important;
}
.dict-entry .dict-example .dict-source {
	color: #ccc;
}
a.icon-link {
	color: #aaa;
}
a.icon-link:hover, a.icon-link:focus {
	background-color: rgba(255, 255, 255, 0.1);
	color: #eee;
}

.dropdown-menu, .tt-menu {
	background-color: #111;
	border: 1px solid rgba(255, 255, 255, 0.1);
}
.dropdown-menu>li>a, .tt-menu>li>a {
	color: #eee;
}
.dropdown-menu>li>a:hover, .tt-menu>li>a:hover, .dropdown-menu>li>a:focus, .tt-menu>li>a:focus {
	color: #eee;
	background-color: #252525;
}
.dropdown-menu>.active>a, .tt-menu>.active>a, .dropdown-menu>.active>a:hover, .tt-menu>.active>a:hover, .dropdown-menu>.active>a:focus, .tt-menu>.active>a:focus {
	color: #aaa;
}
.keyboard-wrapper {
	background-color: #1f1f1f;
	border-bottom: 1px solid #333;
}
.keyboard-wrapper .babla-extra-letter-set a {
	border: 1px solid #333;
	background-color: #333;
	color: #eee;
}
.keyboard-wrapper .babla-extra-letter-set a:hover {
	background-color: #444;
	color: #eee;
}

.site-footer,
.site-footer a:hover, .site-footer a:focus {
	color: #ccc;
}

.content-list .content-item-teaser-img {
	border: 1px solid #333;
	background: #333 no-repeat center center scroll;
}
body.corporate .section-label {
	background-color: #444;
}
body.corporate .section-label:after {
	border-top-color: #555;
}
.form-control {
	color: #eee;
	background-color: #151515;
	border: 1px solid #444;
}
.bab-custom-dictsub,
.bab-custom-dictsub a:hover {
	background-color: #252525 !important;
}


#topslot_container,
.info-wrapper>div {
	display: none;
}



}

@-moz-document domain("voz.vn") {
/** reduce image in thread and align center **/
.lbContainer.lbContainer--inline {
    display: flex !important;
    width: 100%;
    justify-content: center;
    padding-top: 10px;
    padding-bottom: 10px;
}
/** reduce smilie size **/
 .smilie {
    max-width: 100px;
}
/** No more profile posts **/
[data-widget-key$="new_profile_posts"],
/** hide sidebar in what's new **/
[data-template="whats_new"] .p-body-sidebar,
/** hide username on header row **/
 .contentRow-header .username,
/** remove the fucking dot before first subtitle (datetime) **/
 .structItem-startDate:before,
/** hide sponsors **/
 .p-footer-row:nth-child(2), .p-footer-row:nth-child(3),
/** hide body sidebar in forum view **/
[data-template$="forum_view"] .p-body-sidebar, 
[data-template="thread_view"] .p-body-sidebar,
[data-template="whats_new_posts"] .p-body-sidebar,
/** hide username **/
 .p-navgroup-link--user .p-navgroup-linkText,
[id^="div-gpt-ad-"] {
    display: none
}

[data-template$="forum_view"] .p-body-content,
[data-template="thread_view"] .p-body-sidebar,
[data-template="whats_new_posts"] .p-body-content {
    padding-right: 0
}
/** bigger section links **/
 .p-sectionLinks-list .p-navEl-link {
    padding: 8px 15px
}
.p-sectionLinks-list .p-navEl-splitTrigger {
    padding-top: 8px;
    padding-bottom: 8px;
}
/** blur avatar **/
 .message--quickReply .avatar[data-user-id]:not([data-user-id=""]) img, .menu--account .avatar img, .p-account .avatar img {
    filter: blur(10px);
}
/** fix margin **/
 .block--messages .message {
    margin-top: 8px
}
/* fix link color on header user section */
 .menu-tabHeader .tabs-tab {
    color: #5c7099
}
.menu-tabHeader .tabs-tab.is-active {
    color: #1d4171
}
}
